[[code type="CSS"]]
/*
    바다의 전설 테마
    [2021 Wikidot Theme]
    By salamander724
    Based on:
       Penumbra Theme by EstrellaYoshte
       BHL Style Collapsible by Monkatraz
*/

@import url('https://fonts.googleapis.com/css?family=Basic|IBM+Plex+Mono:400,700');


:root {
     --bgColor: #101e41;
     --blueColor: MidnightBlue;
     --whiteColor: Seashell;
     --greenColor: SeaGreen;
     --darkColor: Black;
     --redColor: #d10000;

     /* Typefaces 中文应以隶书体为佳 */
     --body-font: 'LXGW WenKai', '완도청정바다체', sans-serif;
     --header-font: 'LXGW WenKai', '완도희망체', lisu, 'UnYetgul', sans-serif;
     --title-font: 'LXGW WenKai', '완도희망체',  lisu, 'UnYetgul', sans-serif;
     --mono-font: 'IBM Plex Mono', '仿宋',  monospace;
}

body {
    font-family: var(--body-font);
    color: var(--whiteColor);

    background: var(--bgColor) url('https://scpko.wdfiles.com/local--files/theme%3Alegend-of-blue/underwater.jpg') center top no-repeat;
    background-size: 1920px;
}

.modalcontainer, .modalcontainer h1, .modalcontainer h2,
.modalcontainer h3, .modalcontainer h4, .modalcontainer h5, .modalcontainer h6,
.owindow, .owindow h1, .owindow h2, .owindow h3, .owindow h4,
.owindow h5, .owindow h6 {
    color: var(--darkColor);
}

a, a:visited, #side-bar a, #side-bar a:visited {
  color: var(--greenColor);
}

a.newpage, #side-bar a.newpage {
  color: #396;
}

div#container-wrap {
  background-image: none !important;
}

#main-content {
  margin-top: 280px;
}

table.wiki-content-table th {
  background: var(--whiteColor);
  color: var(--blueColor);
}

#lock-info {
  color: var(--darkColor);
}

/* TOP-BAR SETTINGS */
#top-bar {
  width: 100%;
  top: 380px;
  display: flex;
  justify-content: center;
}

.top-bar {
  position: relative;
  margin: 0 auto;
}

#top-bar a {
    color: #FFF;
}

#top-bar ul li ul {
    border: none;
    overflow: hidden;
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    border-top: none !important;
    background: var(--bgColor);
    color: var(--whiteColor);
}

#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background: var(--whiteColor);
    color: var(--greenColor);
}

/* SIDE MENU */
 #side-bar .side-block {
     background: var(--whiteColor);
}

 #side-bar .side-block .menu-item img {
  filter: hue-rotate(-208deg);
}

 #side-bar div.side-block:nth-child(4) > div:nth-child(1) > div:nth-child(1) img {
     filter: none;
     -webkit-filter: none;
}

 #side-bar .heading {
     color: var(--greenColor);
     border-bottom: solid 1px var(--greenColor);
}

 #side-bar div.menu-item.inactive a {
     color: var(--greenColor);
}

 #side-bar div.menu-item .sub-text {
     color: var(--greenColor);
}

 #side-bar .collapsible-block-folded {
     background: var(--whiteColor);
}

 #side-bar .collapsible-block-unfolded-link {
     border-bottom: solid 1px var(--greenColor);
}

 #side-bar .collapsible-block-unfolded-link .collapsible-block-link {
     color: var(--greenColor);
}

 #side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
     color: var(--greenColor);
}

/* ---- HEADER ---- */

div#extra-div-1 {
  position: absolute;
  top: -20px;
  left: 50%;
  transform: translateX( -50% );
  max-width: 400px;
  width: 100vw;
  height: 700px;
  background: url(https://scpko.wdfiles.com/local--files/theme%3Alegend-of-blue/Makara.png) center no-repeat;
  background-size: 95%;
  z-index: -10;
}

#header {
  background-image: none;
}

#header h1 a {
  display: inline-block;
  position: absolute;
  color: transparent;
  text-shadow: none;
  left: 50%;
  transform: translateX( -50% );
  top: 100px;
  min-height: 170px;
}

#header h2 span {
  display: none;
}

#side-bar .heading{
   font-family: 'UnYetgul', sans-serif;
}

#login-status {
   color: var(--whiteColor);
   text-shadow: 1px 0 var(--darkColor), -1px 0 var(--darkColor), 0 1px var(--darkColor), 0 -1px var(--darkColor);
}

#login-status a {
   color: var(--whiteColor);
   text-shadow: 1px 0 var(--darkColor), -1px 0 var(--darkColor), 0 1px var(--darkColor), 0 -1px var(--darkColor);
}

#search-top-box-input {
   border: transparent;
   background-color: var(--whiteColor);
}

#search-top-box-input:hover, #search-top-box-input:focus {
   border: solid 0.5px #888;
   background-color: var(--whiteColor);
}

#search-top-box-form input[type=submit] {
   border: solid 1px var(--whiteColor);
   color: var(--whiteColor);
   text-shadow: 0 0 3px var(--darkColor);
   background: none !important;
   background-color:  var(--blueColor);
}

#search-top-box-form input[type=submit]:hover, #search-top-box-form input[type=submit]:focus {
   border: solid 1px var(--whiteColor);
   color: var(--whiteColor);
   background: none !important;
   background-color: var(--blueColor);
}

/* ---- PAGE ELEMENTS ---- */

.page-source, tt{
    font-family: var(--mono-font);
}
 .code pre, .code p {
    font-family: var(--mono-font);
}

.code {
    background-color: var(--whiteColor);
    font-family: var(--mono-font);
    border: 2px solid var(--greenColor);
    border-radius: 1.5em;
    box-shadow: 0 0 6px 1px var(--greenColor);
    color: var(--darkColor);
}

h1,h2,h3,h4,h5,h6 {
    font-family: var(--title-font);
    color: var(--whiteColor);
}

blockquote {
    border: 2px solid var(--greenColor);
    box-shadow: 0 0 6px 1px var(--greenColor);;
    background: var(--whiteColor);
    color: var(--darkColor);
}

#page-title {
    font-family: var(--title-font);
    color: #FFF;
    font-size:300%;
    text-align: center;
    z-index: 10;
}

/* mobile side-bar open button */

#top-bar .open-menu a {
  border-radius: 0px !important;
  border-color: var(--whiteColor) !important;
  border-width: 3px !important;
  padding: 0.1em;
  color: var(--whiteColor) !important;
  background: var(--bgColor) !important;
}

/* ---- 이미지 ---- */

#page-content .scp-image-block {
    border-color: var(--whiteColor);
    box-shadow: none;
}

#page-content .scp-image-block .scp-image-caption {
    border-color: var(--whiteColor);
    background-color: var(--darkColor);
    color: var(--whiteColor);
}


/* footnote by 7happy7 */

span:not([class]):not([style]) > sup.footnoteref:first-child > a.footnoteref:before,
sup.footnoteref > a.footnoteref:before {
content: "（";
}
span:not([class]):not([style]) > sup.footnoteref > a.footnoteref:before {
content: ",";
}

/* 호버팁 */

.hovertip {
    width: 20em;
    padding: 0.5em;
    border: 1px solid var(--greenColor) !important;
    border-radius: 0.5em;
    background-color: var(--bgColor) !important;
    box-shadow: 0 0 6px 1px var(--greenColor);
    z-index: 100;
}
.hovertip .f-heading {
    color: var(--whiteColor);
}

/*====================== 第二个module ============================*/

/* ---- 평가모듈 ---- */

/* Module Rate */

#container .page-rate-widget-box .rate-points {
    background-color: var(--blueColor) !important;
    border: none !important;
}

#container .page-rate-widget-box .cancel {
    background-color: #FFF5EE;
    border: none;
}

#container .page-rate-widget-box .rateup,
#container .page-rate-widget-box .ratedown,
#container .page-rate-widget-box .cancel {
    background: #FFF5EE;
    border: none;
}


#container .page-rate-widget-box .rateup a:hover,
#container .page-rate-widget-box .ratedown a:hover,
#container .page-rate-widget-box .cancel a:hover {
    background: #FFF5EE;
    color: var(--greenColor);
}

/* Credit Box */

#page-content .rate-box-with-credit-button .creditButton {
    background-color: var(--blueColor);
    border: none;
}

#page-content .rate-box-with-credit-button .creditButton:hover {
    background-color: #191970;
    border: none;
    color: #000080;
}

#page-content .creditButton a {
    color: var(--whiteColor);
}

#page-content .creditButton a:hover {
    color: var(--greenColor);
}

#page-content div.creditRate a,
#page-content div.page-rate-widget-box a,
#u-credit-view a {
    text-decoration: none;
}

/* Info Box */

#page-content .rate-box-with-credit-button {
    background-color: var(--blueColor);
    border: none;
}

#page-content .rate-box-with-credit-button > .page-rate-widget-box {
    border-radius: 0px;
    border: 0px;
}

#page-content .rate-box-with-credit-button > .page-rate-widget-box .rate-points {
    border: none;
}

#page-content .rate-box-with-credit-button > .page-rate-widget-box .rateup,
#page-content .rate-box-with-credit-button > .page-rate-widget-box .ratedown {
    border-top: none;
    border-bottom: none;
}

#page-content .rate-box-with-credit-button > .page-rate-widget-box .cancel {
    border: none;
}

#page-content .rate-box-with-credit-button .creditButton {
    background-color: transparent !important;
    border: none !important;
}

#page-content .rate-box-with-credit-button .creditButton:hover {
    background-color: transparent !important;
    border: none !important;
    color: var(--greenColor);
}

#page-content .creditButton p a {
    border-left: solid 1px transparent;
}

 .rate-box-with-credit-button .page-rate-widget-box .cancel {
     border-radius: 0;
}

/*=================== 第三个module =======================*/


/* タブデザイン */
.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
  zoom: unset;
  display: flex;
  flex-wrap: wrap;
  box-sizing: border-box;
  border: solid 2px var(--darkColor);
  font-size: 120%;
}

/* 通常時のタブ */
.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
  zoom: unset;
  border: unset;
  background-image: none;
  transition: color 0.25s, background-color 0.25s;
  color: var(--darkColor);
  background-color: var(--whiteColor);
}

/* hover時のタブ */
.yui-navset .yui-nav a:focus,
.yui-navset .yui-nav a:hover {
  zoom: unset;
  background: none;
  background-color: var(--greenColor);
  color: var(--whiteColor);
}

.yui-navset .yui-nav li,
.yui-navset .yui-navset-top .yui-nav li {
  zoom: unset;
  display: flex;
  position: relative;
  flex-grow: 2;
  margin: 0;
  box-shadow: 0 0 0 .0625rem var(--greenColor);
  border-color: rgba(0, 0, 0, 0);
  background-color: var(--whiteColor);
  background: none;
  padding: 0;
  max-width: 100%
}

.yui-navset-bottom .yui-nav li a,
.yui-navset-top .yui-nav li a,
.yui-navset .yui-nav li a {
  zoom: unset;
  display: flex;
  align-items: center;
  justify-content: center;
  vertical-align: bottom;
  width: 100%
}

.yui-navset .yui-nav a em,
.yui-navset .yui-navset-top .yui-nav a em {
  -o-text-overflow: ellipsis;
  padding: .35em .85em;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}

.yui-navset .yui-nav li em {
  border: unset !important
}

.yui-navset .yui-nav .selected,
.yui-navset .yui-navset-top .yui-nav .selected {
  flex-grow: 2;
  margin: 0;
  box-shadow: 0 0 6px 1px var(--greenColor);
  background-color: var(--blueColor);
  color: var(--whiteColor);
  padding: 0
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a em {
  border: rgba(0, 0, 0, 0)
}

.yui-navset .yui-nav .selected a {
  background: none;
  background-image: none;
  width: 100%;
  color: var(--whiteColor) !important;
}

.yui-navset .yui-nav .selected a:active,
.yui-navset .yui-nav .selected a:focus {
  background-color: gray;
  color: gainsboro;
}

.yui-navset .yui-nav .selected a:hover,
.yui-navset .yui-nav .selected a:focus {
  cursor: default;
  background: none;
  background-image: var(--blueColor):
}

.yui-navset .yui-content {
  background-color:  var(--whiteColor);
  color:  var(--darkColor);
  padding: 15px 25px 15px;
}

.yui-navset .yui-content h1,
.yui-navset .yui-content h2,
.yui-navset .yui-content h3,
.yui-navset .yui-content h4,
.yui-navset .yui-content h5,
.yui-navset .yui-content h6 {
  color: var(--darkColor);
}

.yui-navset .yui-content,
.yui-navset .yui-navset-top .yui-content {
  border: solid 2px;
  border-top: none;
  border-color:  var(--darkColor);
}

.yui-navset-left .yui-content {
  border-left-color:  var(--whiteColor);
}

@media (max-width: 480px) {
  .yui-navset .yui-content {
    padding: 15px 17px 15px;
  }
}

/* New paper class from Unusual Cargo theme by CityToast */

div.paper {
