/*
    바다의 전설 테마
    [2021 Wikidot Theme]
    By salamander724
    Based on:
       Penumbra Theme by EstrellaYoshte
       BHL Style Collapsible by Monkatraz
*/

:root {
     --bgColor: #101e41;
     --blueColor: MidnightBlue;
     --whiteColor: Seashell;
     --greenColor: SeaGreen;
     --darkColor: Black;
     --redColor: #d10000;

     /* Typefaces */
     --body-font: '완도청정바다체', sans-serif;
     --header-font: '완도희망체', 'UnYetgul', sans-serif;
     --title-font: '완도희망체', 'UnYetgul', sans-serif;
     --mono-font: 'IBM Plex Mono', monospace;
}

body {
    font-family: var(--body-font);
    color: var(--whiteColor);

    background: var(--bgColor) url('https://scpko.wdfiles.com/local--files/theme%3Alegend-of-blue/underwater.jpg') center top no-repeat;
    background-size: 1920px;
}
@media (min-width: 1921px) {
  body {
    background-size: 100%;
  }
}

.modalcontainer, .modalcontainer h1, .modalcontainer h2,
.modalcontainer h3, .modalcontainer h4, .modalcontainer h5, .modalcontainer h6,
.owindow, .owindow h1, .owindow h2, .owindow h3, .owindow h4,
.owindow h5, .owindow h6 {
    color: var(--darkColor);
}

a, a:visited, #side-bar a, #side-bar a:visited {
  color: var(--greenColor);
}

a.newpage, #side-bar a.newpage {
  color: #396;
}

div#container-wrap {
  background-image: none !important;
}

#main-content {
  margin-top: 280px;
}

table.wiki-content-table th {
  background: var(--whiteColor);
  color: var(--blueColor);
}

#lock-info {
  color: var(--darkColor);
}

/* TOP-BAR SETTINGS */
#top-bar {
  width: 100%;
  top: 380px;
  display: flex;
  justify-content: center;
}

.top-bar {
  position: relative;
  margin: 0 auto;
}

#top-bar a {
    color: #FFF;
}

#top-bar ul li ul {
    border: none;
    overflow: hidden;
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    border-top: none !important;
    background: var(--bgColor);
    color: var(--whiteColor);
}

#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background: var(--whiteColor);
    color: var(--greenColor);
}

/* SIDE MENU */ 
 #side-bar .side-block {
     background: var(--whiteColor);
}

 #side-bar .side-block .menu-item img {
  filter: hue-rotate(-208deg);
}

 #side-bar div.side-block:nth-child(4) > div:nth-child(1) > div:nth-child(1) img {
     filter: none;
     -webkit-filter: none;
}

 #side-bar .heading {
     color: var(--greenColor);
     border-bottom: solid 1px var(--greenColor);
}

 #side-bar div.menu-item.inactive a {
     color: var(--greenColor);
}

 #side-bar div.menu-item .sub-text {
     color: var(--greenColor);
}

 #side-bar .collapsible-block-folded {
     background: var(--whiteColor);
}

 #side-bar .collapsible-block-unfolded-link {
     border-bottom: solid 1px var(--greenColor);
}

 #side-bar .collapsible-block-unfolded-link .collapsible-block-link {
     color: var(--greenColor);
}

 #side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
     color: var(--greenColor);
}

/* ---- HEADER ---- */

div#extra-div-1 {
  position: absolute;
  top: -20px;
  left: 50%;
  transform: translateX( -50% );
  max-width: 400px;
  width: 100vw;
  height: 700px;
  background: url(https://scpko.wdfiles.com/local--files/theme%3Alegend-of-blue/Makara.png) center no-repeat;
  background-size: 95%;
  z-index: -10;
}

#header {
  background-image: none;
}

#header h1 a {
  display: inline-block;
  position: absolute;
  color: transparent;
  text-shadow: none;
  left: 50%;
  transform: translateX( -50% );
  top: 100px;
  min-height: 170px;
}

#header h2 span {
  display: none;
}

#side-bar .heading{
   font-family: 'UnYetgul', sans-serif;
}

#login-status {
   color: var(--whiteColor);
   text-shadow: 1px 0 var(--darkColor), -1px 0 var(--darkColor), 0 1px var(--darkColor), 0 -1px var(--darkColor);
}

#login-status a {
   color: var(--whiteColor);
   text-shadow: 1px 0 var(--darkColor), -1px 0 var(--darkColor), 0 1px var(--darkColor), 0 -1px var(--darkColor);
}

#login-status ul a {
   color: #444;
   text-shadow: none;
}

#search-top-box-input {
   border: transparent;
   background-color: var(--whiteColor);
}

#search-top-box-input:hover, #search-top-box-input:focus {
   border: solid 0.5px #888;
   background-color: var(--whiteColor);
}

#search-top-box-form input[type=submit] {
   border: solid 1px var(--whiteColor);
   color: var(--whiteColor);
   text-shadow: 0 0 3px var(--darkColor);
   background: none !important;
   background-color:  var(--blueColor);
}

#search-top-box-form input[type=submit]:hover, #search-top-box-form input[type=submit]:focus {
   border: solid 1px var(--whiteColor);
   color: var(--whiteColor);
   background: none !important;
   background-color: var(--blueColor);
}

/* ---- PAGE ELEMENTS ---- */

.page-source, tt{
    font-family: var(--mono-font); 
}
 .code pre, .code p {
    font-family: var(--mono-font); 
}

.code {
    background-color: var(--whiteColor);
    font-family: var(--mono-font); 
    border: 2px solid var(--greenColor);
    border-radius: 1.5em;
    box-shadow: 0 0 6px 1px var(--greenColor);
    color: var(--darkColor);
}

h1,h2,h3,h4,h5,h6 {
    font-family: var(--title-font); 
    color: var(--whiteColor);
}

blockquote {
    border: 2px solid var(--greenColor);
    box-shadow: 0 0 6px 1px var(--greenColor);;
    background: var(--whiteColor);
    color: var(--darkColor);
}

#page-title {
    font-family: var(--title-font); 
    color: #FFF;
    font-size:300%;
    text-align: center;
    z-index: 10;
}


textarea {
   font-family: var(--mono-font);
}
input#edit-page-title.text {
   font-family: var(--title-font);
}

/* https://scp-sandbox-3.wikidot.com/component:image-zoom-base */

.enlarge{
transition: .25s;
}

.enlarge:hover{
transform: scale(1.6);
transition: .25s;
transition-delay: .4s;
z-index: 1000;
}

/* mobile side-bar open button */

#top-bar .open-menu a {
  border-radius: 0px !important;
  border-color: var(--whiteColor) !important;
  border-width: 3px !important;
  padding: 0.1em;
  color: var(--whiteColor) !important;
  background: var(--bgColor) !important;
}

#top-bar .open-menu a {
  pointer-events: auto !important;
}

/* ---- 이미지 ---- */

#page-content .scp-image-block {
    border-color: var(--whiteColor);
    box-shadow: none;
}

#page-content .scp-image-block .scp-image-caption {
    border-color: var(--whiteColor);
    background-color: var(--darkColor);
    color: var(--whiteColor);
}


/* footnote by 7happy7 */

span:not([class]):not([style]) > sup.footnoteref:first-child > a.footnoteref:before,
sup.footnoteref > a.footnoteref:before {
content: "(";
}
span:not([class]):not([style]) > sup.footnoteref > a.footnoteref:before {
content: ",";
}

/* 호버팁 */

.hovertip {
    width: 20em;
    padding: 0.5em;
    border: 1px solid var(--greenColor) !important;
    border-radius: 0.5em;
    background-color: var(--bgColor) !important;
    box-shadow: 0 0 6px 1px var(--greenColor);
    z-index: 100;
}
.hovertip .f-heading {
    color: var(--whiteColor);
}

