[[module CSS]]
@import url('https://fonts.googleapis.com/css2?family=Roboto:ital,wght@0,100;0,300;0,400;0,500;0,700;0,900;1,100;1,300;1,400;1,500;1,700;1,900&display=swap');
@import url('https://fonts.googleapis.com/css2?family=DM+Sans:wght@700&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Montserrat+Alternates:wght@100;200;300;400;500;600;700;800;900&display=swap');



/* 逻各斯 Sigma-9-10 */

/* 顶部背景 */

div#container-wrap {
  background-image: url("https://files.scpfoundation.net/local--files/theme:logos-sigma/logos-bg.png") ;
}

/* 逻各斯顶部和所有相关物 */

#header {
  background-image: url('https://files.scpfoundation.net/local--files/theme:logos-sigma/Logos2.png');
  background-size: 100px;
}
@media (max-width: 500px) { 
#header {
     background-size: 60px;
}
}
#header h1 a, #header h2 span {
  color: transparent;
  text-shadow: none;
}
#header h1 a::before {
  content: "逻各斯公司";
  color: rgb(232, 232, 232);
  text-shadow: none;
  font-family: 'DM Sans', sans-serif;
  font-weight: 800;
}
#header h2 span::before {
  content: '心灵为最高价值';
  color: rgb(232, 232, 232);
  text-shadow: none;
  font-family: 'DM Sans', sans-serif;
  font-weight: 500;
}



/* 逻各斯菜单和所有相关物 */
.random-article-block .menu-item:first-child:hover {
  background: linear-gradient(to top, #1e9b99 0%, #1e9b99 80%, #27b3b0 100%) !important;
}
.random-article-block .menu-item:last-child:hover {
  background: #1e9b99 !important;
}
.collapsible-side .collapsible-block-folded::before {
  background-color: rgba(39, 179, 176);
}
.collapsible-side .collapsible-block-unfolded-link {
  background: rgba(39, 179, 176);
}

.collapsible-side .collapsible-block-folded {
  background: linear-gradient(to left, white 50%, rgba(39, 179, 176) 50%) right;
  background-size: 200% 100%;
}

.corner-side::before {
  border-left: 7px solid rgba(39, 179, 176);
  border-top: 7px solid rgba(39, 179, 176);
}

#side-bar .heading, #interwiki .heading {
  font-size: 8pt;
  font-weight: 600;
  text-transform: uppercase;
  font-family: "Montserrat Alternates", sans-serif;
  color: rgba(22, 140, 138);
}
#side-bar div.menu-item img, #interwiki div.menu-item img {
  display: none;
}
@media (max-width: 767px) {
  #side-bar {
    background-color: rgba(22, 140, 138);
}
}

/* 颜色的链接+标签与菜单的链接 */

a {
  color: rgba(39, 179, 176);
}
#side-bar a:visited {
  color: rgba(39, 179, 176);
}
a:visited {
  color: rgba(22, 140, 138);
}

.yui-navset .yui-nav, .yui-navset .yui-navset-top .yui-nav {
  border-color: #27b3b0;
}

.yui-navset .yui-nav .selected a, .yui-navset .yui-nav .selected a:focus, .yui-navset .yui-nav .selected a:hover {
  background: rgb(39, 179, 176);
}
.yui-navset .yui-nav a:hover, .yui-navset .yui-nav a:focus {
  background: rgba(22, 140, 138);
  text-decoration: none;
  color: white;
}
.yui-navset .yui-nav a em, .yui-navset .yui-navset-top .yui-nav a em {
  top: 0;
}
#top-bar ul li ul a, #top-bar a:hover {
  color: rgba(39, 179, 176);
}
#top-bar ul li.sfhover a, #top-bar ul li:hover a {
  color: rgba(39, 179, 176);
}

/* 标题与页面的名称 */

#page-title {
  font-family: "Montserrat Alternates", sans-serif;
  color: black;
  font-weight: bold;
}
h1 {
  color: rgba(39, 179, 176);
}
h1,
h2,
h3,
h4,
h5,
h6 {
    font-family: "Montserrat Alternates", sans-serif;
    font-weight: 700;
}

/* 搜索 */

#search-top-box-input {
  background-color: rgba(22, 140, 138);
}
#search-top-box-input:hover, #search-top-box-input:focus {
  background-color: rgba(39, 179, 176);
}
#search-top-box-form input[type="submit"] {
  background-image: linear-gradient(to bottom, rgba(39, 179, 176), rgba(22, 140, 138));
}
#search-top-box-form input[type="submit"]:hover, #search-top-box-form input[type="submit"]:focus {
  background-image: linear-gradient(to bottom, rgba(39, 179, 176), rgba(22, 140, 138));
}

/* 脚页 */

#footer {
  background: rgba(39, 179, 176);
  color: black;
}

/* 自定义元素 */

.hr-logos {
  margin: 16px 0;
  padding: 0;
  height: 3px;
  border: none;
  background-color: #27b3b0;
  box-shadow: 0 2px 3px #ccc;
}

.left-logos {
  margin: 5px 10px;
  padding: 10px 15px;
  border-left: solid 3px black;
  background-color: #83d9de;
}

.wide-spoiler .collapsible-block {
    margin: 16px 0;
    border: 1px solid #dcdcdc;
    background: white;
    box-shadow: 0 2px 3px #ccc;
    transition: all 0.3s ease;
}
.wide-spoiler .collapsible-block:hover {
    border: #505052 solid 1px;
    box-shadow: 0 2px 3px #ccc;
}
.wide-spoiler .collapsible-block-content {
    margin: 16px 24px;
}
.wide-spoiler .collapsible-block-unfolded-link {
    text-align: center;
}
.wide-spoiler .collapsible-block-folded {
    text-align: center;
}
.wide-spoiler .collapsible-block-link {
    font-weight: bold;
    text-transform: uppercase;
    text-align: center;
    padding: 18px 24px;
    display: block;
}
/* 以下部分于spoiler删除spoiler边框 */
.wide-spoiler .collapsible-block .collapsible-block {
    border: none;
    box-shadow: none;
}
/* 这个是于spoiler删除spoiler内部的空格 */
.wide-spoiler .collapsible-block-content .collapsible-block-content {
    margin: 0px;
}

.notice-logos {
  border: solid 2px #83d9de;
  padding: 5px;
  margin: 2rem 0;
  text-align: center;
}

.notice-logos h4 {
  text-transform: uppercase;
}

.code {
  margin: 2rem 0 !important;
}
[[/module]]

