/* CUERPO */


:root{
    --font-body:Huiwen-mincho,  宋体,  'Noto Serif SC', 'HanWang WeiBeiMedium-Gb5', 'Special Elite', cursive;
    --font-title: 'HanWang WeiBeiMedium-Gb5', 华文新魏, 华文隶书, lisu, 'Noto Sans SC', 'Special Elite', cursive;

}


body {
   background-color: #fff7e5;
   font-family: var(--font-body);
   background-image: url('https://lafundacionscp.wdfiles.com/local--files/theme%3Amac/fondo.png');
}

#page-content {
   font-size: 124%;
}

/* TÍTULO DE LA PÁGINA */

#page-title {
   text-align: center;
   border-bottom: solid 1px black;
   font-size: 275%;
}

@media (max-width: 767px) {
   #page-title {
      font-size: 225%;
   }
}

/* TÍTULOS EN CUERPO */

h1 {
    font-family:  var(--font-title);
   font-weight: bold;
   font-size: 230%;
   margin-bottom: 0.4em;
   margin-top: 0.4em;
   color: black;
}

h2 {
   font-family:  var(--font-body);
   font-weight:normal;
   font-style:normal;
}

h3 {
   font-family: var(--font-body);
   font-weight:normal;
   font-style:normal;
}

h4 {
   font-family: var(--font-body);
   font-weight:normal;
   font-style:normal;
}

h5 {
   font-family:  var(--font-body);
   font-weight:normal;
   font-style:normal;
}

h6 {
   font-family:  var(--font-body);
   font-weight:normal;
   font-style:normal;
}


/* HEADER */

div#container-wrap {
    background: url(https://lafundacionscp.wdfiles.com/local--files/theme%3Amac/header.png) top center repeat-x;
}

/* LOGO DE CABECERA */

#header {
    background-image: url(https://lafundacionscp.wdfiles.com/local--files/theme%3Amac/logo-int.svg);
    height: 13rem;
    position: relative;
    z-index: 10;
    padding-bottom: 25px;
    background-repeat: no-repeat;
    background-size: 250px;
    background-position: 0rem  -0.9rem;
}

/* TÍTULO DE CABECERA */

#header h1 {
    margin-left: 0;
    float: none;
    text-align: center;
    margin-top: 55x;
    padding: 0;
    max-height: 113px;
    filter: none;
}

#header h2 {
    margin-left: 175px;
    float: none;
    text-align: center;
    padding: 0;
    clear: left;
    font-size: 105%;
    max-height: 38px;
    filter: none;
}

#header h1 a {
    display: block;
    content: "";
    margin: 0;
    line-height: 0px;
    max-height: 0px;
    color: transparent;
    background: transparent;
    font-size: 120%;
    text-decoration: none;
    text-shadow: none;
    filter: none;
}

#header h1 a::before {
    display: inline-block;
    width: 100%;
    text-align: center;
    content: "古巴异常部";
    color: #FFF;
    font-size: 60px;
    text-shadow: 5px 5px 0 #000, 5px -5px 0 #000, -5px 5px 0 #000, -5px -5px 0 #000, 5px 0px 0 #000, 0px 5px 0 #000, -5px 0px 0 #000, 0px -5px 0 #000;
    position: absolute;
    left: unset;
    transform: translateX(-50%);
    top: 70px;
    padding-top: 25px;
    font-family:  var(--font-title);
    font-weight: bold;
    letter-spacing: 0.1em;
    right: -55%;
}


/* SUBTÍTULO DE CABECERA */
 #header h2  {
    margin-left: 0;
}

#header h2 span {
    position: relative;
    display: block;
    margin: 0;
    padding: 40px 235px;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: transparent;
    text-shadow: none;
    filter: none;
    margin-left: 0;
}

#header h2 span::before {
     position: absolute;
     content: '向着胜利，直到永远';
     font-family:  var(--font-body);
     display: block;
     margin: 0;
    margin-top: 0.5em;
     right:33%;
     line-height: 0px;
     max-height: 0px;
     font-weight: bold;
     color: #FFF;
     font-size: 175%;
     filter: none;
     padding-top: 0;
     padding-bottom: 0;
     padding-right: auto;
     padding-left: auto;
     text-align: center;
     text-shadow: 3px 3px 0 #000, 3px -3px 0 #000, -3px 3px 0 #000, -3px -3px 0 #000, 3px 0px 0 #000, 0px 3px 0 #000, -3px 0px 0 #000, 0px -3px 0 #000;
}


@media (max-width: 600px) {
  #header h1 a::before {
    content: '古异部';
    font-size: 150%;
      top: 120px;
          right:-70%;
          letter-spacing: 0em

  }

    #header h2 span {
    padding: 40px 0px;
    margin-top: 40px;
    margin-left: 30%;
                  right:-5%;


}
    #header h2 span::before {
        content: '国家或死亡';
         font-size: 165%;

 }
}


/* BARRA SUPERIOR */

#top-bar {
    top: 13.95rem;
    right: 3rem;
    height: 3rem;
    line-height: 3rem;
    font-size: 110%;
}

@media (max-width: 767px) {
#top-bar {
    top: 12.3rem;
    left: auto;
    right: auto;
}
}

#top-bar a {
    color: #fff7ea;
}

#top-bar ul li a {
    border-left: solid 1px rgba(255, 0, 0, 0);
    border-right: solid 1px rgba(255, 0, 0, 0);
    text-decoration: none;
    padding-top: 10px;
    padding-bottom: 10px;
    padding-left:0.6em;
    padding-right:0.6em;
    line-height: 1px;
    max-height: 1px;
    overflow: hidden;
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background-color: #ece1c5;
    color: #bc0302;
    border-left: solid 1px #bc0302;
    border-right: solid 1px #bc0302;
}

#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
    border-width: 0;
    width: 150px;
    line-height: 160%;
    height: auto;
    max-height: none;
    padding-top: 0;
    padding-bottom: 0;
}

#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background: #bc0302;
    text-decoration: none;
    color: #ece1c5;
}

.topbar_arrow {
    color: #bc0302;
}

/* CUENTA */

#login-status {
    top: 0.5rem;
    right: 0.7rem;
    color: #FFF;
}

#login-status a {
    color: #FFF;
}

#account-topbutton {
    font-size: 0rem;
    border: none;  
    padding: 0;
    margin: 0;
    padding: 0;
    position:absolute;
    right: -1.5rem;
    bottom: 0rem;
}

#account-topbutton:before {
    content: '★';
    color: #FFF;
    font-size: 1rem;
    padding: 2px;
    margin-top: 0.2rem;
    border: solid 2px #bc0302;
    background-color: #bc0302;
}

#account-options {
    border: solid 2px #bc0302;
    background-color: #ece1c5;
    color: #bc0302;
}

#login-status ul a {
    color: #bc0302;
}

#login-status ul a:hover {
    color: #ece1c5;
    background-color: #bc0302;
}

/* BUSCADOR */

#search-top-box {
    top: 2.5rem;
    right: -0.8rem;
}

#search-top-box-input,
#search-top-box-input:focus,
#search-top-box-form input[type=submit],
#search-top-box-form input[type=submit]:focus {
    background: #bc0302;
    box-shadow: none;
    border-radius: 0px;
    color: #FFF;
    border: solid 2px #bc0302;
}

#search-top-box-input:hover,
#search-top-box-form input[type=submit]:hover {
    background: #FFF;
    box-shadow: none;
    border: solid 2px #FFF;
    color: #bc0302
}

#search-top-box-input:focus,
#search-top-box-form input[type=submit]:focus {
    background: #bc0302;
    border: solid 2px #bc0302;
    box-shadow: none;
    color: #FFF;
}

/* BARRA LATERAL */

#side-bar .side-block {
    padding: 10px;
    border-top: solid 1px #000;
    border-left: solid 1px #000;
    border-right: solid 1px #000;
    border-bottom: solid 1px #000;
    margin-bottom: -1px;
    border-radius: 0px;
    box-shadow: none;
    background-color: #ece1c5;
    font-size: 110%;
}

#side-bar::before {
    content: '';
    background-image: url(https://lafundacionscp.wdfiles.com/local--files/theme%3Amac/MAC-1.png);
    background-size: 4.5rem;
    background-repeat: no-repeat;
    background-position: top center;
    background-color: #bc0302;
    padding-bottom: 0.3rem;
    padding-top: 4.2rem;
    display: inline-block;
    border-top: solid 1px #000;
    border-right: solid 1px #000;
    border-left: solid 1px #000;
    border-bottom: none;
    font-family: 'Special Elite', cursive;
    font-weight: 800;
    text-align: center;
    margin-right: auto;
    margin-left: auto;
    margin-bottom: -2px;
        width: 99.5%;
    text-transform: none;
}

@media (max-width: 767px) {
    #side-bar::before {
        width: 99.5%;
    }
}

#side-bar .side-block.blue, #side-bar .side-block.media {
    background: #ece1c5;
}

#side-bar .side-block.red, #side-bar .side-block.media {
    background: #ece1c5;
}

/* MENÚ BARRA LATERAL */

@media (max-width: 767px) {
    #side-bar {
        opacity: 1;
        background: #ece1c5;
    }
}

.open-menu a,
#top-bar .open-menu a {
    border: solid 5px #000 !important;
    background-color: #bc0302 !important;
    color: #FFF !important;
}

.open-menu a:hover,
#top-bar .open-menu a:hover {
    text-decoration: none !important;
    -webkit-box-shadow: 0px 0px 20px 3px rgba(188,3,2,.7);
    -moz-box-shadow: 0px 0px 20px 3px rgba(188,3,2,.7);
    -ms-box-shadow: 0px 0px 20px 3px rgba(188,3,2,.7);
    -o-box-shadow: 0px 0px 20px 3px rgba(188,3,2,.7);
    box-shadow: 0px 0px 20px 3px rgba(188,3,2,.7);
}

/* INTERWIKI */

#interwiki .side-block {
    padding: 10px;
    border: 1px solid #000;
    border-radius: 0px;
    box-shadow: none;
    background: #ece1c5;
    margin-bottom: 15px;
    margin-top: 10px;
}

#interwiki .heading {
    color: #b01;
    border-bottom: solid 1px #000;
    padding-left: 15px;
    margin-top: 10px;
    margin-bottom: 5px;
    font-size: 8pt;
    font-weight: bold;
}

#interwiki a,
#interwiki a:visited,
#interwiki a.newpage {
    color: #b01;
}

/* PUNTUACIÓN */

.page-rate-widget-box {
    margin-right: 0px;
    border: solid 2px #bc0302 !important;
}

.creditRate {
    margin-right: 0px!important;
}

.page-rate-widget-box .rate-points,
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown,
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a,
.page-rate-widget-box .cancel a {
  background-color: transparent !important;
  border: 0;
  text-transform: capitalize;
  font-weight: bold;
    color: #FFF!important;
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    margin-right: 0px;
    margin-left: 0px;
}
div.page-rate-widget-box {
    background-color: #bc0302 !important;
    border: solid #bc0302 1px!important;
    border-radius: 0px;
    box-shadow: none;
}
div.page-rate-widget-box span.rate-points,
div.page-rate-widget-box span.cancel {
    background-color: #bc0302 !important;
    border: 1px #bc0302;
}
div.page-rate-widget-box span.rateup,
div.page-rate-widget-box span.ratedown {
    background-color: #fff7ea !important;
    margin-right: 0px;
    margin-left: 0px;
    color: #bc0302!important;
}
div.page-rate-widget-box span.rateup a,
div.page-rate-widget-box span.ratedown a {
    color: #bc0302!important;
}
div.page-rate-widget-box span.rateup a:hover {
    background: #bc0302 !important;
    color: #fff7ea !important;
}
div.page-rate-widget-box span.ratedown a:hover {
    background: #bc0302 !important;
    color: #fff7ea !important;
}
div.page-rate-widget-box span.cancel a {
    color: #fff7ea !important;
}
div.page-rate-widget-box span.cancel a:hover {
    background: #bc0302 !important;
    color: #fff7ea !important;
    border-radius: 0px;
}

/* PUNTUACIÓN CON INFO */

#page-content .rate-box-with-credit-button {
    background-color: #bc0302;
    border: 1px solid #bc0302;
    box-shadow: 1px 1px 3px rgba(0,0,0,.5);
    border-radius: 0px;
    box-shadow: none;
}

#page-content .rate-box-with-credit-button .creditButton p a {
    border-left-color: #ffffff;
}

#page-content .rate-box-with-credit-button .fa-info {
     border-color: #ffffff;
     color: #ffffff;
}
#page-content .rate-box-with-credit-button .fa-info:hover a {
     color: red;
}

.creditButton p a:hover {
     color: #2a47c5 !important;
}

.close-credits,
.credit-back {
    filter: hue-rotate(0deg);
}

#page-content .modalbox {
    box-shadow: 0 2px 6px rgba(0,30,0,.5);
}

/* BLOCKQUOTE */

blockquote,
div.blockquote {
    background-color: #ece1c5;
    border: solid 1px #000;
}

/* BARRA SEPARADORA */

hr {
    background-color: #000;
}

/* IMAGE BLOCK */

.scp-image-block .scp-image-caption {
    background-color: #ece1c5;
    border-top: solid 1px #000;
}

.scp-image-block {
    box-shadow: none;
}

.scp-image-block .scp-image-caption > p {
    background-color: #ece1c5;
    color: #000;
    font-size: 110%;
    font-weight: normal;
    box-shadow: none;
}

/* TABVIEW */

/* This is the parent class for most of the tab module.  */
.yui-navset .yui-content {
    background-color: #ece1c5;
    padding: 10px 25px 10px 25px;
    border-width: 15px 1px 1px 1px;
    border-color: #000;
}

/* This section affects unselected tabs. */
.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
    background-color: transparent;
    background-image: url(https://lafundacionscp.wdfiles.com/local--files/theme%3Amac/yuitabs7.png);
    background-size: cover;
    border-width: 0px;
    padding: 2px 20px 0px 20px;
    margin-top: 10px;
    border-top: none;
    color:#FFF;
}

/* This section affects below tabs. */
.yui-nav {
    background-color: transparent;
    position: relative;
    z-index: 15;
}

.yui-nav::before {
    position: absolute;
    display: inline-block;
    content: '';
    border-top: solid 10px black;
    border-left: solid 1px black;
    border-right: solid 1px black;
    border-bottom: solid 1px black;
    background-color: #ece1c5;
    margin-top: 2rem;
    margin-right: auto;
    margin-left: auto;
    margin-right: 2px !important;
    width: calc(100% - 1px);
    height: 50%;
    z-index: -13;
}

@media (max-width: 767px) {
    .yui-nav::before {
        width: calc(100% - 2px);
        background-color: #000;
        height: 70%;
    }
}

/* This section affects the currently selected tab. */
.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus, /* no focus effect for selected */
.yui-navset .yui-nav .selected a:hover { /* no hover effect for selected */
    background:transparent url(https://lafundacionscp.wdfiles.com/local--files/theme%3Amac/yuitabs8.png) ; /* selected tab background */
    color:#FFF;
    background-size: cover;
    border-width: 0px;
    padding: 2px 20px 0px 20px;
    margin-top: 10px;
    border-top: none;
}

/* This section affects tabs you are hovering over. */
.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus {
    background:transparent url(https://lafundacionscp.wdfiles.com/local--files/theme%3Amac/yuitabs8.png) ;
    background-size: cover;
    text-decoration: hover;
    border-width: 0px;
    padding: 2px 20px 0px 20px;
    margin-top: 10px;
    border-top: none;
}

/* This removes the border around tab images. */
.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    border-color: transparent;
}
.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    border-color: #000;
}
.yui-navset li {
    line-height: normal;
}
.yui-navset .yui-nav a em, .yui-navset .yui-navset-top .yui-nav a em {
    border-color: none;
    border-width: 0;
    bottom: 0;
    left: 0;
    padding: 0.25em 0.75em;
    position: relative;
    right: 0;
    top: -1px;
}

/* TABLAS */

table.wiki-content-table th {
    border: solid 1px #000;
    padding: 0.3em 0.7em;
    background: #ece1c5;
}

table.wiki-content-table td {
    border: solid 1px #000;
}

/* OWINDOW */

.owindow {
    background-color: #ece1c5;
}

.owindow .title {
    background-color: #bc0302;
    color: #fff7ea;
}

.owindow img {
    background-color: #ece1c5 !important;
}

.owindow .button-bar a {
    background-color: #bc0302;
    border: solid 2px #bc0302;
    color: #FFF;
}

.owindow .button-bar a:hover {
    background-color: #FFF;
    border: solid 2px #FFF;
    color: #bc0302;
}

.owindow.owait .content {
    padding-bottom: 2rem;
    background-image: url("https://lafundacionscp.wdfiles.com/local--files/theme%3Amac/mac_loader.gif");
}

/* NOTAS AL PIE */

.hovertip {
    width: 20em;
    padding: 0.5em;
    border: solid 1px #000 !important;
    color: #000;
    text-align: center;
    background-color: #ece1c5 !important;
    z-index: 100;
}

.footnote .f-heading, .equation .e-heading, .reference .r-heading {
    display: none;
}

.footnote .f-footer, .equation .e-footer, .reference .r-footer {
    display: none;
}

a.footnoteref {
    font-weight: bold;
    background-color: black;
    margin-right: 2px;
    margin-left: 2px;
    font-size: 113%;
    color: #ece1c5;
    padding: 2px;
}

.footnotes-footer {
    display: none;
}

/* MODALBOX */

.modalbox {
    background: #ece1c5 !important;
    border-radius: 0px;
}

.modalbox h2 {
   font-family: "Big Shoulders Stencil Text", sans-serif;
   font-weight: bold;
   font-size: 230%;
   margin-bottom: 0.4em;
   margin-top: 0.4em;
   color: black;
}

.modalbox hr {
    background-color: #000;
    background-image: none;
}

.modalbox hr::before {
    background-image: none;
}

.modalbox hr::after {
    background-image: none;
    background-color: #ece1c5;
}

/* ETIQUETAS */

.page-tags {
    border-top: solid 1px black;
}

.page-tags a {
    padding: 0.2rem 0.2rem;
    margin: 0 0.05rem;
    font-size: 113%;
}

.page-tags a:hover {
    background-color: #bc0302;
    color: #FFF;
    text-decoration: none;
}

/* SCROLLBAR */

::-webkit-scrollbar {
    width: 10px;
    border-left: solid 1px #000;
}

::-webkit-scrollbar-thumb {
    background-color: #bc0302;
    background-repeat: repeat;
    background-position: top right;
    border: solid 0px #000;
}

::-webkit-scrollbar-track {
    background: #ece1c5;
    border-left: solid 0px #000;
}

/* CODE */

.code {
    background-color: #ece1c5;
    border: dashed 2px #000;
}

/* FOOTER */

#footer {
    background-color: #bc0302;
    color: #FFF;
}


