--box-bk-0:0.0625rem 0.0625rem 0 rgba(var(--cl-a1)), 0.125rem 0.125rem 0 rgba(var(--cl-a2)), 0.1875rem 0.1875rem 0 rgba(var(--cl-a2)), 0.25rem 0.25rem 0 rgba(var(--cl-a3)), 0.3125rem 0.3125rem 0 rgba(var(--cl-a2)), 0.375rem 0.375rem 0 rgba(var(--cl-a2)), 0.4375rem 0.4375rem 0 rgba(var(--cl-a2)), 0.8rem 0.8rem 0 rgba(var(--cl-a4));
/* 书籍拱起描边 */ 
  --NOTE-book-arch:drop-shadow(0px -1px 0px rgb(var(--cl-a4))) drop-shadow(0px -1px 0px rgb(var(--cl-a4))) drop-shadow(0px -1px 0px rgb(var(--cl-a4))) drop-shadow(0px -1px 0px rgb(var(--cl-a4))) drop-shadow(0px -1px 0px rgb(var(--cl-a4)));

/**/    

  --NOTEPAGE-txt_size: 1.1rem; 
  --NOTEPAGE-p_padding-top:0em;  

    --NOTEPAGE-line_size: 1.5em;

    --NOTEPAGE-line-height: 1.5em; 
    --NOTEPAGE-line-height_half: calc(var(--NOTEPAGE-line-height) / 2);
    --NOTEPAGE-line-height_half-OPS: calc( 0em -  var(--NOTEPAGE-line-height_half));
--color-rowline: #c0c0c0;



    --NOTEPAGE-side_width: 1rem;
   --NOTEPAGE-side_height: 100%; 
    --NOTEPAGE-txt_border-size: 0;

    --MASK-chew_repeat: repeat;
