    --NOTE-quote_bg-size:  calc(var(--NOTEPAGE-line_size) * 1.4) calc(var(--NOTEPAGE-line_size) * 1.4);
     --NOTE-quote_height:100%;
     --NOTE-quote-side_position: 0 0.5em;
    --NOTE-quote-side_clip: inset(0 0 0 25%);
     --NOTE-quote_padding_lr: 0.5em; 
     --NOTE-quote_padding: var(--NOTEPAGE-line-height_half) var( --NOTE-quote_padding_lr); 

    /* 书缝 */
        --NOTE-bookseam_bg: 55,55,55;
     --NOTE-bookseam_width: 2rem;
    --NOTE-bookseam_width-half: -1rem;

}

@media (max-width: 550px) {    
   .notebook-body, .notepage-body{
     --NOTEPAGE-txt_size: 0.9rem; 
     --NOTE-bookmark_display:none;
     --NOTE-bookseam_display:none;
    }  

    .mz-notebook-wrap {        
    --NOTEBOOK-grid:'left' 'right';
    --NOTEBOOK-grid-columns:auto;
    --NOTEBOOK-mid_display:none;
    --NOTEBOOK-bookmark_display:none;    
    --box-bk-0:none;
     --NOTEPAGE-side_width: 0.6rem;
     --NOTEBOOK_padding:0 0 1em 0;
        --NOTEBOOK_border:3px solid #bbb;
    }

    .notebook-right{
        --NOTEBOOK-bookmark_display:none;
        margin-top: calc( 0em - var(--NOTEPAGE-line-height) / 2);
    }

}

/*---------------------------
整本
--------------------------*/

.notebook-body{   
    display:grid;
     grid-template-areas:  var(--NOTEBOOK-grid,'left mid right');
    grid-template-columns: var(--NOTEBOOK-grid-columns, 50% auto 50%);

    -moz-box-shadow:  var(--box-bk-0);
    -webkit-box-shadow: var(--box-bk-0);
    -o-box-shadow: var(--box-bk-0);
    -ms-box-shadow: var(--box-bk-0);
    box-shadow: var(--box-bk-0);
    background-color: #f3f3f3;
    background-color: var(--page-color);
    border:var(--NOTEBOOK_border, 1px solid #cccccc); 
    background-color: var(--color-notepage);
    padding: var(--NOTEBOOK_padding, 0);
}


.notebook-left{
    grid-area: left;
}

.notebook-mid{
    grid-area: mid;
    width: 0.15em;
    height: 100%;
    background-color: var(--color-dark);
    display: var(--NOTEBOOK-mid_display, inline-grid);
    grid-template-rows: 100% 0%;
}

.notebook-right{
    grid-area: right;
}

.notebook-body .notepage-main{
   margin: 0 var(--NOTEPAGE-side_width) var(--NOTEPAGE-side_width) var(--NOTEPAGE-side_width); 
    height: 100%;
    width: calc(100% - var(--NOTEPAGE-side_width) * 2 );
    align-self: flex-start;
    align-items: flex-start;
    align-content: flex-start
}

.notebook-mid:before{
     content: ' ';
    display: inline-grid;     
}

.notebook-mid:after{
   content: '▲';
    display: inline-grid;
    position: relative;
    width: 0rem;
    height: 0rem;
    left:-0.55rem;
    bottom:-0.4rem;
   color: var(--page-bg);
    font-size: 1.2rem;
    line-height: 0;

   -webkit-filter: var(--NOTE-book-arch);
    filter: var(--NOTE-book-arch);  
}

.mz-notebook-wrap .notebook-body:before{
    content: "";
    display:  var(--NOTE-bookseam_display, inline-grid);
    grid-area: right;
    width: var(--NOTE-bookseam_width);
    margin-left: var(--NOTE-bookseam_width-half);
    position: relative;
    background-image: linear-gradient(to left,transparent 0%, transparent 20%, rgba(var(--NOTE-bookseam_bg),0.1) 40%, rgba(var(--NOTE-bookseam_bg),0.2) 50%, rgba(var(--NOTE-bookseam_bg),0.1) 60%, transparent 80%, transparent 100%);
}

.notebook-noseam{
    --NOTE-bookseam_display:none;
}


/*---------- 书签 --------------*/
.notebook-bookmark{
    display:grid;
    grid-template-areas: 'mid ';
}

.notebook-bookmark:before{
    grid-area: mid;
    content: ' ';
    display:var(--NOTEBOOK-bookmark_display,block);
    position: relative;
    width: var(--NOTEBOOK-bookmark_width, 1.2rem);
    height:var(--NOTEBOOK-bookmark_height, 20rem);
    max-height: 40%; 
    background-color: var(--NOTEBOOK-bookmark_color);
    margin-top: -1px;
    margin-left:var(--NOTEBOOK-bookmark_left, 90%);
    z-index: 2;

-webkit-clip-path:var(--NOTEBOOK-bookmark-clip);
clip-path: var(--NOTEBOOK-bookmark-clip);

    --NOTEBOOK-bookmark-clip:polygon(0 0, 100% 0, 100% 100%, 50% 95%, 0 100%);

}

/*---------------------------
单页与边缘
--------------------------*/

.mz-notepage-wrap{
    width: 100%;
    margin: 0 auto;
   -webkit-filter:drop-shadow(1px 2px 2px #333);
    filter: drop-shadow(1px 2px 2px #333);
    height: auto;
}

.notepage-body{
 display: inline-grid;
    grid-template-areas:
        'header header header'
        'left top  right'
        'left mid right'
        'left bottom right'
        'footer footer footer';
    grid-gap: 0;

}

.notepage-body:before,
.notepage-body:after{
        content: ' ';
    width: var(--NOTEPAGE-side_width);
    height: var(--NOTEPAGE-side_height); 
    -webkit-mask-position: left;
            mask-position: top;
     -webkit-mask-repeat:var(--MASK-chew_repeat);
         mask-repeat: var(--MASK-chew_repeat);
     -webkit-mask-size: var(--MASK-chew_size);
         mask-size:var(--MASK-chew_size);
            background-color: var(--color-notepage);
}

.notepage-body:before{
    grid-area: var(--NOTEPAGE-before_area,left);  
    -webkit-mask-image:var(--MASK-chew-before);
            mask-image:var(--MASK-chew-before);
    margin: var(--MASK-chew-before_margin);
    display: var(--NOTEPAGE-before_display, inherit);  
}

.notepage-body:after{
    grid-area:var(--NOTEPAGE-after_area,right);  
            -webkit-mask-image:var(--MASK-chew-after);
            mask-image:var(--MASK-chew-after);
        margin: var(--MASK-chew-after_margin);
    display: var(--NOTEPAGE-after_display, inherit);  
}

.mz-notepage-wrap :is(.blockquote, blockquote):before{
    content: " ";
    display: block;
    position: absolute; 
    margin:-2.5em 0em 0 -0.5em;
    width: 1.5em;
    height: 6em;
    background: var(--NOTE-tap_bg, rgba(55,55,55,0.1));
    transform: rotate(45deg);
    border: var(--NOTE-tap_bd, rgba(33,33,33,0.4) solid 1.5px);
}

/* 撕痕 */
.chew-left .notepage-body:before{
   --MASK-chew-before: var(--MASK-chew);
    --NOTEPAGE-before_display:inline-grid;
}

.chew-right .notepage-body:after{
   --MASK-chew-after: var(--MASK-chew);
   --NOTEPAGE-after_display:inline-grid;
   -webkit-transform:scale(-1);
    transform: scale(-1);
}

.chew-bottom,
.chew-top{
    --NOTEPAGE-txt_border-size:0.8em;
    --NOTEPAGE-before_area:header;
    --NOTEPAGE-after_area:footer;
    --NOTEPAGE-side_width: 100%;
   --NOTEPAGE-side_height: 1rem; 
     --MASK-chew_repeat: repeat-x;
    --MASK-chew_size:14rem;
    --NOTEPAGE-before_display:none;
    --NOTEPAGE-after_display:none;
    --NOTEPAGE-header_font-size:0;
    --NOTEPAGE-footer_font-size:0; 

    --NOTE-quote_width:100%;
    --NOTE-quote_bg-size:3em;
    --NOTE-quote_height:3em;
     --NOTE-quote-side_clip:inset(0 0 25% 0);
   --NOTE-quote-side_position: 0em 0;
  --NOTE-quote-side_repeat:repeat-x;
}

