    -moz-transform:scale(-1);
    -webkit-transform:scale(-1);
    -o-transform:scale(-1);
   transform:scale(-1);
    left:unset;
    right:var(--tk-delta-verti);
    top:unset;
    bottom:0;
}

.talkbox-inby.talkbox-righttop::before,
.talkbox-inby.talkbox-righttop::after {
   -moz-transform:scale(-1,1);
    -webkit-transform:scale(-1,1);
    -o-transform:scale(-1,1);
        transform:scale(-1,1);
    left:unset;
    right:var(--tk-delta-verti);
    top:0;
    bottom:unset;
}

