/*=====================================
杂志源代码 - 本体 - 000000000000
=====================================*/

@import url(https://scp-wiki-cn.wikidot.com/theme:magazine/code/1);

@import url('https://fonts.googleapis.com/css2?family=Noto+Serif+SC:wght@500;600&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Zhi+Mang+Xing&display=swap');

/* 自定义 */

#main-content{    
    width:{$content-width};
}

div#header h1 a::before {
    content:{$header-title};
}

div#header h2::before {
   content: {$header-subtitle};
}

#header-extra-div-1::before {  
    background-image:{$logo-image};
}


@media (max-width: 767px) {
 div#header h1 a::before {
    content:{$header-title};
    content:{$pad-title};
   }

  div#header h2::before {
    content:{$header-subtitle};
    content:{$pad-subtitle};
   }

   #header-extra-div-1::before {  
       background-image:{$logo-image};
       background-image: {$logo-image-pad};
    }
}

@media (max-width: 500px) {
  div#header h1 a::before {
    content:{$header-title};
    content:{$pad-title};
    content:{$mob-title};
    }

    div#header h2::before {
    content: {$header-subtitle};
    content:{$pad-subtitle};
    content: {$mob-subtitle};
   }

   #header-extra-div-1::before {  
       background-image:{$logo-image};
       background-image: {$logo-image-pad};
       background-image: {$logo-image-mob};
    }
}

#side-bar:before {
    background-image:{$logo-sidebar};
    content:{$sidebar-content};
}


#page-title{
   display:{$page-title};
}


#page-title::after{
    content:{$title-orn-content};
    font-size:{$title-orn-size};
    line-height:calc( {$title-orn-size} * 0.7); 
    width: {$title-orn-width};
    color:{$title-orn-color};
    word-break:{$title-orn-break}
}

#page-title::before,
#page-title::after{
display: {$title-underlines}
}
