content:
[[iftags +theme]]
[[collapsible show="+ CODE" hide="- HIDE"]]
[!---------------1111111111111111111111111111111111111-------------------------------]
[[code type="CSS"]]
/*=====================================
Magazine Theme - sigma9
[2021 Wikidot Theme]
by Etinjat

Inspired by EstrellaYoshte, hoah2333 and MalyceGraves
Magazine logo is created by Kcorena 
Bird icon from https://pixabay.com/zh/vectors/eagle-bird-animal-perching-162199/
=====================================*/

/* FONTS */
@import url('https://fonts.googleapis.com/css2?family=DM+Serif+Display&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Yesteryear&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Roboto+Slab:wght@300;400;500;700&display=swap');
@import url('https://fonts.googleapis.com/css2?family=IM+Fell+English&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Special+Elite&display=swap');
/*=====================================
根属性 ROOT
=====================================*/

:root{

/* ----------- 页面 || PAGE ----------- */

--content-width:inherit;
--main-width:inherit;

    --main-top:var(--TB_height_half);
    --main-top_PAD:min(var(--PGtt_size),1.3rem); 
    --main-top_MOB:min(var(--PGtt_size),1rem);   



/* ----------- 字体 || FONT ----------- */

    --page-font: 'Roboto Slab', 'Markazi Text', 'Nanum Gothic', verdana, arial, helvetica, sans-serif;
    --handwriting-font:'Yesteryear', 'La Belle Aurore', "Monotype Corsiva", "Bradley Hand ITC", sans-serif;
    --page-tt-font:'DM Serif Display', 'Roboto Slab', 'BauhausLTDemi', sans-serif;   
    --header-tt-font:'IM Fell English', 'DM Serif Display','Roboto Slab', 'BauhausLTDemi', sans-serif;   
    --mono-font:'Courier New', 'Nanum Gothic Coding', 'Ubuntu Mono', "Trebuchet MS", Courier, monospace;
    --teletype-font:'Special Elite', 'IM Fell English', Courier, monospace;


--header-tt-font-weight:500;
--HD-h2_font-weight:600;

--mob-tt-font:var(--page-tt-font);

/* 用户栏文本 */
--font_printuser: var(--handwriting-font);
--font_my-account: var(--teletype-font);

/* 编辑区域 */    
--EDIT-box_font:  "Trebuchet MS", var(--page-font);
--EDIT-btn_font: 'Nanum Gothic', 'Special Elite', "Monotype Corsiva", "Bradley Hand ITC",var(--page-font);
--EDIT-help_font: fontawesome, 'Nanum Gothic', "Trebuchet MS", var(--mono-font);

/* ----------- 颜色 || BASE COLOR----------- */

--dark-accent:rgb(110, 100, 105);
--page-bg:var(--color-grey);
--accentColor:#542429;

--color-red:#990314;
--color-darkred:#6f1212;
--color-grey:#ecebf0;
--color-grey-pale:#eee;
--color-grey-dark:#e0e1e1;
--color-grey-darker:#d2d2d2;
--color-grey-darkest:#c4c4c4;
--swatch-primary-darker:egb(64,96,22);

--color-kraft:rgb(185,137,102);
--color-navy:rgb(20,21,39);
--color-blue:#2c477d;
--color-paleblue:#4e5166;
--color-gold:#c39c52;
--color-brown:#ead1b3;

--color-light:#e8e8e8;
--color-darkest:#000;
--color-darker:#333;
--color-dark:#555;  

/* ------------ 版头 ------------------  */

   /* 版头 和 标题 */
 --HD_bg:var(--color-kraft);
 --TB_bg:var(--color-navy);
 --TB_txt:var(--color-gold);
 --HD_mob_HOV:var(--color-red);
 --HDtt_color:var(--accentColor);
 --HD-h2_color:var(--color-darkest);     

 --HD_height:var(--HD_height_PC);
 --HD_height_PC:max(8rem, var(--HDtt_height));  
 --HD_height_PAD:var(--HD_height_PC);  
 --HD_height_MOB:var(--HD_height_PC);  
 --HD_blank:var(--SB_width-total,16rem);    


    /* 页眉标题 */
--header-title:"FOUNDATION";
--header-subtitle:"Secure Contain Protect";
--mob-title:"S C P";
--mob-subtitle:"FOUNDATION";
--pad-title:var(--header-title);
--pad-subtitle:var(--header-subtitle);

    --HDtt_top:2rem;
    --HDtt_size:4rem;
    --HDtt_size_PAD:3rem;
    --HDtt_size_MOB:var(--HDtt_size_PAD);
    --HD-h2_size:1.5rem;
    --HD-h2_size_PAD:1.25rem;
    --HD-h2_size_MOB:var(--HD-h2_size_PAD);
    --HDtt_gap:0.5rem;
    --HDtt_gap_MOB:0.25rem;
    --HDtt_height:calc(var(--HDtt_size) + var(--HDtt_gap) + var(--HD-h2_size) + var(--TB_height));
    --HD-h1_top:inherit;
    --HD-h2_top:-0.25em;
    --HD-h1_top_PAD:1rem;
    --HD-h1_top_MOB:var(--HD-h1_top_PAD);

  /* 用户栏和搜索栏 */  
     --SEARCH_line:var(--accentColor);
     --SEARCH_bg:transparent;
     --SEARCH_bg_HOV:var(--SEARCH_line);
     --SEARCH_txt:var(--SEARCH_line);
     --SEARCH_txt_HOV:var(--HD_bg);
    --USER_line:var(--HDtt_color);
    --USER_unline:var(--HD-h2_color);
	--USER-list_txt:var(--HDtt_color);
    --USER-list_txt_HOV:var(--color-gold);
    --HD-btn_line_HOV:var(--HD_mob_HOV);
    --HD-btn_bg_HOV:var(--HDtt_color);
    --HD-btn_line_HOV:var(--SEARCH_txt_HOV);

 --STATUS_maxwidth:var(--HD_blank); 
 --STATUS_top:1rem;
 --STATUS_padding:0.5rem; 
 --STATUS-btn_padding:0.5rem;
 --STATUS_grid: 1.5rem;
 --STATUS_grid-gap: 0.25rem;
 --STATUS-btn_grid: 2rem;
 --STATUS-btn_btn: 1.2rem;    
 --STATUS-btn_size: 1rem;
 --USER_font-size:var(--STATUS_grid);
 --unlogin-font-size: 1.25rem;
 --STATUS_row-11:var(--STATUS_grid);
 --STATUS_row-22:var(--STATUS_grid);
 --STATUS_column-11:0;
 --STATUS_column-22:var(--STATUS-btn_grid);
 --STATUS_column-33:minmax(5rem,auto);   

  --SEARCH_top:calc(var(--STATUS_top) + var(--STATUS_padding) * 3 + var(--USER_font-size) * 2 );  
   --HD-menu_minwidth:10rem;
   --HD-menu_maxwidth:var(--HD_blank);
   --HD-menu_height:var(--STATUS_grid);

/* 顶栏 */    
   --TB_height:1.8rem;
   --TB_height_half:calc(var(--TB_height) / 2 + var(--HDtt_gap) / 2);
   --TB_font-size:90%;

/* -------------------------------- 标志 ---------------------------------------- */
    --HD_cover:var(--icon-noise);
    --LOGO_sidebar-opacity:1;
    --LOGO_size:7rem;
    --LOGO_top:0;
    --LOGO_opacity:0.2;
    --LOGO_position:50.2% 0%;
    --LOGO_top_PAD:0.45rem;
    --LOGO_size_PAD:6.5rem;
    --LOGO_position_PAD:50.1% 0%;
    --LOGO_top_MOB:var(--LOGO_top_PAD);
    --LOGO_size_MOB:var(--LOGO_size_PAD);
    --LOGO_position_MOB:var(--LOGO_position_PAD);
   --LOGO_image:none;
    --LOGO_image_PAD:var(--LOGO_image);
   --LOGO_image_MOB:var(--LOGO_image_PAD);


   --LOGO_sidebar:var(--icon-magazine);
   --LOGO_sidebar_MOB:var(--LOGO_sidebar);


/* 图标 || ICON */

--icon-scp:url(https://scp-wiki.wikidot.com/local--files/theme:magazine/LOGO_SCP.svg);
--icon-magazine:url(https://scp-wiki.wikidot.com/local--files/theme:magazine/LOGO_magazine.svg);
--icon-noise:url(https://scp-wiki.wikidot.com/local--files/theme:magazine/IMG_noise.png);


--mzlog-img:url('https://scp-wiki.wikidot.com/local--files/theme:magazine/ICON_eagle.svg');

 /* ----------------  侧边栏 || SIDE BAR ---------------------- */
   --SB_content:'Documents';   
   --barColour: var(--color-grey);
   --SB-hdtxt_color:#600;

   --SB-hdtxt_size:0.95rem;
    --SB-link_size:0.85rem;
    --SB-main_size:1.2rem;

--SB-line11: solid 0.18em var(--SB-hdtxt_cl);
--SB-line22: solid min(0.12em, 0.15rem) var(--SB-line_cl);



  /* 左右空白 + 侧边栏 = 整个侧边栏尺寸 = 页面右侧空白  */
    --SB_width:14rem;
    --SB-blank_right:1rem;
    --SB-blank_left:2rem;
    --SB-blank_top:6rem;
    --content-width:54rem;   
    --SB_width-total:calc(var(--SB_width) + var(--SB-blank_left) + var(--SB-blank_right));  

    /* 背景颜色 */
     --SB-bg_PC:transparent;
     --SB-bg_MOB:transparent;
     --SB-cover_MOB:transparent ; /* var(--icon-noise);*/

    /* 项目颜色 || ITEM COLOR */
    --SB-btn_border_cl:var(--color-darker);
    --SB-btn_txt:var(--color-darker);
    --SB-btn_bg:var(--color-navy);

    --SB-link_txt:var(--color-navy);
    --SB-link_bg:transparent;
    --SB-link_txt_HOV:var(--color-light);
    --SB-link_bg_HOV:var(--accentColor);

/* 栏线颜色 || ITEM LINE */
    --SB-hdtxt_cl:var(--accentColor);
    --SB-line_cl:var(--SB-hdtxt_cl);
    --SB-content_color:var(--SB-hdtxt_cl);
    --SB-hdtxt_cl_MOD:var(--SB-hdtxt_cl);
   --color-hr-sidebar:var(--color-navy);
   --SB-fold_color:var(--color-red);

    /* 栏线 */
    --SB-hdtxt_left: 0.4em;
    --SB-line11_size: min(0.2em, 2.5rem);
    --SB-line22_size: min(0.12em, 1.5rem);   
    --SB-line22_top:var(--SB-line11_size);
    --SB-line22_left: var(--SB-hdtxt_left);
    --SB-line22_right: max(7em, 65%) ;
    --SB-line_height: calc((var(--SB-line11_size) + var(--SB-line22_size)) / 2 + 0.5px );  

    --SB-line_clip: polygon(0 0, 100% 0, 100% var(--SB-line22_top), var(--SB-line22_right) var(--SB-line22_top), var(--SB-line22_right) 100%,  var(--SB-line22_left)  100%, var(--SB-line22_left)  var(--SB-line22_top), 0% var(--SB-line22_top));

/* 栏头 */
    --SB-topbox_size:8rem;
    --SB-topbox_top222:0rem;   
    --SB-phrase_size:2rem;
    --SB-phrase_top:calc(var(--SB-topbox_size) + 0.5rem);

     /* 手机按钮 */
    --SB_btntxt: "\f0c9";
    --SB-btn_square:1.5rem;
    --SB-btn_left:1rem;
    --SB-btn_top:1rem;
    --SB-btntxt_size:1rem;
    --SB-btntxt_size_HOV:var(--SB-btntxt_size);
    --SB-btntxt_cl:var(--color-navy);
    --SB-btn_bgcl:var(--color-navy);
    --SB-btn_border_cl: var(--color-navy);   
    --SB-btntxt_bg_HOV:var(--accentColor);
    --SB-btntxt_cl_HOV:var(--TB_txt); 
    --SB-btn_bgcl:var(--color-grey-dark); 


/* ------------------- 页面标题 || PAGE TITLE ------------------- */
  --PGtt_txt-color:var(--color-darkest);
    --PGtt_size:2.5rem;
    --PGtt_size_PAD:2rem;    
    --PGtt_size_MOB:1.5rem;    

     /*.....底部盒子.....*/
    --PGtt_underbox-gap:0rem;  
    --PGtt_underbox-height:0.5em;

     /*.....底部装饰线.....*/
    --PGtt-line_color:var(--color-darker);
    --PGtt-linebox_space: max(var(--PGtt-line01_height),var(--PGtt-line02_height) );
    --PGtt-linebox_gap: 0.07em;
    --PGtt-line_ornleft:calc(50% - var(--PGtt-orn_width) / 2);
    --PGtt-line_ornright:calc(50% + var(--PGtt-orn_width) / 2);

    --PGtt-line01_height: 0.1em;
    --PGtt-line02_height: 0.06em;
    --PGtt-line02clip_height: var(--PGtt-line01_height);
    --PGtt-line02clip_right: calc(100% - var(--PGtt-line02clip_left));
    --PGtt-line02clip_left: min(10%, var(--PGtt-orn_size) * 10 );

  --PGtt-lines-img: linear-gradient(90deg, var(--PGtt-line_color) , var(--PGtt-line_color));
  --PGtt-line02_clip:polygon(100% 0, 0 0, 0 var(--PGtt-line02clip_height), var(--PGtt-line02clip_left) var(--PGtt-line02clip_height), var(--PGtt-line02clip_left) 100%, var(--PGtt-line_ornleft) 100%, var(--PGtt-line_ornleft) 0, var(--PGtt-line_ornright) 0, var(--PGtt-line_ornright) 100%, var(--PGtt-line02clip_right) 100%, var(--PGtt-line02clip_right) var(--PGtt-line02clip_height), 100% var(--PGtt-line02clip_height));



     /*.....底部图案.....*/
    --PGtt-orn_content:"\f005";
    --PGtt-orn_txt-color:var(--accentColor);   
    --PGtt-orn_size:0.8em;
    --PGtt-orn_width:calc(var(--PGtt-orn_size) * 1.5);    



/* ------------------- 评分栏 || RATE BOX------------------- */

  --RATE_text:'Rating';
    --RATE_text_HOV:var(--RATE_text);   
    /* 颜色 */
    --RATE_font:var(--page-font);
    --RATE_bg:var(--page-bg);
    --RATE-num_bg:var(--RATE-num_border);
    --RATE-num_num:var(--color-light);
    --RATE-num_txt:var(--color-darkest);
    --RATE-num_border:var(--color-darker);

    --RATE-info_btn:var(--color-dark);
    --RATE-info_btn_HOV:var(--color-darker);
    --RATE-info_btn_HOV-txt:var(--color-light);
    --RATE-info_border:var(--color-grey-darkest);

    --RATE-mobox_border:var(--color-navy);
    --RATE-mobox_bg:var(--color-kraft);
    --RATE-mobox_tt:var(--color-gold);
    --RATE-mobox_txt:var(--color-darkest); 
    --RATE-mobox_btn: var(--accentColor) ; 

 /* .....尺寸..... */
    --RATE-num_size:1.2rem;
    --RATE-txt_size:0.8rem;
    --RATE-num_lh:1.2; /* 数字行间距 */
    --RATE-num_padding:0.4em;  
    --RATE_padding-left:0em;
    --RATE-num_height:calc(var(--RATE-num_size) + var(--RATE-num_padding) * 2);

    /*按钮*/
    --RATE-btn_mgleft:0.5rem;
    --RATE-btn_mgleft_half:0.25rem;    
    --RATE-btn_width: 1.5em;
    --flag-delta-size:0.5em;
    --RATE-btn_height:2em; 
    --RATE-btn_size: 1rem;

    --RATE-box_heigh:calc(var(--RATE_padding-top) + var(--RATE-line_width) + var(--RATE-num_size) + var(--RATE-line_gap) + var(--RATE-box_margin-bottom) - 1px);
    --RATE-box_margin-bottom:0.25rem;

      /* 旗子 */
   --RATE-btn_bgcolor:var(--RATE_bg);  
   --RATE-btn_color:var(--accentColor);  
   --RATE_bg_HOV:var(--color-grey-darkest);
   --RATE_txt_HOV:var(--color-red);
   --RATE_boder-flag: rgba(33,33,33,0.4);
   --RATE-flag_gap:var(--page-bg);  
    --RATE_padding-top:0rem;
    --RATE-flag_top: calc(var(--RATE_padding-top) + var(--RATE-btn_height) );
    --RATE-delta_height:calc(var(--RATE-btn_width) / 4);
    --flag-delta-count: calc(100% - var(--flag-delta-size));

    /* 线条 */ 
    --RATE-line_width:0.1rem; 
    --RATE-line_bottom: var(--RATE-line_width);
    --RATE-line_gap: var(--RATE-line_width);
    --RATE-line_gapcount:calc(var(--RATE-line_width) * 2 + var(--RATE-line_gap));

   /* ---------  引用块  --------- */

    --QUOTE_bg:rgba(236,235,220, 0.6);
    --QUOTE_bg222:rgba(220,220,230, 0.2);
    --QUOTE_border:rgba(33,33,33,0.4);
    --QUOTE_border-left:rgba(33,33,33,0.7);

  /* --------- 分割线 --------- */

      --color-hr:rgba(128,128,128,0.8);

  /* --------- 目录 / TOC MENU--------- */

    --TOC-bd_cl:var(--color-navy);
    --TOC-tt_cl:var(--color-navy);
    --TOC-tt_bg:var(--page-bg);

       --TOC-txt_size:1rem;
       --TOC-line_height:1.6rem;

       --TOC_text-tt:'MENU';
       --TOC_text-fold:'Fold';
       --TOC_text-unfold:'Unfold';

  /* --------- 特殊 div 样式 --------- */

    /* 泛黄块 */
    --mz-paper-bg:rgba(236,235,220, 0.6);
    --mz-paper-bg222:#e9e9e2;

    /* 便利贴 */
    --tip-bdcl:155,155,155;
    --tip-color:var(--color-grey-darker);
    --tip-deg:-45deg;
    --tip-dogear-size:1.5em;
    --tip-dogearoll-size:calc( var(--tip-dogear-size) * 1.4); 
    --tip-maxwidth_PC:calc(var(--content-width, 56rem) * 0.5);
    --tip-dogearoll-margin: 0rem -0.9rem 0 0;

   /* --------- 维基图片框 || IMAGE BOX--------- */

    --mz-img-bg:var(--page-bg);
    --shadow-gap:var(--mz-img-bg);
    --shadow-line:var(--color-navy);

    --box-line:drop-shadow(0px -1px 0.5px var(--shadow-line)) drop-shadow(0px 0px 0px var(--shadow-line))
        drop-shadow(0px 0px 0.5px var(--shadow-line)) drop-shadow(0px 0px 0px var(--RATE_boder-flag))
        drop-shadow(0px 0px 0.5px var(--shadow-line)) drop-shadow(0px 0px 0px var(--RATE_boder-flag))
        drop-shadow(0px 0px 0.1px var(--shadow-gap)) drop-shadow(0px 0px 0.1px var(--shadow-line))
        drop-shadow(0px 0px 0.1px var(--shadow-gap)) drop-shadow(0px 0px 0.1px var(--shadow-gap))
        drop-shadow(0px 0px 0.5px var(--shadow-line)) drop-shadow(0px 0px 0px var(--shadow-line))
        drop-shadow(0px 0px 0.5px var(--shadow-line)) drop-shadow(0px 0px 0px var(--shadow-line))
        drop-shadow(0px 0px 0.5px var(--shadow-line)) drop-shadow(0px 0px 0px var(--shadow-line));

 /* ----------- TAB 样式 ----------- */

    --tabNAV-txt:var(--color-navy);
    --tabNAV-txt_HOV:var(--color-light);
    --tabMain-txt:var(--color-darker);

    --tabNAV-bg_HOV:var(--accentColor);
    --tabNAV-bg_SEL:var(--tabNAV-bg_HOV);

    --tabNAV-txt_SEL:var(--tabNAV-txt_HOV);
    --tabNAV-bg_ACT:var(--tabNAV-bg_HOV);
    --tabNAV-txt_ACT:var(--tabNAV-txt_HOV);

    --tabNAV-border:var(--color-darker);   
   --tabMain-border:var(--accentColor);
   --tabNAV-bg:var(--color-grey-darker);
   --tabMain-bg:var(--color-grey-dark);
    --tab-shadow:var(--color-grey-darkest);

    /* 深线样式 */
    --Line-tabNAV-txt:var(--color-navy);
    --Line-tabNAV-txt_HOV:var(--color-light);

   --Line-tabNAV-txt_SEL:var(--Line-tabNAV-txt_HOV);
   --Line-tabNAV-txt_ACT:var(--Line-tabNAV-txt_HOV);

   --Line-tab-base:var(--color-navy);  
    --Line-tabNAV-bg_HOV:var(--Line-tab-base);
    --Line-tabNAV-bg_SEL:var(--Line-tabNAV-bg_HOV);
    --Line-tabNAV-bg_ACT:var(--Line-tabNAV-bg_HOV);

    --Line-tabNAV-border:var(--Line-tab-base);
    --Line-tabMain-border:var(--Line-tab-base);

   --Line-tabNAV-bg:var(--page-bg);
   --Line-tabMain-bg:var(--Line-tabNAV-bg);
   --Line-tab-shadow:transparent;
   --Line-tabMain-txt:var(--Line-tab-base);

   --Line-tabBD-weight:3px;

    /* ------------- 文本标题框 / TITLE BOX-------------  */
    --ttbox_padding:0.5rem;
    --ttbox_margin:0.5rem;

    --ttbox_h1BDR-color:var(--color-navy);
    --ttbox_boxBDR-color:var(--ttbox_h1BDR-color);
    --ttbox_bevel-color:var(--ttbox_h1BDR-color);

    --ttbox_h1-color:var(--color-navy);

    --ttbox_h1-bg:var(--page-bg);
    --ttbox_box-bg:var(--page-bg);

    --ttbox_box-size:0.2rem;
    --ttbox_border-size:0.25rem;

    --ttbox_h1-size:1.2rem;
    --ttbox_h1-top:calc(0rem - var(--ttbox_h1-size) * 2 );
    --ttbox_h1-left:calc(0rem - var(--ttbox_padding) * 4);
    --mztt-h1pd-horiz:1rem;

     --ttbox_bevel-size:2rem;
     --ttbox_bevel-left:calc(0rem - var(--mztt-h1pd-horiz) + var(--ttbox_padding));
     --ttbox_bevel-top:calc(0rem - var(--ttbox_padding) + var(--ttbox_h1-size));
     --ttbox_bevel-bg:linear-gradient(-135deg, var(--ttbox_bevel-color), var(--ttbox_bevel-color) 50%, transparent 50%, transparent 100%);

 /*-------------- 操作区域 ----------------*/ 

  /*......... 历史区 .........*/
    --HISTORY-btn_size：1rem;
    --HISTORY-form_columns:repeat(8, auto);
    --HISTORY-form_td_width:100%;
    --HISTORY-form_td_margin:0.2rem 0;
    --HISTORY-perpage_width: 8.5rem;

  /*......... 编辑区 .........*/
   --EDIT-table_width:95%;

    --EDIT-ui_size-11: 1rem;
    --EDIT-ui_size-22: 0.8em;
    --EDIT-ui_size-33: 0.8em;
    --EDIT-ui_size-sub: 0.7em;
    --EDIT-ui_size-99:1em;
    --EDIT-ui_height: 2em;
    --EDIT-ui_weight: var(--EDIT-ui_height);
    --EDIT-ui_mono-font: 'Nanum Gothic Coding';
    --EDIT-ui_color:var(--color-navy);
    --EDIT-ui_color_HOV:var(--EDIT-ui_color);
    --EDIT-ui_bg:transparent;
    --EDIT-ui_bg_HOV:var(--color-grey-dark);
    --EDIT-ui-sub_HOV:var(--color-grey-darkest);

    --EDIT-ui-btn_margin:0 0.5em 0 0;
    --EDIT-ui-panel_margin:0 0 0.5em 0;

    --EDIT-border_color:var(--color-dark);
    --EDIT-border_color_Foc:var(--color-dark);

    --EDIT-box_bg:var( --color-grey);
