/*
=====================================
页面全局 / GLOBLE
=====================================
*/

body {
    font-family:var(--page-font), sans-serif;
    background-color: var(--page-bg);
    background-image:none;
}


#page-content p{
/*    font-size: 1.1em; */
}

tt {
    font-family:var(--mono-font);
    font-size: 98%;
}

::selection {
     color: white;
    background:var(--color-blue);
}

::-moz-selection {
     color: white;
    background:var(--color-blue);
}

a {
    color: var(--color-red);
}

a:visited {
    color:var(--color-darkred);
}

a.newpage {
    color: var(--color-kraft);
}

#page-content a {
    border: none;
}

#page-content .printuser img.small {
    padding-right: 0.25rem;
}

/* ----------------- 字体及字重 || FONT -------------------- */

#action-area h1,
div#extra-div-1:before, div#extra-div-1:after, 
#page-title, .meta-title, #page-content :is(h1, h2, h3, h4, h5,h6) {
        font-weight:600;
        font-family:var(--page-tt-font), serif;
}

#header :is(h1,h1 a,h1 a::before, h2,  h2::before) {
    font-family:var(--header-tt-font), serif;
    font-weight:var(--header-tt-font-weight, 600);
}

#header h2::before {
    font-weight:var(--HD-h2_font-weight,600);
}

@media (max-width: 600px) {
div#extra-div-1:before, div#extra-div-1:after, 
#page-title, .meta-title, #page-content :is(h1, h2, h3, h4, h5,h6) {
       font-family:var(--mob-tt-font);
       font-weight: var(--mob-hdtt-h1-font-weight,600);
 letter-spacing: 1.5px;
  }
 #container {
    /*  overflow: hidden; */
  }


}


/*
=====================================
版头
=====================================
*/


#container-wrap-wrap #container-wrap {
    background-color: none;
    background-image: none;
}

div#header {   

    background-image: none;
    width: 100%;
   max-width: 100%;
    height: var(--HD_height_PC);
    background-color:var(--HD_bg);
    background-image: var(--HD_cover);
    background-size:18rem;
    background-position: 0% 0%;
    background-repeat: repeat;

  display: grid;
  justify-content: space-between;
  grid-template-columns: var(--HD_leftright,1.2rem) minmax(300px, 100%) min-content var(--HD_leftright,1.2rem);
  grid-template-rows:0  var(--HDtt_top) minmax(var(--HDtt_size), 100%) minmax(var(--HD-h2_size), 100%) auto;
      grid-template-areas:
    '.  upper  upper    . ' 
    '.      leftop rightop .      '
    'hdleft bigtt  bigtt   hdright' 
    'hdleft subtt  subtt   hdright' 
    'topbar topbar topbar  topbar ';

}

#header h1, #header h2, 
#top-bar,#login-status, #search-top-box{
    position: initial;
    display: grid;
    top: 0;
    left:0;
    right:0;
    margin: 0;
    padding: 0;
    height: auto;
    align-content: center;
}

#header h1 a {
  padding:0;
}

#header h1{
    order: 1;
    grid-area: bigtt;
    display: inline-grid;    
    height: var(--HDtt_size);
    line-height: 0
}

#header h2{
    order: 2;
    grid-area: subtt;
}

/* ------------- ----
LOGO 
------------------ */

#header-extra-div-1, #header-extra-div-2, #header-extra-div-3{
     grid-area: upper;
}

#header-extra-div-1{
height: var(--HD_height);
width: 100%;
    display: inline-flex;
    justify-content: center;
   top:var(--LOGO_top);
    position: relative;
    overflow: hidden;
}

#header-extra-div-1:before{
    content: ' ';
    display: inline-flex;
    align-self: center;
    position: relative;
    height: var(--LOGO_size);
width: 100%;
background-size: contain;
background-position: var(--LOGO_position);
background-repeat: no-repeat;
background-image: var(--LOGO_image);
opacity: var(--LOGO_opacity);   
}

#header-extra-div-2{
    order:0
}
#header-extra-div-3{
    order:0
}

/* ---------------- 
版头标题
--------------------- */

#header h1, #header h2 {
    margin-left: 0;
    float: none;
    text-align: center;
    z-index: 1;
    pointer-events: none;
}

#header h1 span, #header h2 span {
    font-size: 0;
    display: none;
}

#header h1 a::before, #header h2::before {
    text-shadow: none;
}

div#header h1 a{
    top: var(--HD-h1_top); 
    position: relative;
    letter-spacing: 0.1rem;
    max-width: max-content;
    margin: 0 auto;
}

div#header h1 a:before{
    font-size:var(--HDtt_size);
    content: var(--header-title);
    color: var(--HDtt_color);
}

#header h2::before {
   content: var(--header-subtitle);
   font-size:var(--HD-h2_size);
   margin-top:var(--HD-h2_top);
   color: var(--HD-h2_color);
}

/* ------------- ----
用户栏 
------------------ */

#login-status{
    grid-area: var(--area-status,rightop);
    margin-top: var(--STATUS_top);
    align-items: center;
    align-self: start;
    grid-gap: 0.5rem 0rem;
    grid-template-columns:var(--STATUS_column-11) var(--STATUS_column-22) var(--STATUS_column-33)  var(--STATUS_column-44,0);
    grid-template-rows:  var(--STATUS_row-11) var(--STATUS_row-22) var(--STATUS_row-33, auto);
    grid-template-areas:
        'left botton   Account  right'
        'left messages username right';
    width: max-content;
    word-break:  keep-all;
    color: transparent;
    font-size: 0;
    max-width: var(--HD-menu_maxwidth);
    min-width: var(--HD-menu_minwidth);
    display: inline-grid;
    height: var(--STATUS_height);
    justify-self: var(--justify-status);
    position: relative;
    left:var(--STATUS_left, 0);
}

/*------- 账户与用户名  ----------*/

#login-status .printuser, #login-status #my-account , #account-topbutton{
     font-family: var(--header-tt-font);
      color: var(--USER_line);
      font-size: var(--USER_font-size);
    margin-left: var(--STATUS_grid-gap);
}

#login-status #my-account{
    grid-area: Account;
    text-decoration: none;
    order:0;
    font-family: var(--font_my-account);
    pointer-events: none;
    width: auto;
    display: var(--display-status-txt,inline-grid);
    align-self: start;
}

#login-status .printuser{
    font-family: var(--font_printuser);
    grid-area: var(--area-username,username);
    white-space: nowrap;
    overflow: hidden ;
    text-overflow: '.';
    letter-spacing: 0.1rem;
    background-image:linear-gradient(40deg, transparent 0%, transparent 12%, var(--USER_line) 14%, var(--USER_line) 15%, var(--USER_line) 60%, transparent 90%, transparent 100%);
    background-size: 110% 0.15rem;
    background-repeat: no-repeat;
    background-position:-0.25em 70%;   
     width: 90%;
     max-width: min-content;
    cursor: default;
    padding: var(--STATUS_padding); 
    display: var(--display-topbox-notbtn);
    background-blend-mode:  color-burn
}


#login-status:hover .printuser{
    animation:  user-script 1.5s;
}

#login-status:has(:is(#account-topbutton, a[href*="messages"]):hover) .printuser{
    animation:  none;
}

#login-status .printuser:hover{
       color:var(--HD-h2_color) ;
    -webkit-text-stroke: 0.5px var(--HD_mob_HOV);
}

@keyframes user-script{
    0% {width:10%; background-size: 0% 0.15rem;text-overflow: '';}
    80% {width:90%; background-size: 0% 0.15rem;text-overflow: '';}
    100%  {width:90%; background-size: 0% 0.15rem;}
}

#login-status .printuser a{
    width: 100%;
    position: relative;
    display:  inline-block;
    margin-right: -100%;
    height: 100%;
    opacity: 0;
}

#login-status .printuser img.small {
 height:var(--USER_font-size);
}

/*------- 菜单 ----------*/
#account-topbutton{
        grid-area: botton;
    font-size: 0rem;
    border: none;
    margin: 0
}

#account-topbutton:hover:before {
    color: var(--HD_bg);
 background-color:  var(--USER_line);
}

#login-status #account-options{
    font-size:  var(--STATUS-btn_size);
    position: relative;
    background-color: var(--HD_bg);
    border: 2px solid var(--USER_line);
    grid-area: var(--area-account-options,left);
    justify-self:  var(--justify-account-options,end);
    align-self: start;
    overflow:hidden;
}

#account-options:hover, #login-status #account-options[style*="block"]{
    display: inline-grid;
}

#account-options ul {
margin:-0.1rem;   
width:105%;
height:105%;
}

#login-status #account-options a {
    color: var(--USER-list_txt);
    background-color: var(--HD_bg);
    border: 1px solid var(--USER_line);
    text-decoration: none;
}

#login-status  #account-options a:hover {
    background-color: var(--USER_line);
    color: var(--USER-list_txt_HOV);
    border: 1px solid var(--USER_unline);
}


/*------------- 按钮 --------------------*/
#login-status:before,
#login-status:after,
#account-topbutton,
#login-status :not(div) + a[href*="messages"]{
    display: inline-grid;
    color:var(--USER_unline);
    font-size: 0rem;
    margin: 0;
    padding: 0;    
    justify-items: left;    
    margin-left: var(--STATUS_padding);
        min-height: var(--HD-menu_height);
}

#account-topbutton:after,
#login-status :not(div) + a[href*="messages"]:after{
    width: var(--STATUS-btn_grid);
    height:var(--STATUS-btn_grid);
    position: absolute;
}

/* 按钮样式 */
#login-status a:is(.login-status-create-account, .login-status-sign-in):before,
#login-status:before,
#login-status:after,
#search-top-box-form:before,
#account-topbutton:before {
    display: inline-grid;
    justify-content: center;
    color: var(--USER_line);
    content: '\f007';
    font-family: FontAwesome;
    font-size: var(--STATUS-btn_size);
    border:  var(--USER_line) solid 2px;
    width: var(--STATUS-btn_btn);
    min-height: var(--STATUS-btn_btn);
    margin-right: var(--STATUS-btn_padding);
    content: '\f007';
    border-radius:40%;
    padding:1px;
    align-content: center;
}

/* 用户名旁的装饰 */
#login-status:before,
#login-status:after{
   content:"\f05a";
    grid-area: messages;
    border:none;
    font-size: var(--STATUS-btn_btn);
    justify-content: end;
    display:none;
}

#login-status:has(:not(div) + a[href*="messages"]):after{
    display:none;
}


#login-status:before{
    grid-area: botton;
}

#login-status:has(.printuser):after{
    display: var(--display-status-txt, inherit);
}


/*------------- 私信 --------------------*/
#login-status :not(div) + a[href*="messages"]{
     grid-area: var(--area-messages,messages);
    display: inline-grid;
     align-self:  start;
    align-content:  center;
        height: 0;
}

#login-status  :not(div) + a[href*="messages"]:before{
    content: '\f0e0 ';
    font-size: var(--STATUS_grid);
    font-family: 'FontAwesome';
    color:var(--USER_line);

    -webkit-animation-name: user-messages;
    -webkit-animation-iteration-count: infinite;
    -webkit-animation-timing-function: cubic-bezier(1.0, 0, 0, 1.0);
    -webkit-animation-duration: 1s;
    animation-name: user-messages;
    animation-iteration-count: infinite;
    animation-timing-function: cubic-bezier(1.0, 0, 0, 1.0);
    animation-duration: 1s;
}

#login-status  :not(div) + a[href*="messages"]:hover:before{
   color:var(--HD_mob_HOV);
    -webkit-animation-play-state:paused;
    animation-play-state:paused;
}

#login-status > a > strong {
    text-decoration: none ;
}

@-webkit-keyframes user-messages {
    from { content: '\f0e0 '; }
    to { content: '\f003'; }
}

@keyframes user-messages {
    from { content: '\f0e0 '; }
    to { content: '\f003'; }
}


/* ----------------- 未登录 ------------------- */
#login-status {
    column-gap: var(--STATUS_gridbox-gap,0);
}

#login-status a:is(.login-status-create-account, .login-status-sign-in){
   font-size:var(--unlogin-font-size);
   grid-area: var(--area-create-account, messages);
   font-family: var(--header-tt-font), var(--page-tt-font), serif;
   font-weight:600;
   color: var(--USER_line);
   display:inline-grid;
   white-space:nowrap;
   grid-template-columns: var(--STATUS-btn_grid) auto;
   grid-gap: var(--STATUS_unlogin-gap,var(--STATUS_padding));
   justify-content: start;
   align-self: start;
   text-decoration: none;
}

#login-status a.login-status-sign-in{
   grid-area: var(--area-sign-in,botton);
}

#login-status a:is(.login-status-create-account, .login-status-sign-in):before{
   content:"\f090";
   font-family:fontawesome;
   font-size:var(--STATUS-btn_size);
   width: var(--STATUS-btn_btn);
   height:var(--STATUS-btn_btn);
   border-radius: 5px;
   align-content: center;
   margin-left: var(--STATUS_padding);
   align-self: center;
}

#login-status a.login-status-create-account:before{
  content:"\f234"; 
}

#search-top-box-form:hover:before,
#login-status :is(.login-status-create-account, .login-status-sign-in):hover:before{
      color: var(--SEARCH_txt_HOV);
    background: var(--SEARCH_bg_HOV);
}

#login-status a:is(.login-status-create-account, .login-status-sign-in)::after {
    content: "";
}

/* ------------- ----
搜索栏 
------------------ */

#header  #search-top-box{
    grid-area: var(--area-search, rightop);
    justify-content: var(--justify-search,stretch);
    align-content:   start;
    width: 100%;
    min-width: var(--HD-menu_minwidth);
    margin-top:var(--SEARCH_top);
    position: relative;
    left: var(--HD-menu_left);
    height: var(--STATUS_grid);
}

#search-top-box-form {
   position: relative;
   display: grid;
   width:  initial;
   grid-template-columns: var(--STATUS-btn_grid) auto;
   grid-gap: var(--STATUS_grid-gap); 
}

#search-top-box-form:before {
      content: '\f002 ';
      position: absolute;
      z-index: -1;
      border-radius: 5px;
      align-self: center;
      min-height: var(--STATUS-btn_btn);
      margin-left: var(--STATUS_padding);
}

#search-top-box input.empty,
#search-top-box-input {
    order: 2;
    border: solid 2px var(--SEARCH_line);
    background-color: transparent;
    box-shadow: none;
    color: var(--SEARCH_txt);
   width: 100%;
   margin-left:0.25rem;
        display: var(--display-topbox-notbtn);
    border-radius: 5px;
}

#search-top-box-form #search-top-box-input:hover,
#search-top-box-form  #search-top-box-input:focus {
    border: solid 2px var(--SEARCH_line);
    color: var(--SEARCH_txt_HOV);
    background-color:var(--SEARCH_bg_HOV);
    box-shadow: none;
       outline: none;
}

#search-top-box-form input[type="submit"]:focus,
#search-top-box-form input[type="submit"]:hover {
    color: transparent;
    background: transparent;
    border: none;
    box-shadow: none;
}

#search-top-box-form input[type="submit"] {
    color: var(--SEARCH_txt);
    background: transparent;
    box-shadow: none;
    border: none;
    font-size: 0;
    width: var(--USER_font-size);
    height: var(--STATUS_grid);
   margin-left: var(--STATUS_padding);
}

/*========================== 
顶栏 
==============================*/
#header #top-bar{
    grid-area: topbar; 
    height: var(--TB_height); 
