/*========================== 
版头窄屏 
==============================*/

@media (max-width: 1100px) {
  div#header{
    --area-status:leftop;
    --area-username:Account;
    --area-account-options:username;
     --area-sign-in:botton;
     --area-create-account:Account;
    --STATUS_top:0.5rem;
   --STATUS_unlogin-gap:var(--STATUS_grid-gap);
