    --HD-menu_maxwidth:80vw;
    --justify-account-options:start;   
      --justify-search:right;
  }
}

@media (max-width: 767px) {
  div#header{
   --HD_height:var(--HD_height_PAD);  
   --HDtt_size:var(--HDtt_size_PAD);
   --HD-h2_size:var(--HD-h2_size_PAD);
   --header-title:var(--pad-title);
   --header-subtitle:var(--pad-subtitle);
      --HD_leftright:0;
    --HD-h1_top:var(--HD-h1_top_PAD);
    --HD-h2_top:var(--HD-h2_top_PAD);
     --LOGO_image:var(--LOGO_image_PAD);
    --LOGO_top:var(--LOGO_top_PAD);
    --LOGO_size:var(--LOGO_size_PAD);
    --LOGO_position:var(--LOGO_position_PAD);
   --HDtt_gap:var(--HDtt_gap_MOB);
   --display-topbox-notbtn:none;
   --HD-menu_minwidth:var(--STATUS_grid);
    --HD-menu_maxwidth:var(--STATUS_grid);
   --area-status:upper;
   --area-search:upper;
      --STATUS_row-22:auto;
       --STATUS_row-33:0;
  --STATUS_column-11:var(--HD-menu_minwidth);
      --STATUS_column-22:var(--HD-menu_minwidth);
      --STATUS_column-33:var(--HD-menu_minwidth);
      --STATUS_column-44:0;
      --HD-menu_left:-0.5rem;
      --STATUS_gridbox-gap:calc(var(--STATUS_padding)  + var(--HD-menu_left) - var(--HD-menu_left) * 1.5);
