    -webkit-filter: drop-shadow(0px 0px 2px rgba(var(--tip-bdcl),0.5));
    filter: drop-shadow(0px 0px 2px rgba(var(--tip-bdcl),0.5));
}

.hovertip  .content:after,
.mz-note::after{
    content: "  ";
    display: block;
    width: var(--tip-dogearoll-size);
    height: var(--tip-dogearoll-size);
    background-size:contain;
    background-image:linear-gradient(var(--tip-deg),transparent 0%, transparent 50% , var(--tip-color) 50%, var(--tip-color) 100%);    
    float: right;
    margin: 0 -0.5rem -0.5rem 0;
    -webkit-filter: drop-shadow(-2px -1px 4px rgba(var(--tip-bdcl),0.6))  drop-shadow(2px -1px 1px rgba(var(--tip-bdcl),0.2)) grayscale(50%);
    filter: drop-shadow(-2px -1px 4px rgba(var(--tip-bdcl),0.6))  drop-shadow(2px -1px 1px rgba(var(--tip-bdcl),0.2)) grayscale(50%);
    position: absolute;
    bottom: 0;
    right: 0;
    margin: 0;
}

.mz-note.note-left{
    --tip-deg:45deg;
}

