/*=====================================
    Magazine Theme
----------------------------------
    Extensions Source
=====================================*/


@import url(https://scp-wiki.wikidot.com/local--files/theme:magazine/IMG-source_base64.css);

:root{
--img-chew444: var(--MASK-chew222);
}

/* ==================
笔记本组件
====================*/

:root{
 --color-notepage: var(--color-light);
 --NOTEBOOK-bookmark_color:var(--color-dark);
  --color-header-footer:var(--color-notepage);

 --cl-a1:235,235,225,1;
  --cl-a2:222,210,220,1;
  --cl-a3:210,200,195,1;
  --cl-a4:25,25,25,1;
   --page-color:#f3f3f3;

/* 书本厚度描框 */
--box-bk-0:0.0625rem 0.0625rem 0 rgba(var(--cl-a1)), 0.125rem 0.125rem 0 rgba(var(--cl-a2)), 0.1875rem 0.1875rem 0 rgba(var(--cl-a2)), 0.25rem 0.25rem 0 rgba(var(--cl-a3)), 0.3125rem 0.3125rem 0 rgba(var(--cl-a2)), 0.375rem 0.375rem 0 rgba(var(--cl-a2)), 0.4375rem 0.4375rem 0 rgba(var(--cl-a2)), 0.8rem 0.8rem 0 rgba(var(--cl-a4));
/* 书籍拱起描边 */ 
  --NOTE-book-arch:drop-shadow(0px -1px 0px rgb(var(--cl-a4))) drop-shadow(0px -1px 0px rgb(var(--cl-a4))) drop-shadow(0px -1px 0px rgb(var(--cl-a4))) drop-shadow(0px -1px 0px rgb(var(--cl-a4))) drop-shadow(0px -1px 0px rgb(var(--cl-a4)));

/**/    

  --NOTEPAGE-txt_size: 1.1rem; 
  --NOTEPAGE-p_padding-top:0em;  

    --NOTEPAGE-line_size: 1.5em;

    --NOTEPAGE-line-height: 1.5em; 
    --NOTEPAGE-line-height_half: calc(var(--NOTEPAGE-line-height) / 2);
    --NOTEPAGE-line-height_half-OPS: calc( 0em -  var(--NOTEPAGE-line-height_half));
--color-rowline: #c0c0c0;



    --NOTEPAGE-side_width: 1rem;
   --NOTEPAGE-side_height: 100%; 
    --NOTEPAGE-txt_border-size: 0;

    --MASK-chew_repeat: repeat;
    --MASK-chew_size:100%;

    /* 小 */
    --NOTE-quote_bg:#e9e8e0;
    --NOTE-quote_line: var(--NOTE-quote_bg);
    --NOTE-quote_width:2em;
    --NOTE-quote_shadow: drop-shadow(1px 1px 2px rgba(11,11,11,0.5)); 

    --NOTE-quote_bg-size:  calc(var(--NOTEPAGE-line_size) * 1.4) calc(var(--NOTEPAGE-line_size) * 1.4);
     --NOTE-quote_height:100%;
     --NOTE-quote-side_position: 0 0.5em;
    --NOTE-quote-side_clip: inset(0 0 0 25%);
     --NOTE-quote_padding_lr: 0.5em; 
     --NOTE-quote_padding: var(--NOTEPAGE-line-height_half) var( --NOTE-quote_padding_lr); 

    /* 书缝 */
        --NOTE-bookseam_bg: 55,55,55;
     --NOTE-bookseam_width: 2rem;
    --NOTE-bookseam_width-half: -1rem;

}

@media (max-width: 550px) {    
   .notebook-body, .notepage-body{
     --NOTEPAGE-txt_size: 0.9rem; 
     --NOTE-bookmark_display:none;
     --NOTE-bookseam_display:none;
    }  

    .mz-notebook-wrap {        
    --NOTEBOOK-grid:'left' 'right';
    --NOTEBOOK-grid-columns:auto;
    --NOTEBOOK-mid_display:none;
    --NOTEBOOK-bookmark_display:none;    
    --box-bk-0:none;
     --NOTEPAGE-side_width: 0.6rem;
     --NOTEBOOK_padding:0 0 1em 0;
        --NOTEBOOK_border:3px solid #bbb;
    }

    .notebook-right{
        --NOTEBOOK-bookmark_display:none;
        margin-top: calc( 0em - var(--NOTEPAGE-line-height) / 2);
    }

}

/*---------------------------
整本
--------------------------*/

.notebook-body{   
    display:grid;
     grid-template-areas:  var(--NOTEBOOK-grid,'left mid right');
    grid-template-columns: var(--NOTEBOOK-grid-columns, 50% auto 50%);

    -moz-box-shadow:  var(--box-bk-0);
    -webkit-box-shadow: var(--box-bk-0);
    -o-box-shadow: var(--box-bk-0);
    -ms-box-shadow: var(--box-bk-0);
    box-shadow: var(--box-bk-0);
    background-color: #f3f3f3;
    background-color: var(--page-color);
    border:var(--NOTEBOOK_border, 1px solid #cccccc); 
    background-color: var(--color-notepage);
    padding: var(--NOTEBOOK_padding, 0);
}


.notebook-left{
    grid-area: left;
}

.notebook-mid{
    grid-area: mid;
    width: 0.15em;
    height: 100%;
    background-color: var(--color-dark);
