/* 方向 */
.talking-box.talkbox-righttop:before,
.talking-box.talkbox-lefttop:before, 
.talking-box.talkbox-righttop:after,
.talking-box.talkbox-lefttop:after {
     align-self: start;

    right: var(--tk-delta-horiz);
    top:var(--tk-delta-verti);
    bottom:unset;

     -moz-transform:scaleY(-1);
    -webkit-transform:scaleY(-1);
    -o-transform:scaleY(-1);
    transform:scaleY(-1);
}

.talking-box.talkbox-righttop:before,
.talking-box.talkbox-righttop:after{
   -moz-transform:scale(-1,-1);
    -webkit-transform:scale(-1,-1);
    -o-transform:scale(-1,-1);
     transform:scale(-1,-1);

    left:unset;
    right: var(--tk-delta-horiz);
    top:var(--tk-delta-verti);
    bottom:unset;
}

.talkbox-inby{
     align-self: start;
}

.talkbox-inby::before,
.talkbox-inby::after {
       -moz-transform:scaleY(-1);
    -webkit-transform:scaleY(-1);
    -o-transform:scaleY(-1);
    transform:scaleY(-1);

    left: var(--tk-delta-verti);
    bottom:0;
}

.talkbox-inby.talkbox-lefttop::before,
.talkbox-inby.talkbox-lefttop::after{
     transform:scaleY(1);
    left:var(--tk-delta-verti);
    right: unset;
    top:0;
    bottom:unset;
}

.talking-right .talkbox-inby::before, 
.talking-right .talkbox-inby::after{
    -moz-transform:scale(-1);
    -webkit-transform:scale(-1);
    -o-transform:scale(-1);
   transform:scale(-1);
    left:unset;
    right:var(--tk-delta-verti);
    top:unset;
    bottom:0;
}

.talkbox-inby.talkbox-righttop::before,
.talkbox-inby.talkbox-righttop::after {
   -moz-transform:scale(-1,1);
    -webkit-transform:scale(-1,1);
    -o-transform:scale(-1,1);
        transform:scale(-1,1);
    left:unset;
    right:var(--tk-delta-verti);
    top:0;
    bottom:unset;
}

/* 内折 */
