[[module CSS show="true"]]
#page-content .collapsible-block {
    position: relative;
    padding: 0.5em;
    margin: 0.5em;
    box-shadow: 2px 1.5px 1px rgba(176,16,0,0.7), 0 0 0px 1px lightgrey;
    overflow-wrap: break-word;
}

.collapsible-block-unfolded{
    color: black;
    overflow-wrap: break-word;

}

.collapsible-block-unfolded-link {
    text-align:center;
}

.collapsible-block-folded {
    text-align: center;
    color: dimgrey;
}

.collapsible-block-link {
    font-weight: bold;
    color: dimgrey;
    text-align: center;
}

.addendumbox {
    padding: .01em 16px;
    margin-bottom: 16px;
    margin-top: 16px;
    padding-bottom: 1em;
    box-shadow:0 2px 5px 0 rgba(0,0,0,0.16),0 2px 10px 0 rgba(0,0,0,0.12);
}

.material-box {
    padding: .01em 16px;
    margin-bottom: 16px;
    margin-top: 16px;
    padding-bottom: 1em;
    border: 1px lightgrey solid;
    box-shadow: 1px 2px 2px 0 rgba(0,0,0,0.16);
}

.material-box blockquote {
    border: 1px double #999;
}

.wiki-content-table {
    width: 100%;
}

.addendumbox blockquote {
    border: 1px double #999;
}

.addendumtitle {
   opacity: 0.8;
   margin-bottom: 10px;
   color: #b01;
}

.maintitle {
   margin-bottom: 10px;
   color: black;
}

.scp-header {
    text-align: center;
    font-size:x-large;
    color:#b01;
}

.addenda-header {
    width: 100%;
    border-bottom: 2px black solid;
    color: black;
}

.scp-info {
    display:flex;
    justify-content:space-between;
    font-size:large;
}

.scp-info-box {
    display:flex;
    justify-content:space-between;
}

.object-info {
    color:black;
    align-self: flex-end;
    font-size: large;
}

.title-style {
    opacity: 0.8;
    margin-bottom: 10px;
    color: #b01;
    font-size: large;
    text-decoration: underline;
    font-weight: bold;
}

.update-div-empty {
    text-align: right;
    font-size: x-small;
    color: lightgrey;
}

.update-div {
    text-align: right;
    font-size: x-small;
}

.computed {
    border: 1px black solid;
    width: 50%;
    display: inline-block;
text-align: left;
    padding: 3px;
}
.computed:before {
    content:"加工済コード";
    font-weight: bold;
border-bottom: solid 1px black;
width: 100%;
}
.rawcode {
    border: black solid 1px;
    width: 50%;
    display: inline-block;
text-align: left;
    padding: 3px;
}
.rawcode:before{
    content:"未加工コード";
    text-align: center;
    font-weight: bold;
border-bottom: solid 1px black;
width: 100%;
}
.codebox {
    display: inline-block;
    width: 100%;
    text-align: center;
}
.yui-navset .yui-nav .selected a em,  .yui-navset .yui-nav a em{
        padding: 0.25em .75em;
        top: 0px;
        margin-bottom: 0px;
}
.yui-navset .yui-nav .selected a {
     background: gray;
}
.yui-navset .yui-nav .selected {
       margin: 0px;
}
.yui-navset .yui-nav .selected a, .yui-navset .yui-nav .selected a:focus, .yui-navset .yui-nav .selected a:hover, .yui-navset .yui-nav .selected a {
         background: gray;
}
.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus {
    background: gainsboro;
    text-decoration: none;
}
.yui-navset .yui-nav a, .yui-navset .yui-navset-top .yui-nav a {
background-color: none;
background-image: none;
}
.yui-navset .yui-nav a {
background: none;
}
.yui-navset .yui-nav li{
margin: 0px;
}

#page-content .licensebox .collapsible-block {
    position: unset;
    padding: unset;
    margin: unset;
    box-shadow: unset;
}

.licensebox .collapsible-block-unfolded{
    color: inherit;
}

.licensebox .collapsible-block-unfolded-link {
    text-align: left;
}

.licensebox .collapsible-block-folded {
    text-align: left;
    color: inherit;
}

.licensebox .collapsible-block-link {
    color: inherit;
    text-align: left;
}
[[/module]]
[[/div]]
