[[code type="css"]]
/*
    SCP Sigma 9: Manhattan Crisis Theme
    [2020 Wikidot Theme]
    Created by Mishary
    Edited by ukwhatn
    SCP Sigma 9 was Created as for the SCP Foundation by Aelanna
    The font Gentium Basic is under Open Font License
    CC BY-SA 3.0
*/
@import url('https://fonts.googleapis.com/css2?family=Gentium+Basic:wght@700&display=swap');

div#container-wrap {
    background: url(https://scp-wiki.wdfiles.com/local--files/theme%3Amanhattan-crisis/dawn.png) top left repeat-x;
}
div#header {
    background: url(https://scp-sandbox-3.wdfiles.com/local--files/theme%3Amanhattan-crisis/manhattan_silhouette.svg)  no-repeat;
}
div#header h1 a span {
    font-size: 0px;
}
div#header h1 a:before {
    font-family: 'Gentium Basic', serif;
    content: "Manhattan Crisis";
    text-shadow: 2px 2px 2px rgba(0, 0, 0, .4);
}
div#header h2 span {
    font-size:0px;
    padding: 4px;
}
div#header h2:before {
    font-family: 'Gentium Basic', serif;
    font-size: 16px;
    content: "        9.11 2001";
    color: #eee;
    padding: 16px 0;
    text-shadow: 1px 1px 1px rgba(0, 0, 0, 0.4);
    white-space: pre;
}

#account-options {
    background: rgba(255,255,255,0.2);
    background-clip: padding-box;
    backdrop-filter: blur(16px);
    -webkit-backdrop-filter: blur(16px);
    border: solid 1px transparent;
    border-radius: 15px 0;
    box-shadow: 10px 10px 10px rgb(48 54 60 / 3%);
}
#login-status ul a{
    color: #fff;
}
#account-options li a:hover {
    color: #d00;
    text-decoration: none;
    background: #fff;
    border-radius: 15px 0;
}

/* TOPBAR */

#top-bar {
    top: 141px;
    height: 20px;
    line-height: 20px;
}
#top-bar ul li.sfhover a, #top-bar ul li:hover a {
    background: #fff;
}
#top-bar li:hover ul, #top-bar li.sfhover ul {
    background: rgba(255,255,255,0.2);
    background-clip: padding-box;
    backdrop-filter: blur(16px);
    -webkit-backdrop-filter: blur(16px);
    border: solid 1px transparent;
    box-shadow: 10px 10px 10px rgb(48 54 60 / 3%);
}
#top-bar ul li.sfhover ul li a, #top-bar ul li:hover ul li a {
    background: transparent;
    border-top: transparent;
    box-shadow: 10px 10px 10px rgb(48 54 60 / 3%);
}

@media (max-width: 580px) {
  div#header h1,
  div#header h2 {
    margin-left: unset;
    text-align: center;
    width: 100%;
  }
  div#header h1 {
    margin: unset;
    padding: unset;
  }
  div#header h1 a {
    margin: 0 auto;
    padding: 80px 0 0;
  }
  div#header h1 a::before {
    width: 100%;
    display: block;
  }
  div#header h2 {
    padding: 1.5em 0 0 ;
  }
  div#header h2:before {
    content: "9.11 2001";
    padding: 0;
  }
}

