[[code type="css"]]
/*
    Marshall, Carter & Dark
    [2014 Wikidot Theme, 2018 Update]
    Originally created for the SCP Foundation GoI Contest of 2014 (MC&D team and Group of Interest) by Randomini. Thanks to Reach, Aelanna and Crayne for the template.
     Updated by stormbreath for mobile compatibility.
*/

/* RECOLOR THE TITLE */
h1, #page-title {
    color: #320042;}

#page-title {
    font-weight: lighter;
    border-color: #320042;
}

/*RECOLOR THE LINKS */
a,
#side-bar a,
#interwiki a,
#side-bar a:visited,
a:visited,
#interwiki a:visited {
    color: #6C4279;
}

a.newpage {
    color: #4312A5;
}

/*HEADER IMAGES*/
div#container-wrap {
    background: url(https://scp-wiki.wdfiles.com/local--files/theme%3Amcd/body_bg_MCD2.png) repeat-x;
}

#header {
    background: url(https://scp-wiki.wdfiles.com/local--files/theme%3Ablack-market/mcdpride.png) no-repeat;
}


/* Adjust Mobile Image Size */
@media (max-width: 479px) { 
#header {
    background-size: 150px 75px;
    background-position: 1em;
}
}
@media (min-width: 480px) and (max-width: 580px) { 
#header {
    background-size: 180px 90px;
    background-position: 1em;
}
}
@media (min-width: 581px) and (max-width: 767px) { 
#header {
    background-size: 210px 105px;
    background-position: 1em;
}
}
@media (min-width: 768px) and (max-width: 979px) {
#header {
    background-size: 240px 120px;
    background-position: 1em;
}
}


/* HIDE THE HEADER TEXT */
#header h1 a {
    font-size: 0%;
}

#header h2 span {
    font-size: 0%;
}

/* RECOLOR THE SEARCH BAR */
#search-top-box-input {
    border-color: #3E2646;
    color: #6E3251;
    background-color: #603B6C;
}

#search-top-box-input:hover,
#search-top-box-input:focus {
    background-color: #282053;
}

#search-top-box-form input[type=submit] {
    border: solid 1px #AF4FCF;
    color: #ccc;
    background-color: #282053;
    background: linear-gradient(to bottom, #371F3D,#282053,#8D6BA9);
}

#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    border: solid 1px #fff;
    background-color: #87a96b;
    background: linear-gradient(to bottom, #7E608A,#946BA9,#8C3CA6);
}

/* LOGIN STUFF */
#login-status {
    color: #C5A3C5;
}
#login-status a {
    color: #E696E6;
}
#login-status ul a {
    color: #C5A3C5;
}
#account-topbutton {
    background: #ccc;
    color: #C5A3C5;
}

/* IMAGE BLOCK RECOLORING */
.scp-image-block {
  border: solid 1px #6c006c;
}
.scp-image-block .scp-image-caption {
  background-color: #f0f8ff;
  border-top: solid 1px #6c006c;
}

/* MODULE RATE RECOLOR */
div.page-rate-widget-box .rate-points {
  background-color: #6c006c;
  border: solid 1px #6c006c;
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
  background-color: #f0f8ff;
  border-top: solid 1px #6c006c;
  border-bottom: solid 1px #6c006c;
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
  background: transparent;
  color: #6c006c;
}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
  background: #6c006c;
  color: #f0ffff;
}
.page-rate-widget-box .cancel {
  background: transparent;
  background-color: #6c006c;
  border: solid 1px #6c006c;
}
.page-rate-widget-box .cancel a {
  color: #f0ffff;
}
.page-rate-widget-box .cancel a:hover {
  background: #cdeadc;
  color: #f0ffff;
}

/* INFO MODULE */
#page-content .rate-box-with-credit-button {
    background-color: #6c006c;
    border-color: #6c006c;
    border-radius: 5px;
    box-shadow: 1px 1px 3px rgba(0,0,0,.5);
}

#page-content .creditButton p a {
    border-left-color: #f0ffff;
}

#page-content .modalbox {
    box-shadow: 0 2px 6px rgba(50,0,50,.5);
}

.close-credits,
.credit-back {
    filter: hue-rotate(275deg);
}


/* AYER'S INFO MODULE */
:root {
     --barColour: #6c006c;
}

/* TAB RECOLORING */
.yui-navset .yui-content{
    background-color: #f5f5f5;
}
.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
    background-color:#d8d8d8;
    background-image: url(../images/yuitabs.png);
}
.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus, /* no focus effect for selected */
.yui-navset .yui-nav .selected a:hover { /* no hover effect for selected */
    background:#320082 url(../images/yuitabs.png) repeat-x left -1400px; /* selected tab background */
    color:#fff;
}
.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus {
    background:#FCDBFF url(../images/yuitabs.png) repeat-x left -1300px;
    text-decoration: none;
}
.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    border-color: #400040;
}
.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    border-color: #6c006c;
}
.yui-navset li {
    line-height: normal;
}

/* SIDE BAR RECOLOR */
#side-bar .side-block,
#interwiki .side-block {
    border: 1px solid #8B198B;
    box-shadow: 0 2px 6px rgba(50,0,50,.5);
    color: #8B198B;
}

#side-bar .heading,
#interwiki .heading {
    color: #1E0087;
    border-bottom: solid 1px #1E0087;
}

#side-bar .side-block.media {
     background-color: #dcbff0;
}

#side-bar .side-block.resources {
     background-color: #fff0ff;
}

#interwiki .side-block {
     background-color: #f4e8ff;
}

#interwiki .menu-item img {
    filter: hue-rotate(275deg);
    -webkit-filter: hue-rotate(275deg);
}

/* Side-bar Collapsible */
#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px #220096;
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    color: #220096;
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    color: #6C4279;
}

/* MOBILE SIDEBAR */
@media (max-width: 767px) {
     #side-bar{
         background-color: #6c006c
      }
}

#top-bar .open-menu a {
    border: 0.2em solid #6c006c;
    background-color: #f0f8ff;
    color: #6c006c;
}


/* FOOTER STUFF */
#footer {
    background: #3d2b1f;
    color: #6C006C;
}

/* TOP BAR RECOLORING*/
#top-bar ul li ul {
    border: solid 1px #3d2b1f;
}

#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
    border-top: 1px solid #CE9CCE;
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: #FFE3FD;
    color: #6c006c;
}
#top-bar ul li ul a, #top-bar a:hover {
    color: #8db600;
}
/* CONTENT PANELS */
.content-panel {
    border: solid 1px #5d8aa8;
    background-color: #bcd4e6;
    box-shadow: 3px 3px 6px #e6e6fa;
}
.content-panel .panel-footer {
    color: #A30053;
}
.content-panel .content-toc {
    border: solid 1px #5d8aa8;
}

/* SEXY BOX */
div.sexy-box {
    background: #f0f8ff;
    border: 1px solid #903C67;
}
div.sexy-box div.image-container img {
    border: 1px solid #9f8170;
}
[[/code]]
[[/iftags]]
