[[code type="css"]]
/*
    Mole Cricket CSS Theme
    [2022 Wikidot Theme]

    Created by c_bonefish
    Base Code by Denevola

    Background-image1: https://pixabay.com/vectors/wheat-plant-silhouette-black-311728/
    Background-image2: https://pixabay.com/vectors/grain-field-corn-gold-silhouette-308513/

    Header Image: thd-glasses

    CC BY-SA 3.0
*/

@import url('https://fonts.googleapis.com/css2?family=Dongle:wght@700&display=swap');


/* Header */
div#container-wrap {
    background: url('https://scpko.wikidot.com/local--files/theme:mole-cricket/back.png') center top repeat-x;
    background-size: 500px;
}

#header {
    height: 200px;
    background-image: url('https://scpko.wikidot.com/local--files/theme:mole-cricket/logo.png');
    position: relative;
    background-repeat: no-repeat;
    background-size: 160px auto;
    background-position: 0em 2em;
}

#header h1 a span,
#header h2 span {
    display: none;
}

#header h1 {
    max-height: 125px;
}

#header h1 a {
    color: #EFD592;
    font-size: 100px;
    font-weight: normal;
    text-shadow: 3px 0 #7A3A3A, -3px 0 #7A3A3A, 0 3px #7A3A3A, 0 -3px #7A3A3A, 3px 3px #7A3A3A, -3px 3px #7A3A3A, 3px -3px #7A3A3A, -3px -3px #7A3A3A;
}

#header h1 a::before {
    display: inline-block;
    position: absolute;
    left: 170px;
    top: 38%;
    font-family: 'Dongle', sans-serif;
    content: "두레원";
}

#header h2::after {
    display: inline-block;
    position: absolute;
    left: 350px;
    top: 30%;
    color: #EFD592;
    font-size: 55px;
    font-weight: bold;
    text-shadow: 3px 0 #7A3A3A, -3px 0 #7A3A3A, 0 3px #7A3A3A, 0 -3px #7A3A3A, 3px 3px #7A3A3A, -3px 3px #7A3A3A, 3px -3px #7A3A3A, -3px -3px #7A3A3A;
    letter-spacing: 0px;
    font-family: 'Dongle', sans-serif;
    content: "바람이 머물다 간 들판에";
}

/* Login & Search & Top Bar */
#login-status {
    position: absolute;
    top: 4px;
    color: #7A3A1A;
    z-index: 2;
}

#login-status a {
    color: #7A3A1A;
    font-weight: bolder;
}

#account-topbutton {
    border-color: #7A3A1A;
}

#login-status ul a {
    color: #000000 !important;
}

#search-top-box {
    position: absolute;
    top: 30px;
    right: 0;
    width: 250px;
    text-align: right;
    z-index: 1;
}

#search-top-box-form {
    display: inline-flex;
}

#search-top-box-form > input {
    -webkit-appearance: none;
    -webkit-border-radius: 0;
    border-radius: 5px;
}

#search-top-box-input {
    background-color: #542812;
    border: solid 1px #000;
    color: #000;
}

#search-top-box-input:hover,
#search-top-box-input:focus {
    box-shadow: inset 1px 1px 5px rgb(0 0 0 / 50%);
    color: #000000 !important;
}

#search-top-box-form input[type=submit] {
    padding: 2px 5px;
    background: #542812;
    box-shadow: 0 1px 3px rgba(0, 0, 0, .5);
    border: solid 1px #FCF7EA;
    color: #FCF7EA;
    font-size: 90%;
}

#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    background: #7A3A1A;
}

#top-bar {
    display: flex;
    justify-content: center;
    top: 187px;
    width: 100%;
    z-index: 20;
}

#top-bar a {
    color: #fff;
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: #E8E8E5;
    border-color: transparent;
    color: #000;
}

#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background: #E8E8E5;
}

#top-bar ul li ul,
#top-bar ul li ul li ul {
    border-color: rgba(0, 0, 0, .4);
}

#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
    border-color: rgba(0, 0, 0, .1);
}

#top-bar ul li.sfhover ul li ul a,
#top-bar ul li:hover ul li ul a {
    border-top: none;
}

.top-bar {
    position: relative;
    margin: 0 auto;
}
.c_topbar_arrow {
    color: #D5D5D5;
}

/* Page Elements */
#page-title,
.meta-title {
    color: #4C2410;
    font-size: 2.1em;
    font-weight: bold;
}

#content-wrap {
    margin: 30px auto 0;
}

h1, h2, h3, h4, h5, h6 {
    color: #4C2410;
    font-weight: bold;
}

body {
    background-color: #FCF7EA;
    color: #000000;
}

a:link {
    color: #4C2410;
}

a:visited {
    color: #562913;
}

.hovertip {
    background-color: #F9F7F2 !important;
    border: 2px solid #4C2410 !important;
    color: #4C2410 !important;
}

.scp-image-block {
    background: #F9F7F2;
    box-shadow: 0 1px 3px rgba(0, 0, 0, .5);
}

.scp-image-block .scp-image-caption {
    background: #FCF7EA;
    border-top: none;
    font-size: 85%;
}

.scp-image-block img {
    box-shadow: 0 0 1px black;
}

/* ---- SCROLLBAR ---- */
::-webkit-scrollbar {
    width: unset;
    height: unset;
}

::-webkit-scrollbar-track {
    background: #FCF7EA;
}

::-webkit-scrollbar-thumb {
    background: #7A3A1A;
    border: none;
}

::-webkit-scrollbar-thumb:hover {
    background: #91451F;
}

textarea::-webkit-scrollbar-track {
    background: #FCF7EA;
}

/* Side Bar */
#side-bar {
    background: none;
}

#side-bar .side-block,
#side-bar .side-block.media,
#side-bar .side-block.resources,
#interwiki .side-block {
    background: #F9F3E3 !important;
    border: 2px solid #683217;
    border-radius: 0 0;
}

#side-bar .heading,
#interwiki .heading,
#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px #000;
    color: #562913;
}

#side-bar .collapsible-block-folded-link .collapsible-block-link:hover,
#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    color: #602D16;
}

#side-bar .collapsible-block-folded-link .collapsible-block-link,
#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    color: #562913;
}

#side-bar .menu-item .image,
#interwiki .menu-item .image,
#side-bar .collapsible-block-folded,
#side-bar .collapsible-block-unfolded-link .collapsible-block-content .image {
    filter: grayscale(80%);
}

#side-bar .collapsible-block-folded a:link {
    filter: grayscale(80%);
}

#side-bar a:link,
#interwiki a:link {
    color: #4C2410;
}

#side-bar a:visited,
#interwiki a:visited {
    color: #562913;
}

#side-bar a:link:hover,
#side-bar a:visited:hover,
#interwiki a:link:hover,
#interwiki a:visited:hover {
    color: #602D16;
}

#top-bar .open-menu a {
    background: #E8E8E5;
    border-color: #121212;
    color: #000000;
}

/* Tab */
div.yui-navset ul.yui-nav a,
div.yui-navset ul.yui-nav a:hover,
div.yui-navset ul.yui-nav .selected a,
div.yui-navset ul.yui-nav .selected a:hover,
div.yui-navset ul.yui-nav .selected a:focus {
    background: none;
    border: none;
}

.yui-navset {
    clear: both;
    margin-bottom: 1em;
    box-shadow: 0 1px 2px rgba(0, 0, 0, .5);
}

.yui-navset .yui-nav {
    background: #F9F2E0;
    border: 1px solid gainsboro;
    border-bottom: none;
}

.yui-navset .yui-nav li {
    margin-right: 10px !important;
}

.yui-navset .yui-nav li:first-child {
    margin-left: 1.5em;
}

.yui-navset .yui-nav li a em {
    padding: 10px .75em .25em;
    margin-bottom: -1px;
    background: none;
    border: none;
    border-bottom: 4px solid transparent;
    color: gray;
    transition: .2s;
}

.yui-navset .yui-nav li a em:hover {
    background: none;
}

.yui-navset .yui-nav .selected a em,
.yui-navset .yui-nav .selected a em:hover {
    padding: 10px .75em .25em;
    background: none;
    border: none;
    border-bottom: 4px solid #8E855E;
    color: #000;
    font-weight: bold;
}

div.yui-navset div.yui-content {
    background: #F9F7EF;
    border: 1px solid gainsboro;
}

/* Rate Module */
.page-rate-widget-box {
    margin-right: 0;
    background-color: #F9F3E3;
    border: 2px solid #683217;
    border-radius: 0px;
}

.page-rate-widget-box > span {
    border: none !important;
}

.page-rate-widget-box .rate-points {
    background: #F9F3E3 !important;
    color: #000 !important;
}

.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background: #F9F3E3;
}

.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    color: #000;
}

.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background-color: #683217;
    color: #F9F3E3;
}

.page-rate-widget-box .cancel {
    background: #F9F3E3;
}

.page-rate-widget-box .cancel a {
    color: #000;
    font-weight: bold;
}

.page-rate-widget-box .cancel a:hover {
    background: #683217;
    border-radius: 0;
    color: #F9F3E3;
}

.creditRate {
    margin-right: 0 !important;
}

.rate-box-with-credit-button {
    background: #F9F3E3 !important;
    border: 2px solid #683217 !important;
    border-radius: 0px !important;
}

.rate-box-with-credit-button .page-rate-widget-box {
    border: none;
}

.creditButton p a {
    border-left: 1px #000 !important;
    color: #000 !important;
}

#page-content .rate-box-with-credit-button .fa-info {
    padding: 3px 0 1px;
}

#page-content .rate-box-with-credit-button .fa-info:hover {
    background: #121212;
    border-radius: 0 0 0 0;
    color: #E7E7E7 !important;
}

/* ---- EDITING WINDOWS ---- */
.owindow {
    background-color: #ffffff;
    border-color: #8C8C8C;
}

.owindow .modal-header {
    background-color: #ffffff;
}

.owindow .modal-body img {
    background-color: #ffffff !important;
    color: #000000;
}

.owindow .title {
    background-color: #ffffff;
}

.owindow a,
.owindow a:visited {
    color: #CECECE;
}

.owindow .active {
    color: #000000;
}

.owindow .content.modal-body img {
    background-color: #727272 !important;
}

.owindow .button-bar a {
    background-color: #ffffff;
    color: #000000;
}

.owindow .button-bar a:hover,
.owindow .button-bar a:focus {
    background-color: #ffffff !important;
    border-color: #999999;
    color: #000000;
}

table.page-files .highlight {
    background-color: #FBFBFB;
}

input.text,
input.text:focus,
input.text:hover {
    background-color: #ffffff !important;
    border-color: #999999;
    color: #000000;
}

textarea {
    background-color: #FEFEFE;
    color: #000000;
}

.change-textarea-size a {
    background-color: #ffffff;
    color: #000000;
}

#lock-info {
    background-color: #ffffff;
}

/* Blockquote */
blockquote,
div.blockquote {
    background-color: #F9F7F2;
    border-style: dotted;
}

/* etc. */
@media (min-width: 336px) and (max-width: 755px) {
    #header h2::after {
        display: none;
    }
}

@media (max-width: 335px) {
    #header h1 a {
        font-size: 70px;
    }
    #header h2::after {
        display: none;
    }
}

[[/code]]

[[/iftags]]
