[[code type="css"]]
/*
    Mr. Reprisal CSS Theme
    [2022 Wikidot Theme]

    Created by c_bonefish
    Base Code by Denevola

    CC BY-SA 3.0
*/

@import url('https://scpko.wdfiles.com/local--files/korean-font/NapjakBlock.css');
@import url('https://fonts.googleapis.com/css?family=Berkshire+Swash');
@import url('https://fonts.googleapis.com/css?family=Sansita');
@import url('https://fonts.googleapis.com/css?family=Stylish');

:root {
    --image-1: url('https://scpko.wikidot.com/local--files/theme:mr-reprisal/back.png') center top repeat-x;
    --image-2: url('https://scpko.wikidot.com/local--files/theme:mr-reprisal/logo.png');

    --color-1: #F9F9F9;
    --color-2: #E5E5E5;
    --color-3: #161616;
    --color-4: #2D2D2D;
}

/* Header */
div#container-wrap {
    background: var(--image-1);
}

#header {
    background-image: var(--image-2);
    position: relative;
    background-repeat: no-repeat;
    background-size: 100px auto;
    background-position: 0em 3em;
}

#header h1 a span,
#header h2 span {
    display: none;
}

#header h1 {
    max-height: 125px;
}

#header h1 a {
    color: var(--color-2);
    font-weight: normal;
    text-shadow: 2px 0 #000, -2px 0 #000, 0 2px #000, 0 -2px #000, 2px 2px #000, -2px 2px #000, 2px -2px #000, -2px -2px #000;
}

#header h1 a::before {
    display: inline-block;
    position: absolute;
    left: 110px;
    top: 70px;
    font-family: 'Berkshire Swash', 'NapjakBlock', cursive;
    content: "원더테인먼트 박사!";
}

#header h2::after {
    display: inline-block;
    left: 350px;
    top: 30%;
    color: var(--color-2);
    font-weight: bold;
    font-size: 18px;
    text-shadow: 1px 0 #000, -1px 0 #000, 0 1px #000, 0 -1px #000, 1px 1px #000, -1px 1px #000, 1px -1px #000, -1px -1px #000;
    letter-spacing: 0px;
    font-family: 'Sansita', 'Stylish', sans-serif;
    content: "명심해 너의 머릿속이 원더풀한 곳이라는 걸";
}

/* Login & Search & Top Bar */
#login-status {
    position: absolute;
    top: 3px;
    color: #EFEFEF;
    z-index: 2;
}

#login-status a {
    color: #EFEFEF;
    font-weight: bolder;
}

#account-topbutton {
    border-color: #EFEFEF;
}

#login-status ul a {
    color: var(--color-3) !important;
}

#search-top-box {
    position: absolute;
    top: 30px;
    right: 0;
    width: 250px;
    text-align: right;
    z-index: 1;
}

#search-top-box-form {
    display: inline-flex;
}

#search-top-box-form > input {
    -webkit-appearance: none;
    -webkit-border-radius: 0;
    border-radius: 5px;
}

#search-top-box-input {
    background-color: var(--color-4);
    border: solid 1px var(--color-3);
    color: #000;
}

#search-top-box-input:hover,
#search-top-box-input:focus {
    box-shadow: inset 1px 1px 5px rgb(0 0 0 / 50%);
    color: var(--color-3) !important;
}

#search-top-box-form input[type=submit] {
    padding: 2px 5px;
    background: var(--color-3);
    box-shadow: 0 1px 3px rgba(0, 0, 0, .5);
    border: solid 1px var(--color-4);
    color: var(--color-1);
    font-size: 90%;
}

#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    background: var(--color-3);
}

#top-bar {
    display: flex;
    justify-content: center;
    top: 141px;
    width: 100%;
    z-index: 20;
}

#top-bar a {
    color: var(--color-1);
    background: none;
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: var(--color-1);
    border-color: transparent;
    color: var(--color-3);
}

#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background: var(--color-1);
}

#top-bar ul li ul,
#top-bar ul li ul li ul {
    border-color: rgba(0, 0, 0, .4);
}

#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
    border-color: rgba(0, 0, 0, .1);
}

#top-bar ul li.sfhover ul li ul a,
#top-bar ul li:hover ul li ul a {
    border-top: none;
}

.top-bar {
    position: relative;
    margin: 0 auto;
}
.c_topbar_arrow {
    color: #BFBFBF;
}

/* Page Elements */
#page-title,
.meta-title {
    color: var(--color-4);
    font-size: 2.1em;
    font-weight: bold;
}

#content-wrap {
    margin: 30px auto 0;
}

h1, h2, h3, h4, h5, h6 {
    color: var(--color-4);
    font-weight: bold;
}

body {
    background-color: var(--color-1);
    color: #000000;
}

a:link {
    color: #444444;
}

a:visited {
    color: #565656;
}

.hovertip {
    background-color: var(--color-1) !important;
    border: 2px solid #000 !important;
    color: #000 !important;
}

.scp-image-block {
    background: var(--color-1);
    box-shadow: 0 1px 3px rgba(0, 0, 0, .5);
}

.scp-image-block .scp-image-caption {
    background: var(--color-2);
    border-top: none;
    font-size: 85%;
}

.scp-image-block img {
    box-shadow: 0 0 1px black;
}

/* ---- SCROLLBAR ---- */
::-webkit-scrollbar {
    width: unset;
    height: unset;
}

::-webkit-scrollbar-track {
    background: var(--color-1);
}

::-webkit-scrollbar-thumb {
    background: var(--color-3);
    border: none;
}

::-webkit-scrollbar-thumb:hover {
    background: var(--color-4);
}

textarea::-webkit-scrollbar-track {
    background: var(--color-1);
}

/* Side Bar */
#side-bar {
    background: none;
}

#side-bar .side-block,
#side-bar .side-block.media,
#side-bar .side-block.resources,
#interwiki .side-block {
    background: var(--color-1) !important;
    border: 2px solid var(--color-3);
    border-radius: 0 0;
}

#side-bar .heading,
#interwiki .heading,
#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px var(--color-4);
    color: #444444;
}

#side-bar .collapsible-block-folded-link .collapsible-block-link:hover,
#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    color: #565656;
}

#side-bar .collapsible-block-folded-link .collapsible-block-link,
#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    color: #6B6B6B;
}

#side-bar .menu-item .image,
#interwiki .menu-item .image,
#side-bar .collapsible-block-folded,
#side-bar .collapsible-block-unfolded-link .collapsible-block-content .image {
    filter: grayscale(80%);
}

#side-bar .collapsible-block-folded a:link {
    filter: grayscale(80%);
}

#side-bar a:link,
#interwiki a:link {
    color: #444444;
}

#side-bar a:visited,
#interwiki a:visited {
    color: #565656;
}

#side-bar a:link:hover,
#side-bar a:visited:hover,
#interwiki a:link:hover,
#interwiki a:visited:hover {
    color: #6B6B6B;
}

#top-bar .open-menu a {
    background: var(--color-1);
    border-color: var(--color-3);
    color: var(--color-3);
}

/* Tab */
div.yui-navset ul.yui-nav a,
div.yui-navset ul.yui-nav a:hover,
div.yui-navset ul.yui-nav .selected a,
div.yui-navset ul.yui-nav .selected a:hover,
div.yui-navset ul.yui-nav .selected a:focus {
    background: none;
    border: none;
}

.yui-navset {
    background: var(--color-1);
    clear: both;
    margin-bottom: 1em;
    box-shadow: 0 1px 2px rgba(0, 0, 0, .5);
}

.yui-navset .yui-nav {
    background: var(--color-2);
    border: 1px solid gainsboro;
    border-bottom: none;
}

.yui-navset .yui-nav li {
    margin-right: 10px !important;
}

.yui-navset .yui-nav li:first-child {
    margin-left: 1.5em;
}

.yui-navset .yui-nav li a em {
    padding: 10px .75em .25em;
    margin-bottom: -1px;
    background: none;
    border: none;
    border-bottom: 4px solid transparent;
    color: gray;
    transition: .2s;
}

.yui-navset .yui-nav li a em:hover {
    background: none;
}

.yui-navset .yui-nav .selected a em,
.yui-navset .yui-nav .selected a em:hover {
    padding: 10px .75em .25em;
    background: none;
    border: none;
    border-bottom: 4px solid var(--color-4);
    color: var(--color-3);
    font-weight: bold;
}

div.yui-navset div.yui-content {
    background: var(--color-1);
    border: 1px solid gainsboro;
}

/* Rate Module */
.page-rate-widget-box {
    margin-right: 0;
    background-color: var(--color-1);
    border: 2px solid var(--color-3);
    border-radius: 0px;
}

.page-rate-widget-box > span {
    border: none !important;
}

.page-rate-widget-box .rate-points {
    background: var(--color-1) !important;
    color: #000 !important;
}

.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background: var(--color-1);
}

.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    color: var(--color-3);
}

.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background-color: var(--color-3);
    color: var(--color-1);
}

.page-rate-widget-box .cancel {
    background: var(--color-1);
}

.page-rate-widget-box .cancel a {
    color: #000;
    font-weight: bold;
}

.page-rate-widget-box .cancel a:hover {
    background: var(--color-3);
    border-radius: 0;
    color: var(--color-1);
}

.creditRate {
    margin-right: 0 !important;
}

.rate-box-with-credit-button {
    background: var(--color-1) !important;
    border: 2px solid var(--color-3) !important;
    border-radius: 0px !important;
}

.rate-box-with-credit-button .page-rate-widget-box {
    border: none;
}

.creditButton p a {
    border-left: 1px #000 !important;
    color: #000 !important;
}

#page-content .rate-box-with-credit-button .fa-info {
    padding: 3px 0 1px;
}

#page-content .rate-box-with-credit-button .fa-info:hover {
    background: var(--color-4);
    border-radius: 0 0 0 0;
    color: var(--color-1) !important;
}

/* ---- EDITING WINDOWS ---- */
.owindow {
    background-color: var(--color-1);
    border-color: var(--color-3);
}

.owindow .modal-header {
    background-color: var(--color-1);
}

.owindow .modal-body img {
    background-color: var(--color-1) !important;
    color: #000000;
}

.owindow .title {
    background-color: var(--color-1);
}

.owindow a,
.owindow a:visited {
    color: var(--color-4);
}

.owindow .active {
    color: #000000;
}

.owindow .content.modal-body img {
    background-color: var(--color-3) !important;
}

.owindow .button-bar a {
    background-color: var(--color-1);
    color: #000000;
}

.owindow .button-bar a:hover,
.owindow .button-bar a:focus {
    background-color: var(--color-1) !important;
    border-color: var(--color-2);
    color: #000000;
}

table.page-files .highlight {
    background-color: #FBFBFB;
}

input.text,
input.text:focus,
input.text:hover {
    background-color: var(--color-1) !important;
    border-color: var(--color-2);
    color: #000000;
}

textarea {
    background-color: #FEFEFE;
    color: #000000;
}

.change-textarea-size a {
    background-color: var(--color-1);
    color: #000000;
}

#lock-info {
    background-color: var(--color-1);
}

/* Blockquote */
blockquote,
div.blockquote {
    background-color: var(--color-1);
    border-style: dotted;
}

@media (min-width: 388px) and (max-width: 767px) {
    #header h1 a {
        font-size: 90%;
    }
    #header h2::after {
        font-size: 12px;
        left: 380px;
    }
}

@media (max-width: 387px) {
    #header h1 a {
        font-size: 70%;
    }
    #header h2::after {
        display: none;
    }
}

[[/code]]

[[/iftags]]
