[[collapsible show="点击展开源代码" hide="收起"]]
[[code type="css"]]
@import url(https://pokm.wikidot.com/local--code/theme:starry-library/1);

/*------- 变量 ------- */
:root {
    --body-font: adelle-sans, 'Lato', 'Noto Sans SC', sans-serif;
    --header-font: 'zuijinregular', 'Poppins', 'Noto Serif SC', serif;
    --title-font: 'zuijinregular', 'Poppins', 'Noto Serif SC', serif;
    --mono-font: "Noto Sans Mono", adaptive-mono, "PT Mono", "Andale Mono", "Courier New", Courier, monospace;
    --pale-gray-monochrome: 217, 198, 137;
    --light-gray-monochrome: 232, 192, 118;
    --gray-monochrome: 189, 144, 58;
    --dark-gray-monochrome: 71, 50, 6;
    --bright-accent: 255, 244, 220;
    --medium-accent: 228, 180, 28;
    --dark-accent: 185, 150, 17;
}

/* ------- 主标题 -------*/
#header h1 a::before {
    color: rgb(var(--bright-accent));
    text-shadow: inherit;
    background: url(https://scp-wiki.wdfiles.com/local--files/theme%3Adustjacket-sigma/goldfoil.png);
    background-size: cover;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    overflow-wrap: normal;
    font-size: 125%;
}

div#header {
    height: 200px;
}

div#header::before {
    top: 0;
    height: 180px;
    background-position: center center;
}

div#header h1 a {
    display: flex;
    height: 180px;
    max-height: 180px;
    padding: 0;
    align-items: center;
    justify-content: center;
    bottom: 0;
}

div#header h1 a::before {
    bottom: 0;
    line-height: 100%;
}

/* ------- 主标题的移动端适配 ------- */
@media (max-width: 767px) {
    #header h1 a {
        font-size: 100%;
    }
}

/* ------- 副标题（默认不显示）------- */
div#header h2 {
    display: none;
}

/* ------- 版头 ------- */
div#header div#top-bar {
    top: 180px;
    z-index: 300 !important;
}

div#header div#top-bar > div.top-bar {
    background-color: rgba(0, 0, 0, 0.5);
    border-radius: 1rem;
    box-shadow: 0 0 5px rgba(0, 0, 0, 0.2);
    padding: 0.5rem 1.5rem;
    height: 21px;
}

div#header div#top-bar > div > ul > li > a {
    color: #dbac53;
    border: none;
    border-radius: 0.4rem;
}

div#header div#top-bar > div > ul > li > a:hover,
div#header div#top-bar > div > ul > li:hover > a{
    border: none;
    background-color:  rgba(0, 0, 0, 0.5);
}

div#header div#top-bar > div > ul > li > ul {
    padding: 0.5rem;
    border: none;
    border-radius: 1rem;
    background-color: rgba(0, 0, 0, 0.5);
    box-shadow: 0 0 5px rgba(0, 0, 0, 0.2);
    overflow: hidden;
}

div#header div#top-bar > div > ul > li > ul > li {
    padding: 0.125rem;
    border: none;
    border-radius: 0.4rem;
    overflow: hidden;
}

div#header div#top-bar > div > ul > li.sfhover > ul > li > a,
div#header div#top-bar > div > ul > li:hover > ul > li > a {
    border: none;
    border-top: none;
    border-radius: 0.4rem;
}

div#main-content {
    background-color: rgba(0, 0, 0, 0.5);
    border-radius: 1rem;
    min-height: 100rem;
    box-shadow: 0 0 5px rgba(0, 0, 0, 0.2);
}

@media (min-width: 768px) {
div#main-content {
    padding: 2rem 4rem;
}
}

@media (max-width: 767px) {
#main-content {
    max-width: 100%;
    margin: 0;
    padding: 1rem 5%;
}

div#header div#top-bar > div.mobile-top-bar {
    background-color: rgba(0, 0, 0, 0.5);
    border-radius: 1rem;
    box-shadow: 0 0 5px rgba(0, 0, 0, 0.2);
    padding: 0.5rem 1.5rem;
    height: 21px;
}

div#header div#top-bar > div.mobile-top-bar > ul {
    display: flex;
    flex-wrap: nowrap;
}
}

/* ------- 字体 ------- */
body {
    font-family: var(--body-font) !important;
}

div#page-content {
    font-size: 0.90rem;
}

div#header h1 a::before {
    font-family: "Noto Serif SC", serif !important;
}

/* ------- 顶栏 ------- */
div#top-bar div.open-menu a {
    border: 0.2em solid transparent;
    border-radius: 1rem;
    background-color: rgba(0, 0, 0, 0.5);
    color: #dbac53;
    z-index: 300 !important;
}

a.close-menu {
    opacity: 0.1;
}

/* ------- 侧栏 ------- */
div#side-bar {
    background-image: linear-gradient(to bottom, #000000, #553F10);
    border: none !important;
    padding: 1em 1em 0 1em;
    z-index: 310 !important;
}

div#side-bar div.side-block {
    /* background-color: rgba(0, 0, 0, 0.5) !important; */
    border-radius: 1rem;
    box-shadow: 0 0 5px rgba(0, 0, 0, 0.2);
    border: none;
}

    #page-content h1,
    #page-content h2,
    #page-content h3,
    #page-content h4,
    #page-content h5,
    #page-content h6 {
        -webkit-background-clip: text;
        background-clip: text;
        background-color: #dbac53;
        -webkit-text-fill-color: transparent;
    }


/* ------- 正文部分 ------- */
div#container-wrap {
    background: black;
    background-image: url("https://pokm.wikidot.com/local--files/theme:city-of-the-nameless/bkg");
    background-size: 480px;
    background-position: center top;
    background-repeat: repeat-x;
    background-attachment: local;
}


div#main-content {
    background-color: rgba(36, 25, 1, 0.75);
    border: solid 5px #dbac53;
    border-radius: 1rem;
    min-height: 100rem;
    box-shadow: 0 0 5px rgba(0, 0, 0, 0.2);
}


div#page-title,
div.meta-title {
    text-align: center;
    color: #dbac53;
}

#page-content {
    color: white;
}

/* ------- 文本框 ------- */
blockquote,
div.blockquote {
    background: rgb(var(--dark-gray-monochrome));
    border: solid 3px #dbac53;
    border-radius: 1rem;
}

/* ------- 分页 ------- */
.yui-navset .yui-content {
    border-color: rgb(var(--light-gray-monochrome));
    border-top-color: rgb(var(--medium-accent));
    background-color:  rgb(var(--dark-gray-monochrome));
    font-weight: normal;
}
.yui-navset .yui-nav, .yui-navset .yui-navset-top .yui-nav {
    border-bottom: 0.3em solid rgb(var(--medium-accent));
}
.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
    background-image: none;
    background-color: rgb(var(--pale-gray-monochrome));
    color: black;
    font-weight: normal;
}
.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus {
    background: rgb(var(--gray-monochrome));
    color: white;
}
.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus, /* Pas d'effet focus/hover pour tab sélectionné */
.yui-navset .yui-nav .selected a:hover {
    background-image: none !important;
    background: rgb(var(--dark-accent));
    border-color: rgb(var(--dark-accent));
    color: white;
}
.yui-navset li {
    line-height: normal;
}

/* ------- 评分模块 ------- */
#page-content .page-rate-widget-box {
    font-size: .8rem;
}

.page-rate-widget-box .rate-points {
    background-color: rgb(var(--dark-gray-monochrome)) !important;
    border-color: #dbac53;
    color: rgb(var(--bright-accent)) !important;
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background-color: rgb(var(--dark-gray-monochrome));
    border-top-color: #dbac53;
    border-bottom-color: #dbac53;
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    background: transparent;
    color: rgb(var(--bright-accent)) !important;
}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background: rgb(var(--gray-monochrome));
    color: white !important;
}
.page-rate-widget-box .cancel {
    background: transparent;
    background-color: rgb(var(--dark-gray-monochrome));
    border-color: #dbac53;
}
.page-rate-widget-box .cancel a {
    color: rgb(var(--bright-accent)) !important;
}
.page-rate-widget-box .cancel a:hover {
    background-color: rgb(var(--gray-monochrome));
    color: white !important;
}

#page-content .rate-box-with-credit-button {
    background-color: rgb(var(--dark-gray-monochrome));
    border-color:#dbac53;
}

#page-content .rate-box-with-credit-button .page-rate-widget-box {
    padding-top: 0px !important;
    border-color: transparent;
}

#page-content .rate-box-with-credit-button .page-rate-widget-box .cancel,
#page-content .rate-box-with-credit-button .page-rate-widget-box .rate-points,
#page-content .rate-box-with-credit-button .page-rate-widget-box .rateup,
#page-content .rate-box-with-credit-button .page-rate-widget-box .ratedown {
    border-color: transparent;
    border-bottom-radius: 0;
    border-bottom: solid 1px #dbac53;
}

#page-content .rate-box-with-credit-button .creditButton p a {
    border-left-color: transparent;
}
#page-content .rate-box-with-credit-button .creditButton a {
    color: rgb(var(--bright-accent)) !important;
}
#page-content .rate-box-with-credit-button .creditButton a:hover {
    background-color: rgb(var(--gray-monochrome));
    color: white !important;
}

/* ------- 链接 ------- */
a:link, a:visited {
    color: #c99c87;
}
a:hover, a:active {
    color: white;
}
a {
    color: yellow;
}

#page-options-container {
  color:  #dbac53;
}

#action-area {
  color:  #dbac53;
}

/* ------- 底部工具栏 ------- */
table.wiki-content-table th {
  background-color: rgb(var(--dark-gray-monochrome));
  border-color:#dbac53;
}

#breadcrumbs {
  color:  #dbac53 !important;
}

table.form td div.sub, form div.sub {
  color:  #dbac53;
}

/* ------- 图片框 ------- */
div.scp-image-block .scp-image-caption {
       background-color: #362503 !important;
}

/* ------- 代码框 ------- */
div.code {
    background: rgb(var(--dark-gray-monochrome));
    border: dashed 2px #dbac53;
}

div.code span.hl-code {
    color: white;
}

div.code span.hl-identifier {
    color: white;
}

div.code span.hl-brackets {
    color: white;
}

div.code span.hl-special {
    color: #b5b8f5;
}

div.code span.hl-reserved {
    color: #cacbe3;
}


div.code span.hl-string {
    color: #7496cc;
}

div.code span.hl-number {
    color: red;
}

div.code span.hl-var {
    color: lightgreen;
}

/* ------- 脚注 ------- */
div.footnotes-footer {
    background: rgb(var(--dark-gray-monochrome));
}

/* ------- 著作信息块 ------- */
#page-content .modalbox {
    background: rgb(var(--dark-gray-monochrome)) !important;
    border: solid 2px #dbac53;
}


/* ------- 分割线 ------- */
hr {
    border-bottom: solid 2px rgb(var(--medium-accent));
}
[[/code]]

