[[iftags +版式]]
[[module CSS]]
@import url('https://fonts.googleapis.com/css2?family=Great+Vibes:wght@300;400;500;700&subset=cyrillic,cyrillic-ext,greek,greek-ext,latin-ext');

div#page-title::after {
    content: "(ver 1.0.1)";
    opacity: 0.5;
    font-size: 50%;
    font-family: var(--mono-font);
    line-height: 2.5rem;
}

div.theme-code {
    display: block !important;
}

div.theme-title {
    text-align: center;
    font-family: "Great Vibes", cursive;
    font-size: 3rem;
}

div.theme-title * {
    margin: 0;
}

div.theme-icon {
    transform: scale(1);
    -webkit-transform: scale(1);
    transition: ease var(--base-transition-time);
    -webkit-transition: ease var(--base-transition-time);
}

div.theme-icon:focus,
div.theme-icon:hover {
    transform: scale(1.2);
    -webkit-transform: scale(1.2);
}

tt,
div.code.example {
    background-color: rgb(225, 225, 225);
}

div.dark tt,
div.box.domc-memo tt,
div.box.pata-memo tt,
div.box.overwatch-memo tt {
    background-color: rgb(75, 75, 75);
}
[[/module]]

