}

div#header #login-status a#account-topbutton {
    border: none;
    margin: 0;
    padding: 0;
}

div#header #login-status a#account-topbutton::before {
    content: "\f0d7";
}

div#header #search-top-box::before {
    content: "\f002";
}

div#header #search-top-box::before,
div#header #login-status span.printuser a,
