/*-------------------------------------*/
/*-------------LAYOUT---------------*/
/*-------------------------------------*/

#container {
    display: flex;
    flex-direction: column;
}

#content-wrap {
    width: 100%;
    max-width: 68rem;
    margin-top: 0;
    display: grid;
    grid-template-areas:
        "deco deco"
        "side main";
    grid-template-rows: 3rem auto;
    grid-template-columns: var(--side-bar-width) minmax(0, 1fr);
    grid-gap: 1.125rem 2.5rem;
    padding: 1.125rem 2.5em;
    box-sizing: border-box;
}

#side-bar {
    position: relative;
    width: var(--side-bar-width);
    top: 0; left: 0;
    margin: 0;
    grid-area: side;
}

#main-content {
    margin: 0; padding: 0;
    max-width: unset;
    grid-area: main;
}

@media only screen and (max-width:768px) {
    #content-wrap {
        grid-template-areas:
            "deco deco"
            "main main";
    }
    #side-bar {
        position: fixed;
        left: calc(var(--side-bar-width)*-1.15);
        overflow-x: hidden;
    }
    #side-bar, #side-bar:target {
        border: none;
        width: var(--side-bar-width);
    }
}

/*--------------------------------------*/
/*-------------SIDEBAR---------------*/
/*--------------------------------------*/

#side-bar {
    box-sizing: border-box;
}

:is(#side-bar, #interwiki) a {
    color: rgb(var(--cold-space));
}

:is(#side-bar, #interwiki) .side-block {
    border: none;
    box-shadow: none;
    border-radius: 0;
    background: rgb(var(--outer-space));
    font-family: var(--UI-font);
}
#side-bar .side-block.media { background-color: rgb(var(--outer-space)); }
