.side-block.sns, .side-block[style="background-color: #fff0f0;"], [style="background-color: #f0f0f0;"], [style="background-color: #a7dba2;"] {
    background-color: rgb(var(--outer-space)) !important;
}

:is(#side-bar, #interwiki) .heading {
    display: flex;
    align-items: center;
    column-gap: 0.125em;
    color: rgb(var(--nebula-hue));
    font-family: var(--header-font);
    font-size: 1.125rem;
    border-color: currentColor;
    padding: 0;
}

@media only screen and (max-width:768px) {
    #side-bar {
        background-color: rgb(var(--outer-space));
        padding: 0;
    }
