[[code type="css"]]
:root {
  --gray-accent: 72, 72, 72;
  --orange-accent: 229, 171, 42;
  --light-gray-accent: 131, 131, 131;
  --vivid-orange-accent: 242, 119, 25;
  --content-bg-color: 245, 245, 245;
}
body {
  color: #484848;
  color: rgb(var(--gray-accent));
}
/*=====================================*/

/*============== Header ===============*/

div#container-wrap {
  background: unset;
}
#main-content {
  margin: 0px 4em;
}
#page-content {
  position: relative;
}
#header {
  padding-bottom: 32px;
  height: 180px;
  background: url(data:image/svg+xml;base64,) 0px 60px no-repeat;
  background-size: 300px;
}
#header h2 {
  display: none;
}
#header h1 {
  position: absolute;
  top: 0;
  margin-top: 40px;
  margin-left: 100px;
  width: 200px;
  opacity: 0;
}
@media (max-width: 479px) {
  #header h1 {
    margin-left: 60px;
  }
}
#top-bar {
  top: 180px;
  height: 31px;
}
#top-bar ul li a {
  padding-top: 15px;
  padding-bottom: 15px;
}
#top-bar .top-bar>ul>li.sfhover>a, #top-bar .top-bar>ul>li:hover>a, #top-bar .mobile-top-bar>ul>li.sfhover>a, #top-bar .mobile-top-bar>ul>li:hover>a {
  background: #fff;
  color: #484848;
  color: rgb(var(--gray-accent));
  text-decoration: none;
}
#top-bar ul li ul {
  border: unset;
  box-shadow: 0px 6px 10px 0px rgba(0, 0, 0, .5);
}
#top-bar ul li.sfhover ul li a, #top-bar ul li:hover ul li a {
  padding-top: 5px;
  padding-bottom: 5px;
  border: none;
  background: #fff;
  color: #484848;
  color: rgb(var(--gray-accent));
}
#top-bar ul li.sfhover ul li.sfhover a:hover {
  background: #484848;
  background: rgb(var(--gray-accent));
  color: #fff;
  text-decoration: none;
}
#header-extra-div-1 {
  padding: 0 5px 0 5px;
  width: calc(100% - 20px);
  height: 40px;
  border-right: 10px solid #e5ab2a;
  border-right: 10px solid rgb(var(--orange-accent));
  border-left: 10px solid #e5ab2a;
  border-left: 10px solid rgb(var(--orange-accent));
  background: #484848;
  background: rgb(var(--gray-accent));
}
#extra-div-1 {
  position: absolute;
  top: 160px;
  width: 100%;
  height: 20px;
  background: repeating-linear-gradient(330deg, #e5ab2a, #e5ab2a 20px, #838383 0, #838383 40px);
  background: repeating-linear-gradient(330deg, rgb(var(--orange-accent)), rgb(var(--orange-accent)) 20px, rgb(var(--light-gray-accent)) 0, rgb(var(--light-gray-accent)) 40px);
}
#extra-div-2 {
  position: absolute;
  top: 180px;
  width: 100%;
  height: 31px;
  background: #484848;
  background: rgb(var(--gray-accent));
}
@media (max-width: 580px) {
  #container #header {
    background-position: 0 70px;
    background-size: 200px;
  }
  #main-content {
    margin: 0 5%;
  }
}
#login-status ul a {
  background: #fff;
  color: #484848;
  color: rgb(var(--gray-accent));
}
#account-options li a:hover {
  background: #484848;
  background: rgb(var(--gray-accent));
  color: #f5f5f5;
  color: rgb(var(--content-bg-color));
  text-decoration: none;
}
#login-status {
  color: #f5f5f5;
  color: rgb(var(--content-bg-color));
}
/*=====================================*/

/*====== Collapsible Search Area ======*/

/* https://scp-jp-sandbox3.wikidot.com/draft:3396310-179-c4b9 */

/*======== Created by ukwhatn =========*/

/*=========== CC BY-SA 3.0 ============*/

#search-top-box {
  width: unset;
}
#search-top-box-form::after {
  position: absolute;
  top: 5px;
  left: 7.5px;
  z-index: 2;
  color: #484848;
  color: rgb(var(--gray-accent));
  content: "\f002";
  font-size: 12px;
  font-family: "FontAwesome";
  pointer-events: none;
}
#search-top-box-input {
  position: relative;
  z-index: 1;
  width: 19px;
  height: 19px;
  border-radius: 35%;
  background: rgba(255, 255, 255, 1);
  transition: .5s ease-out, z-index .1s step-start;
}
#search-top-box-input:hover {
  background: rgba(255, 255, 255, 1);
}
#search-top-box input.empty {
  color: transparent;
}
#search-top-box-input:focus {
  z-index: 3;
  width: 100px;
  height: 19px;
  border-radius: unset;
  background: rgba(255, 255, 255, 1);
  color: #484848;
  color: rgb(var(--gray-accent));
}
#search-top-box-form .btn {
  width: 1px;
  opacity: 0;
  transition: .5s ease-out;
}
@media (max-width: 479px) {
  #search-top-box {
    top: 79px;
    right: 9px;
  }
}
/*===========================================*/

/*=== https://rtas.wikidot.com/_memo:start ===*/

/*===========================================*/

#search-top-box input:-webkit-autofill, #search-top-boxinput:-webkit-autofill:hover, #search-top-boxinput:-webkit-autofill:focus, #search-top-boxselect:-webkit-autofill, #search-top-boxselect:-webkit-autofill:hover, #search-top-boxselect:-webkit-autofill:focus {
  -webkit-box-shadow: 0 0 0px 1000px #fff inset !important;
  color: #484848 !important;
  color: rgb(var(--gray-accent)) !important;
  -webkit-transition: background-color 9999s, -webkit-box-shadow 9999s, color 9999s, -webkit-text-fill-color 9999s !important;
  transition: background-color 9999s, -webkit-box-shadow 9999s, color 9999s, -webkit-text-fill-color 9999s !important;
  -webkit-text-fill-color: #484848 !important;
  -webkit-text-fill-color: rgb(var(--gray-accent)) !important;
}
/*===========================================*/

/*============= Custom sidebar ==============*/

#side-bar {
  position: fixed;
  top: 0;
  left: 0;
  z-index: 40;
  float: none;
  overflow-y: scroll;
  margin: 0;
  padding: 1rem 2rem 0 1rem;
  width: 19em;
  height: 100vh;
  border: none;
  border-right: 5px solid #e5ab2a;
  border-right: 5px solid rgb(var(--orange-accent));
  background: #484848;
  background: rgb(var(--gray-accent));
  transition: transform 300ms cubic-bezier(0.4, 0.0, 0.2, 1), -webkit-transform 300ms cubic-bezier(0.4, 0.0, 0.2, 1), -moz-transform 300ms cubic-bezier(0.4, 0.0, 0.2, 1), -o-transform 300ms cubic-bezier(0.4, 0.0, 0.2, 1);
  -webkit-transform: translateX(-17rem);
  -moz-transform: translateX(-17rem);
  -ms-transform: translateX(-17rem);
  -o-transform: translateX(-17rem);
  transform: translateX(-17rem);
  -ms-overflow-style: none;
  scrollbar-width: none;
}
#side-bar::-webkit-scrollbar {
  display: none;
}
#side-bar:hover {
  -webkit-transform: translateX(0);
  -moz-transform: translateX(0);
  -ms-transform: translateX(0);
  -o-transform: translateX(0);
  transform: translateX(0);
}
#side-bar .side-block {
  padding: 0;
  border: none;
  border-radius: 0;
  box-shadow: none;
  background: transparent !important;
  margin-bottom: 1em;
  color: #838383;
  color: rgb(var(--light-gray-accent));
}
#side-bar .heading {
  color: #fff;
  border: none;
  background: #e5ab2a;
  background: rgb(var(--orange-accent));
  padding-left: 10px;
  margin: 0 0 1em 10px;
  font-size: 1em;
  font-weight: normal;
}
#side-bar .heading:not(:first-child) {
  margin-top: 1em;
}
#side-bar div.menu-item {
  position: relative;
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  margin: 0 0 0 10px;
  padding: 0 0 0 15px;
  border-left: 10px solid #e5ab2a;
  border-left: 10px solid rgb(var(--orange-accent));
}
#side-bar div.menu-item + div.menu-item {
  padding-top: 1em;
}
#side-bar div.menu-item img {
  display: none;
}
#side-bar div.menu-item a {
  font-weight: normal;
}
#side-bar .collapsible-block-folded {
  margin-bottom: 1em;
  background: none;
}
#side-bar .collapsible-block-link {
  margin-left: 0;
  font-weight: normal;
}
#side-bar .collapsible-block .collapsible-block-folded .collapsible-block-link {
  background: transparent;
}
#side-bar .collapsible-block-unfolded-link {
  margin-bottom: 1em;
  border: none;
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
  margin: 0 0 0 10px;
  font-size: 1em;
  border-left: none;
  color: #fff;
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
  color: #fff;
}
#side-bar .collapsible-block .collapsible-block-unfolded .collapsible-block-link {
  width: 90%;
}
#side-bar .side-block a {
  color: #fff;
}
#side-bar .menu-item .sub-text {
  color: #e5ab2a;
  color: rgb(var(--orange-accent));
  font-size: 90%;
}
@media (max-width: 479px) {
  #side-bar {
    left: -19rem;
    -webkit-transition: left 0.5s ease-in-out 0.1s;
    -moz-transition: left 0.5s ease-in-out 0.1s;
    -ms-transition: left 0.5s ease-in-out 0.1s;
    -o-transition: left 0.5s ease-in-out 0.1s;
    transition: left 0.5s ease-in-out 0.1s;
    -webkit-transform: none;
    -moz-transform: none;
    -o-transform: none;
    transform: none;
  }
  #side-bar:target {
    left: 0;
    width: 19em;
    border: none;
    border-right: 5px solid #e5ab2a;
    border-right: 5px solid rgb(var(--orange-accent));
    z-index: 200;
  }
  #side-bar:hover {
    -webkit-transform: none;
    -moz-transform: none;
    -o-transform: none;
    transform: none;
  }
  #side-bar:target .close-menu {
      width: 100vh;
      height: 100vh;
      background-color: rgba(0,0,0,0.5);
  }
}
#top-bar .open-menu {
  display: none;
}
@media (max-width: 479px) {
  #top-bar .open-menu {
    display: block;
  }
  #top-bar .open-menu a {
    border: 0.2em solid #484848;
    border: 0.2em solid rgb(var(--gray-accent));
    border-radius: 10px;
    background-color: #f5f5f5;
    background-color: rgb(var(--content-bg-color));
    color: #484848;
    color: rgb(var(--gray-accent));
  }
}
#side-bar>.side-block .menu-item>a:hover:before {
  opacity: 1;
  transition: all 0.2s ease-in-out;
  animation: sl 0.7s linear forwards;
}
#side-bar>.side-block .menu-item>a:before {
  position: absolute;
  bottom: 0;
  right: 0;
  bottom: 0;
  left: 0;
