content:
[[include :scp-wiki-cn:tabdecoration]]
[[module CSS]]

@import url('https://fonts.googleapis.com/css2?family=Noto+Serif+SC:wght@500;900&display=swap');

#login-status {
    color: gray;
    font-size: 90%;
    z-index: 30;
}
#login-status a {
    background: transparent;
    color: gray;
}
#login-status ul a {
    color: gray;
    background: transparent;
}

#account-topbutton {
    background: #ccc;
    color: #700;
}

* {
font-family: 'Noto Serif SC', serif;
}
h1 {
font-weight: 900;
}
/* HEADER */
#header {
    background: none;
}
div#container-wrap {
    background: url(http://scp-jp-storage.wikidot.com/local--files/file:6611312-100-5u9z/Crest.svg);
    background-repeat: no-repeat;
    background-position: left top;
    background-size: 60%;
}
#header h1 a {
    display: none;
}
#header h2 span {
    display: none;
}
#search-top-box {
    display: none;
}

/* TOP MENU */

#top-bar a {
    color: #666;
    background-color: rgba(255, 255, 255, 0.3);
    -webkit-backdrop-filter: blur(10px);
    backdrop-filter: blur(10px);
}

#top-bar ul li ul {
    border: none;
    box-shadow: none;
}

#top-bar ul li a {
    border: none;
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: transparent;
    color: #666;

    border: none;
}

#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
    border: none;
}
#top-bar ul li ul {
    border: none;
}
#top-bar ul li ul a, #top-bar a:hover {
    color: #666;
}
#side-bar .side-block {
    padding: 10px;
    border: thin solid #eee;
    border-radius: 10px;
    box-shadow: none;
    background-color: rgba(255, 255, 255, 0.3);
    -webkit-backdrop-filter: blur(10px);
    backdrop-filter: blur(10px);
    margin-bottom: 15px;
}
#side-bar img {
    display: none;
}
div.yui-navset div.yui-content {
    background: #fff !important;
}
a {
color: #500000;
}
@media (max-width: 767px) {
div#container-wrap {
    background-size: contain;
}
#side-bar {
background: rgba(255, 255, 255, 0.3);
-webkit-backdrop-filter: blur(10px);
    backdrop-filter: blur(10px);
}
#side-bar .side-block {
    border: thin solid #eee;
    border-radius: 10px;
    background-color: rgba(255, 255, 255, 0.3) !important;
    -webkit-backdrop-filter: blur(10px);
    backdrop-filter: blur(10px);
}
    .open-menu a {
        border: 2px solid black !important;
        background-color: #fff !important;
        border-radius: 3px;
        color: black !important;
        padding-top: 5px
    }
#side-bar hr {
    display: none;
}
#side-bar .scpnet-interwiki-wrapper iframe.scpnet-interwiki-frame {
    background-color: #fff !important;
    border-radius: 0;
}
}
#page-content {
    background-color: rgba(255, 255, 255, 0.3);
    -webkit-backdrop-filter: blur(10px);
    backdrop-filter: blur(10px);
}
#page-content h1 {
  position: relative;
  padding: 0.25em 1em;
  border-top: solid 2px black;
  border-bottom: solid 2px black;
  color: black;
}
#page-content h1:before, #page-content h1:after {
  content: '';
  position: absolute;
  top: -7px;
  width: 2px;
  height: -webkit-calc(100% + 14px);
  height: calc(100% + 14px);
  background-color: black;
}
#page-content h1:before {
  left: 7px;
}
#page-content h1:after {
  right: 7px;
}
#page-title {
  display: none;
}
[[/module]]

[[module CSS]]
/*
    Powered on 2021/08/25 12:20:38
    [2021 Wikidot Theme]
    Created by hitsujikaip
    CC BY-SA 3.0
*/



div.page-rate-widget-box,
div.page-rate-widget-box span.rate-points,
div.page-rate-widget-box span.cancel {
    background-color: black!important;
    border: solid black 1px!important;
}
div.page-rate-widget-box span.rateup,
div.page-rate-widget-box span.ratedown {
    border: solid black!important;
    border-width: 1px 0!important;
}
div.page-rate-widget-box span.rate-points,
div.page-rate-widget-box span.cancel a,
div.page-rate-widget-box span.cancel a:hover {
    color: white;
}
div.page-rate-widget-box span.rateup,
div.page-rate-widget-box span.ratedown {
    background-color: white!important;
}
div.page-rate-widget-box span.rateup a,
div.page-rate-widget-box span.ratedown a {
    color: black!important;
}
div.page-rate-widget-box span.cancel a:hover,
div.page-rate-widget-box span.rateup a:hover,
div.page-rate-widget-box span.ratedown a:hover {
    background: transparent!important;
    color: transparent!important;
}

[[/module]]
