[[module CSS show="true"]]
@font-face {
    font-family: '평창평화체B';
    src: url('https://cdn.jsdelivr.net/gh/projectnoonnu/noonfonts_2206-02@1.0/PyeongChangPeace-Bold.woff2') format('woff2');
    font-weight: 700;
    font-style: normal;
}

@font-face {
    font-family: '조선신명조';
    src: url('https://cdn.jsdelivr.net/gh/projectnoonnu/noonfonts_20-04@1.1/ChosunSm.woff') format('woff');
    font-weight: normal;
    font-style: normal;
}

@font-face {
    font-family: '엘리스 디지털코딩체';
    src: url('https://cdn.jsdelivr.net/gh/projectnoonnu/noonfonts_220508@1.0/EliceDigitalBaeum_Regular.woff2') format('woff2');
    font-weight: 400;
    font-style: normal;
}

@supports(display:grid) {

 :root:lang(ko) {

     /* Typefaces */
     --body-font: 'turnip', '조선신명조', serif;
     --header-font: 'ivymode', '평창평화체B', sans-serif;
     --title-font: 'ivymode', '평창평화체B', sans-serif;
     --UI-font: 'turnip', '조선신명조', serif;
     --mono-font: 'pennsylvania', '엘리스 디지털코딩체', monospace;

    }
}

textarea {
   font-family: var(--mono-font);
}

input#edit-page-title.text {
   font-family: var(--title-font);
}
