[[collapsible show="+ 展开" hide="- 收起"]]

[[code type="CSS"]]
/*
    None CSS with Left Beef
    [2019 Wikidot Theme]
    Created by stormbreath.
    Modified by L200
*/

/*-------------------------------------*\
 *               SIDEBAR               *
\*-------------------------------------*/

div.scpnet-interwiki-wrapper {   /*  translations box  */
    filter: hue-rotate(110deg) invert(80%);
}

/*  PANELS  */


#side-bar img {
    filter: grayscale(100%);
    -webkit-filter: grayscale(100%);
}

#side-bar .side-block {
  background-color: #FFF;
  border-color: #000;
  border-radius: 0px;
  border-width: 2px;
  box-shadow: 1px 1px 2px rgba(235, 235, 235, 0.4);
}
#side-bar .side-block .heading {
  border-color: #000;
  color: #000;
}
.side-block .menu-item > .image {   /*  upping the brightness of the image bullets  */
  filter: brightness(1.5);
}
.side-block.resources .menu-item > .image {   /*  adjusting for resources  */
  filter: brightness(1.1);
}
#side-bar .side-block.media, #side-bar .side-block.resources {
  background-color: #1E1E1E;
}
@media (max-width: 767px) {   /*  recoloring the red mobile background  */
  #side-bar {
    background-color: rgb(79, 67, 111);
  }
}

/*  LINKS  */

#side-bar a, #side-bar a:visited, #side-bar .menu-item.inactive a {
  color: #000;
}
#side-bar a:hover {
  background-color: #4D4D4D;
  text-decoration: none;
}

a {
  color: #1E1E1E;
}
a:visited {
  color: #333333;
}
a.newpage {
  color: #6B6B6B;
}



/* REMAKE THE HEADER */

div#container-wrap {
    background: url(http://scpsandboxcn.wikidot.com/local--files/pic-trans/Olhos-wrap.png) top left repeat-x;
}


#header {
    background-image: url(http://scpsandboxcn.wikidot.com/local--files/pic-trans/Olhos-header.png);
    background-position: 0px 10px;
}

#header {   /*  white icon  */
  background-image: url(http://scpsandboxcn.wikidot.com/local--files/pic-trans/Olhos-header.png)
  background-position: 0px 10px;
  background-size: 375px 125px;
}


h1, #page-title {
    color: black;
}

#page-title {
    border-color: black;
}


div#extra-div-1 {
    height: 22px;
    width: 100%;
    top: 140px;
    position: absolute;
    background: black;
}

}
@media (max-width: 580px) and (min-width: 480px) {
    #header {
        background-position: 0.5em 4.5em;
        background-size: 66px 66px;
    }
}

@media (max-width: 479px) {
    #header {
        background-position: 0 5.5em;
        background-size: 200px;
    }
}

#header h1 a {
    color: transparent;
    text-shadow: none;
    display: none;
}

#header h2 span {
    color: transparent;
    text-shadow: none;
    display: none;
}

/* TOP-BAR */
#top-bar ul li ul {
    border: solid 1px black;
}
#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
    border-top: 1px solid white;
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: white;
    color: black;
}
#top-bar ul li ul a, #top-bar a:hover {
    color: black;
}
#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background: black;
    color: white; /* top bar hover background color */
}

/* RATING MODULE */
.page-rate-widget-box .rate-points {
    background-color: black !important;
    border: solid 1px black;
    text-transform: capitalize;
}

.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background-color: black;
    border-top: solid 1px black;
    border-bottom: solid 1px black;
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    background: transparent;
    color: white;
}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background: white;
    color: black;
}
.page-rate-widget-box .cancel {
    background-color: black !important;
    border: solid 0px black;
}
.page-rate-widget-box .cancel a {
    color: white !important;
}
.page-rate-widget-box .cancel a:hover {
    background: white !important;
    color: black !important;
}

#page-content .rate-box-with-credit-button {
    background-color: black !important;
    border: 1px solid black;
    border-radius: 5px !important;
    box-shadow: 1px 1px 3px rgba(0,0,0,.5);
}

#page-content .rate-box-with-credit-button .page-rate-widget-box {
  border: 0;
}

#page-content .rate-box-with-credit-button .page-rate-widget-box .cancel {
  border-radius: 0;
  border-color: transparent;
}

#page-content .rate-box-with-credit-button .creditButton p a {   /*  "i" border  */
  border-left-color: #000;
  border-left-width: 2px;
}

#page-content .rate-box-with-credit-button .fa-info:hover {
     color: black;
     background-color: white;
}

.close-credits, .credit-back {
    filter: grayscale(100%);
    -webkit-filter: grayscale(100%);
}

.creditButton a {
    border-left: none;
}

#page-content .modalbox {
    box-shadow: 0 2px 6px rgba(102, 102, 102, .5);
}

.close-credits,
.credit-back {
    filter: grayscale(100%);
    -webkit-filter: grayscale(100%);
}

/* PAGE ELEMENTS */
blockquote,
div.blockquote,
#toc,
.code {
     background-color: white;
}

.scp-image-block {
    border: solid 1px black;
    box-shadow: none;
}
.scp-image-block .scp-image-caption {
    background-color: white !important;
    border-top: solid 1px black !important;
    color: black;
}

/* RECOLOR THE SEARCH BAR */
#search-top-box-input {
    border-color: black;
    color: black !important;
    background-color: white;
}

#search-top-box-input:hover,
#search-top-box-input:focus {
    background-color: white;
    border-color: black;
}

#search-top-box-form input[type=submit] {
    border: solid 1px black;
    color: black;
    background-color: white;
    background: white;
}

#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    border: solid 1px black;
    background-color: white;
    background: white;
    color: black;
}


/* LOGIN STUFF */
#login-status {
    color: black;
    filter: grayscale(100%) saturate(0%);
    -webkit-filter: grayscale(100%) saturate(0%);
}
#login-status a {
    color: black;
}
#login-status ul a {
    color: black;
}
#account-topbutton {
     background: #ccc;
     color: black;
}

/* HOVER THINGIES */
.hovertip {
     background: white !important;
}

/* FOOTER  */
#footer {
    background: black;
    color: white;
}

/* ---- TABS ---- */

/* ---- YUI TAB BASE ---- */
.yui-navset .yui-nav a,.yui-navset .yui-navset-top .yui-nav a{background-color:inherit;background-image:inherit}.yui-navset .yui-nav a:hover,.yui-navset .yui-nav a:focus{background:inherit;text-decoration:inherit}.yui-navset .yui-nav .selected a,.yui-navset .yui-nav .selected a:focus,.yui-navset .yui-nav .selected a:hover{color:inherit;background:inherit}.yui-navset .yui-nav,.yui-navset .yui-navset-top .yui-nav{border-color:inherit}.yui-navset li{line-height:inherit}

/* ---- YUI TAB CUSTOMIZATION ----*/

 .yui-navset .yui-nav,
 .yui-navset .yui-navset-top .yui-nav{
     display: flex;
     flex-wrap: wrap;
     width: calc(100% - .125rem);
     margin: 0 auto;
     border-color: #000;
     box-shadow: none;
}
 .yui-navset .yui-nav a, /* ---- Link Modifier ---- */
 .yui-navset .yui-navset-top .yui-nav a{
     color: #000;
    /* ---- Tab Background Colour | [UNSELECTED] ---- */
     background-color: #f4f4f4;
     border: unset;
}
 .yui-navset .yui-nav a:hover,
 .yui-navset .yui-nav a:focus{
     color: #fff;
    /* ---- Tab Background Colour | [HOVER] ---- */
     background-color: #212121;
}
 .yui-navset .yui-nav li, /* ---- Listitem Modifier ---- */
 .yui-navset .yui-navset-top .yui-nav li{
     position: relative;
     display: flex;
     flex-grow: 2;
     max-width: 100%;
     margin: 0;
     padding: 0;
     color: #f4f4f4;
     background-color: #CECECE;
     border-color: transparent;
     box-shadow: none;
}
 .yui-navset .yui-nav li a,
 .yui-navset-top .yui-nav li a,
 .yui-navset-bottom .yui-nav li a{
     display: flex;
     align-items: center;
     justify-content: center;
     width: 100%;
}
 .yui-navset .yui-nav li em{
     border: unset;
}
 .yui-navset .yui-nav a em,
 .yui-navset .yui-navset-top .yui-nav a em{
     padding: .35em .75em;

     text-overflow: ellipsis;
     overflow: hidden;
     white-space: nowrap;
}
 .yui-navset .yui-nav .selected, /* ---- Selection Modifier ---- */
 .yui-navset .yui-navset-top .yui-nav .selected{
     flex-grow: 2;
     margin: 0;
     padding: 0;
    /* ---- Tab Background Colour | [SELECTED] ---- */
     background-color: #000;
}
 .yui-navset .yui-nav .selected a,
 .yui-navset .yui-nav .selected a em{
     border: none;
}
 .yui-navset .yui-nav .selected a{
     width: 100%;
     color: #f4f4f4;
}
 .yui-navset .yui-nav .selected a:focus,
 .yui-navset .yui-nav .selected a:active{
     color: #f4f4f4;
     background-color: #000;
}
 .yui-navset-left .yui-content{
    /* ---- Content Background ---- */
     background-color: #D8D8D8;
}
 .yui-navset .yui-content,
 .yui-navset .yui-navset-top .yui-content{
     padding: .5em;
     border: none;
}

[[/code]]

[[/collapsible]]
[[footnoteblock]]

