#page-title { display: none }

.scp-image-block.block-right img {
    background: var(--gradient-header);
    box-sizing: border-box;
    padding: 1em;
}

.fonts-display {
    font-size: 1.25em;
}

.title-font {
    font-family: var(--title-font);
    font-weight: 700;
}
.header-font {
    font-family: var(--title-font);
    font-weight: 900;
}
.UI-font {
    font-family: var(--UI-font);
}
.mono-font {
    --MONO: 1;
    --CASL: 0;
    --CRSV: 0;
    font-family: var(--mono-font);
}
[[/module]]

