[[collapsible show="+ Show theme code" hide="- Hide theme code"]]
[[code type="CSS"]]
@import url('https://fonts.googleapis.com/css2?family=Itim&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Playwrite+GB+S:ital,wght@0,100..400;1,100..400&display=swap');

:root{

    /* Intro */
    --theme-base: "nuscp"
        --theme-id: "onko-sinulla-nalka"
        --theme-name: "Onko Sinulla NÃ¤lkÃ¤?";


--black-monochrome: 78, 26, 26;
--dark-gray-monochrome: 135, 92, 82;
--gray-monochrome: 173, 116, 95;
--light-gray-monochrome: 189, 124, 104;
--very-light-gray-monochrome: 201, 148, 117;
--pale-gray-monochrome: 227, 181, 141;
--white-monochrome: 255, 225, 176;
--bright-accent: 78, 26, 26;
--medium-accent: 88, 36, 36;
--dark-accent: 98, 46, 46;
--newpage-color: 98, 46, 46;

--diagonal-stripes: url(https://scp-wiki.wdfiles.com/local--files/theme%3Aonko-sinulla-nalka-theme/PaperTextureSarkicDark.png);
--logo-image: url(https://scp-wiki.wdfiles.com/local--files/theme%3Aonko-sinulla-nalka-theme/sarkiclogo.png);

--body-font: "Itim", cursive;
--title-font: "Playwrite GB S", cursive;
--header-font:  "Playwrite GB S", cursive;
--UI-font: "Itim", cursive;

--header-title: "Onko Sinulla NÃ¤lkÃ¤?";
  --header-subtitle: "The Taste of Home.";

}

#page-title, .meta-title {
text-align: center;
}


#page-title {display: none;}

div#container-wrap {
  background-repeat: repeat-x;
  background-size: auto var(--header-height-on-desktop);
}

#main-content hr{
    display: block;
    border:0px;
    height:100px;
background-color: transparent;
background-repeat: no-repeat;
background-position: center;
background-size: 60%;
    background-image:url('https://scp-wiki.wdfiles.com/local--files/theme%3Aonko-sinulla-nalka-theme/OnkoSinullaNalkaHR.png');
}

body{
font-size: 1rem;
}

    #content-wrap {
      background-image: url(https://scp-wiki.wdfiles.com/local--files/theme%3Aonko-sinulla-nalka-theme/PaperTextureSarkic.png), url("https://scp-wiki.wdfiles.com/local--files/theme%3Aonko-sinulla-nalka-theme/OnkoSinullaNalka.png");
background-size: auto, 28%;
 background-position: left top, right top;
   background-repeat: repeat, no-repeat;
}
@media only screen and (max-width: 56.25rem) {
  #content-wrap {
    background-image: url(https://scp-wiki.wdfiles.com/local--files/theme%3Aonko-sinulla-nalka-theme/PaperTextureSarkic.png);
  }
}
.paper{
background-color: rgb(var(--white-monochrome));
border: 2px solid rgb(var(--bright-accent));
padding: 60px 30px 40px 30px;
border-radius:20px;
text-align:center;
margin: 30px 0px;
}
.paper>blockquote{
text-align:left;
}

:is(blockquote, .blockquote, div.blockquote, [class*="blockquote"]) {
  clear: both;
  background-color: transparent;
  box-shadow: none;
  border-radius: 20px;
  border: 2px solid rgb(var(--bright-accent));
}

.collapsible-block-folded, .collapsible-block-unfolded-link{
margin: 20px;
  border: 2px solid rgb(var(--bright-accent));
text-align:center;
  border-radius: 20px;
}

:where(#page-content) img{
border: 2px solid rgb(var(--white-monochrome));
border-radius:100px;
margin-top: 20px;
}
[[/code]]
[[/collapsible]]

[[/iftags]]

