[[code type="css"]]
/*
    Path Of Jinin CSS Theme
    [2022 Wikidot Theme]

    Created by c_bonefish and thd-glasses
    Base Code by Denevola

    CC BY-SA 3.0
*/


/* Header */
div#container-wrap {
    background: url('https://scpko.wikidot.com/local--files/theme:path-of-jinin/back-1.png') center top repeat-x;
    background-size: 256px;
}

div#extra-div-1, div#extra-div-2 {
    background-image: url("https://scpko.wikidot.com/local--files/theme:path-of-jinin/logo-2.png");
    background-repeat: repeat-x;
    background-size: auto 30px;

    position: absolute;
    top: 123px;

    display: block;
    width: 50%;
    height: 40px;
}

div#extra-div-1 {
    background-position: right;
    right: 0;
    transform: translateX( calc( -50vw - 35px ) );
}

div#extra-div-2 {
    background-position: left;
    left: 0;
    transform: translateX( calc( 50vw + 30px ) );
}

#header {
    height: 170px;
    background-image: url('https://scpko.wikidot.com/local--files/theme:path-of-jinin/logo-1.png');
    position: relative;
    background-repeat: no-repeat;
    background-position: center top;
    background-size: 280px auto;
}

#header h1 a {
    position: absolute;
    font-size: 100%;
    text-align: center;
    text-shadow: none;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
}

#header h1 a::before {
    content: " ";
    color: #00000000;
    width: 100%;
    height: 100%;
}

#header h2,
#header h1 a span,
#header h2 span {
    display: none;
}

/* Login & Search & Top Bar */
#login-status {
    position: absolute;
    top: 5px;
    color: #5C000D;
    font-weight: bolder;
    text-shadow: -1px 0 #EADAC9, 0 1px #EADAC9, 1px 0 #EADAC9, 0 -1px #EADAC9;
    z-index: 2;
}

#login-status a {
    color: #5C000D;
    font-weight: bolder;
    text-shadow: -1px 0 #EADAC9, 0 1px #EADAC9, 1px 0 #EADAC9, 0 -1px #EADAC9;
}

#account-topbutton {
    border-color: #5C000D;
}

#login-status ul a {
    color: #5C000D !important;
    background-color: #EADAC9;
}

#search-top-box {
    position: absolute;
    top: 30px;
    right: 0;
    width: 250px;
    text-align: right;
    z-index: 1;
}

#search-top-box-form {
    display: inline-flex;
}

#search-top-box-form > input {
    -webkit-appearance: none;
    -webkit-border-radius: 0;
    border-radius: 5px;
}

#search-top-box-input {
    background-color: #5C000D;
    border: solid 1px #000;
    color: #A05560 !important;
    background-color: #ECDDCD !important;
}

#search-top-box-input:hover,
#search-top-box-input:focus {
    box-shadow: inset 1px 1px 5px rgb(0 0 0 / 50%);
    color: #5C000D !important;
    background-color: #ECDDCD !important;
}

#search-top-box-form input[type=submit] {
    padding: 2px 5px;
    background: #5C000D;
    box-shadow: 0 1px 3px rgba(0, 0, 0, .5);
    border: solid 1px #000;
    color: #ECDDCD;
    font-size: 90%;
}

#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    background: #5C000D;
}

#top-bar {
    display: flex;
    justify-content: center;
    top: 164px;
    width: 100%;
    z-index: 20;
}

#top-bar a {
    color: #ECDDCD;
    background: none;
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: #ECDDCD;
    border-color: transparent;
    color: #5C000D;
}

#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background: #ECDDCD;
}

#top-bar ul li ul,
#top-bar ul li ul li ul {
    border-color: rgba(0, 0, 0, .4);
}

#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
    border-color: rgba(0, 0, 0, .1);
}

#top-bar ul li.sfhover ul li ul a,
#top-bar ul li:hover ul li ul a {
    border-top: none;
}

.top-bar {
    position: relative;
    margin: 0 auto;
}
.c_topbar_arrow {
    color: #C4B29F;
}

/* Page Elements */
#page-title,
.meta-title {
    color: #5C000D;
    font-size: 2.1em;
    font-weight: bold;
}

#content-wrap {
    margin: 30px auto 0;
}

h1, h2, h3, h4, h5, h6 {
    color: #5C000D;
    font-weight: bold;
}

body {
    background-image: url("https://scpko.wikidot.com/local--files/theme:path-of-jinin/back-2.png");
    background-repeat: repeat;
    background-size: 156px;
    color: #000000;
    overflow-x: hidden;
}

html {
    overflow-x: hidden;
}

a:link {
    color: #331C1F;
}

a:visited {
    color: #542F34;
}

.hovertip {
    background-color: #ECDDCD !important;
    border: 2px solid #000 !important;
    color: #5C000D !important;
}

.scp-image-block {
    background: #fff;
    box-shadow: 0 1px 3px rgba(0, 0, 0, .5);
}

.scp-image-block .scp-image-caption {
    background: #ECDDCD;
    border-top: none;
    font-size: 85%;
}

.scp-image-block img {
    box-shadow: 0 0 1px black;
}

/* ---- SCROLLBAR ---- */
::-webkit-scrollbar {
    width: unset;
    height: unset;
}

::-webkit-scrollbar-track {
    background: #ECDDCD;
}

::-webkit-scrollbar-thumb {
    background: #5C000D;
    border: none;
}

::-webkit-scrollbar-thumb:hover {
    background: #7A0012;
}

textarea::-webkit-scrollbar-track {
    background: #ECDDCD;
}

/* Side Bar */
#side-bar {
    background: none;
}

#side-bar .side-block,
#side-bar .side-block.media,
#side-bar .side-block.resources,
#interwiki .side-block {
    background: none !important;
    border: 2px solid #5C000D;
    border-radius: 0 0;
}

#side-bar .heading,
#interwiki .heading,
#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px #380008;
    color: #331C1F;
}

#side-bar .collapsible-block-folded-link .collapsible-block-link:hover,
#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    color: #7A444C;
}

#side-bar .collapsible-block-folded-link .collapsible-block-link,
#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    color: #331C1F;
}

#side-bar .menu-item .image,
#interwiki .menu-item .image,
#side-bar .collapsible-block-folded,
#side-bar .collapsible-block-unfolded-link .collapsible-block-content .image {
    filter: grayscale(80%);
}

#side-bar .collapsible-block-folded a:link {
    filter: grayscale(80%);
}

#side-bar a:link,
#interwiki a:link {
    color: #331C1F;
}

#side-bar a:visited,
#interwiki a:visited {
    color: #542F34;
}

#side-bar a:link:hover,
#side-bar a:visited:hover,
#interwiki a:link:hover,
#interwiki a:visited:hover {
    color: #7A444C;
}

#top-bar .open-menu a {
    background: #ECDDCD;
    border-color: #5C000D;
    color: #5C000D;
}

/* Tab */
div.yui-navset ul.yui-nav a,
div.yui-navset ul.yui-nav a:hover,
div.yui-navset ul.yui-nav .selected a,
div.yui-navset ul.yui-nav .selected a:hover,
div.yui-navset ul.yui-nav .selected a:focus {
    background: none;
    border: none;
}

.yui-navset {
    clear: both;
    margin-bottom: 1em;
    box-shadow: 0 1px 2px rgba(0, 0, 0, .5);
}

.yui-navset .yui-nav {
    background: #ECDDCD;
    border: 1px solid gainsboro;
    border-bottom: none;
}

.yui-navset .yui-nav li {
    margin-right: 10px !important;
}

.yui-navset .yui-nav li:first-child {
    margin-left: 1.5em;
}

.yui-navset .yui-nav li a em {
    padding: 10px .75em .25em;
    margin-bottom: -1px;
    background: none;
    border: none;
    border-bottom: 4px solid transparent;
    color: #5C000D;
    transition: .2s;
}

.yui-navset .yui-nav li a em:hover {
    background: none;
}

.yui-navset .yui-nav .selected a em,
.yui-navset .yui-nav .selected a em:hover {
    padding: 10px .75em .25em;
    background: none;
    border: none;
    border-bottom: 4px solid #D7C5B1;
    color: #5C000D;
    font-weight: bold;
}

div.yui-navset div.yui-content {
    background: #F9F1E8;
    border: 1px solid gainsboro;
}

/* Rate Module */
.page-rate-widget-box {
    margin-right: 0;
    background-color: #ECDDCD;
    border: 2px solid #5C000D;
    border-radius: 0px;
}

.page-rate-widget-box > span {
    border: none !important;
}

.page-rate-widget-box .rate-points {
    background: #ECDDCD !important;
    color: #000 !important;
}

.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background: #ECDDCD;
}

.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    color: #5C000D;
}

.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background-color: #5C000D;
    color: #ECDDCD;
}

.page-rate-widget-box .cancel {
    background: #ECDDCD;
}

.page-rate-widget-box .cancel a {
    color: #000;
    font-weight: bold;
}

.page-rate-widget-box .cancel a:hover {
    background: #5C000D;
    border-radius: 0;
    color: #ECDDCD;
}

.creditRate {
    margin-right: 0 !important;
}

.rate-box-with-credit-button {
    background: #ECDDCD !important;
    border: 2px solid #5C000D !important;
    border-radius: 0px !important;
}

.rate-box-with-credit-button .page-rate-widget-box {
    border: none;
}

.creditButton p a {
    border-left: 1px #000 !important;
    color: #000 !important;
}

#page-content .rate-box-with-credit-button .fa-info {
    padding: 3px 0 1px;
}

#page-content .rate-box-with-credit-button .fa-info:hover {
    background: #5C000D;
    border-radius: 0 0 0 0;
    color: #ECDDCD !important;
}

/* ---- EDITING WINDOWS ---- */
.owindow {
    background-color: #F7EADE;
    border-color: #5C000D;
}

.owindow .modal-header {
    background-color: #ECDDCD;
}

.owindow .modal-body img {
    background-color: #ECDDCD !important;
    color: #5C000D;
}

.owindow .title {
    background-color: #ECDDCD;
}

.owindow a,
.owindow a:visited {
    color: #F7EEE6;
}

.owindow .active {
    color: #5C000D;
}

.owindow .content.modal-body img {
    background-color: #38181C !important;
}

.owindow .button-bar a {
    background-color: #ECDDCD;
    color: #5C000D;
}

.owindow .button-bar a:hover,
.owindow .button-bar a:focus {
    background-color: #ECDDCD !important;
    border-color: #5C000D;
    color: #5C000D;
}

table.page-files .highlight {
    background-color: #FFFFFB;
}

input.text,
input.text:focus,
input.text:hover {
    background-color: #ECDDCD !important;
    border-color: #5C000D;
    color: #5C000D;
}

textarea {
    background-color: #fff;
    color: #5C000D;
}

.change-textarea-size a {
    background-color: #ECDDCD;
    color: #5C000D;
}

#lock-info {
    background-color: #ECDDCD;
}

/* Blockquote */
blockquote,
div.blockquote {
    background-color: #F7EADE;
    border-style: dotted;
}

/* etc. */
@media (max-width: 767px) {
    #side-bar {
        background: #ECDDCD;
    }
}

[[/code]]

[[/iftags]]
