[[code type="CSS"]]
/*
    Penumbra Theme
    [2020 Wikidot Theme]
    By EstrellaYoshte
    Based on:
       Paperstack Theme by EstrellaYoshte
       Ad Astra Theme by NatVoltaic and stormbreath
       Inkblot Theme by Croquembouche
       Anderson Robotics Theme by Croquembouche
       BHL Style Collapsible by Monkatraz
*/

@import url('https://fonts.googleapis.com/css2?family=Josefin+Sans:wght@700&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Roboto&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Fira+Code&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+SC:wght@100;300;400;500;700;900&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Noto+Serif+SC:wght@100;300;400;500;700;900&display=swap');

:root {
  --body-font: 'Roboto', "Noto Sans SC", Inter, BlinkMacSystemFont,'Segoe UI',Oxygen,Ubuntu,Cantarell,'Fira Sans','Droid Sans','Helvetica Neue',sans-serif;
  --header-font: 'Josefin Sans', "Noto Sans SC", Verdana, Arial, Helvetica, sans-serif;
  --mono-font: 'Fira Code', "Noto Serif SC", 'Andale Mono', 'Courier New', Courier, monospace;

  --accentColor: #1EB5E8;
  --darkColor: #2F333C; 
  --darkerColor: #21252E;
  --lgurl: url("https://scp-wiki.wdfiles.com/local--files/theme%3Apenumbra/logo.png");
}

body {
    font-family: var(--body-font);
    font-size: .9325rem;
    color: #EDEDED;
    background-color: var(--darkColor);
    background-image: linear-gradient(
        to bottom,
        var(--darkerColor), var(--darkerColor) 90px,
        var(--darkerColor) 90px, var(--darkColor) 200px,
        var(--darkColor) 200px, var(--darkColor) 100%);
    background-repeat: no-repeat;
    accent-color: var(--accentColor);
}

#content-wrap {
    margin-top: 1.25rem;
}

input { color-scheme: dark; }

/* ---- SCROLLBAR ---- */

::-webkit-scrollbar {
  width: .625rem;
  height: .625rem;
  background: transparent;
}

::-webkit-scrollbar-track {
  background: var(--darkerColor);
}

::-webkit-scrollbar-thumb {
  background: var(--accentColor);
  border: none;
}

:root {
    scrollbar-color: var(--accentColor) var(--darkerColor);
    scrollbar-width: thin;
}

/* ---- HEADER ---- */

div#container-wrap {
     background-image: none;
}
#header {
    background-image: none;
    height: 8.75rem;
}

div#extra-div-1 {
    height: 164px;
    width: 100%;
    top: 0;
    position: absolute;
    background: var(--lgurl);
    background-size: contain;
    background-repeat: no-repeat;
    background-position: 50% 50%;
    z-index: -1;
}

#header h1, #header h2 { margin-left: 0; float: none; text-align: center; }
/* Move the subtitle down a smidge */
#header h2 { margin-top: 0.45rem; }
#header h1 a {
    margin: auto;
    width: max-content;
}
/* Hide the existing text */
#header h1 span, #header h2 span { font-size: 0; display: none; }
/* Style the new text */
#header h1 a::before, #header h2::before {
  color: var(--accentColor);
  font-family: var(--header-font);
  text-shadow: none;
}
/* Set the new text's content from variable */
#header h1 a::before {
  content: var(--header-title, "SCP基金会");
  font-weight: 300;
  font-size: 1.3em;
}
#header h2::before {
  content: var(--header-subtitle, "控制 - 收容 - 保护");
  font-weight: 600;
  font-size: 1.22em;
}

#login-status {
    color: #ededed;
    width: 100%;
    text-align: right;
    z-index: 9;
}
#login-status a {
    color: var(--accentColor);
}

#account-topbutton {
    border: none;
    margin-left: 0.35em;
    padding: 0 0.45em;
    font-size: 1em;
    position: relative;
}
#account-topbutton::before {
    content: "";
    position: absolute;
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    background-color: var(--accentColor);
    opacity: 0.125;
}

#account-options {
    width: 6.25rem;
    background: var(--darkerColor);
    border: none;
    border-right: solid 3px var(--accentColor);
}
#account-options::before,
#account-options::after {
    content: "";
    position: absolute;
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    pointer-events: none;
}
#account-options::before {
    background: rgba(0,0,0, .4);
}
#account-options::after {
    background-color: var(--accentColor);
    mix-blend-mode: overlay;
}
#account-options > ul {
    position: relative;
    z-index: 1;
}
#account-options ul a {
    color: var(--accentColor);
    background-color: transparent;
    font-weight: bold;
    letter-spacing: 0.01em;
    padding: 0.25em 0.5em;
    text-align: right;
}
#account-options ul a:hover {
    color: var(--darkerColor);
    background: var(--accentColor);
}

#footer {
    background: transparent;
    color: #ededed;
    font-size: 0.625rem;
    margin-top: 0.25em;
    padding: 0.25em 0.35em;
    display: flex!important;
    align-items: center;
    justify-content: flex-end;
    flex-wrap: wrap;
}
#footer > a[href*="www.wikidot.com"] {
    color: #ededed;
    padding: 0 0.85ch 0 0.5ch;
    border-right: 1px solid currentColor;
}
#footer .options {
    float: none;
    order: 2;
    width: max-content;
    font-size: 0;
}
#footer .options a {
    color: #ededed;
    font-size: 0.625rem;
    padding: 0 0.85ch;
    border-right: 1px solid currentColor;
}
#footer .options a:last-child {
    border-right: none;
    padding-right: 0;
}

#license-area {
    color: #ededed;
    background-color: var(--darkerColor);
    border-top: solid 0.1rem var(--accentColor);
}

#footer-bar {
    border: none;
    padding: 0 0 1em;
    margin: 0;
    max-width: 100%;
    background-color: var(--darkerColor);
}
#footer-bar .units {
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
}

#search-top-box {
    top: 2.2rem!important;
    right: 8px;
    z-index: 8;
}
#search-top-box-form > input[type=submit] {
    border: solid 1px #ededed;
    background: var(--darkerColor)!important;
    box-shadow: none;
    border-radius: 0;
    color: #ededed;
    transition: color 0.15s linear;
}
#search-top-box-form input[type=submit]:hover {
    border: solid 1px var(--accentColor);
    box-shadow: none;
    color: var(--accentColor);
    appearance: none;
}
#search-top-box-form > input[type=text] { display: none; }

/* ---- TOP BAR ---- */

#top-bar {
     top: 8.4rem;
     display: flex;
     justify-content: center;
     right: 0;
}
@media (max-width: 768px) {
    .mobile-top-bar {
         display: flex;
         justify-content: center;
         max-width: 100%;
         width: 100%;
         left: 0;
    }
}
#top-bar, #top-bar a {
     color: #ededed;
     transition: color 0s;
}
#top-bar ul li ul,
#top-bar ul li ul li ul {
    border-color: var(--accentColor);
    box-shadow: none;
}
#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
   border-top-color: var(--darkColor);
}
#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: var(--darkerColor);
    color: #ededed;
}
#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background: var(--darkerColor); /* top bar hover background color */
    color: var(--accentColor);
    transition: color 0.1s linear;
}

/* ---- SIDE BAR ---- */

div#side-bar {
    background: var(--darkerColor);
    clear: both;
    width: 19em;
    padding:0;
    border-radius: 0;
    box-shadow: none;
    overflow-x: hidden;
    direction: rtl;
}

#side-bar .side-block,
#interwiki .side-block {
    border: transparent;
    border-radius: 0;
    padding: 5px 15px;
    box-shadow: none;
    background-color: transparent;
    direction: ltr;
}

/* Commented for not using in CN
#side-bar .side-block.media {
    position: relative;
    background-color: var(--darkerColor);
    margin: 0.75em 0;
}
#side-bar .side-block.media::before,
#side-bar .side-block.media::after {
    content: "";
    position: absolute;
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    pointer-events: none;
}

#side-bar .side-block.media::before {
    background: rgba(0,0,0, .4);
}
#side-bar .side-block.media::after {
    background-color: var(--accentColor);
    mix-blend-mode: overlay;
}

#side-bar .side-block.media > * {
    display: flex;
    justify-content: space-evenly;
    position: relative;
    z-index: 1;
}

#side-bar .side-block.resources {
    background-color: transparent;
    position: relative;
    margin-bottom: 0.25em;
    padding: 0.75em 15px 0.5em;
}

#side-bar .side-block.resources::before {
    content: "";
    position: absolute;
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    pointer-events: none;
    z-index: -1;
    background-color: var(--accentColor);
    opacity: 0.125;
}
*/
#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px #EDEDED; /* Altered content for CN */
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    color: #EDEDED; /* Altered content for CN */
    font-size: 0.94rem; /* Altered content for CN */
}
#side-bar .collapsible-block-folded .collapsible-block-link,
#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    font-family: var(--header-font);
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    color: #ffffff;
}

/* Altered content for CN */
#side-bar .collapsible-block-folded {
    background: none;
}
#side-bar .collapsible-block-folded .collapsible-block-link {
    margin-left: 0;
}
/* End */

#side-bar .menu-item img, #interwiki .menu-item img {display: none; }

#side-bar .heading,
#interwiki .heading {
    color: #ededed;
    font-family: var(--header-font);
    font-size: 0.94rem;
    border-bottom: solid 1px #ededed;
    margin-top: 1.25em;
}

#top-bar div.open-menu a {
    border-radius: 0;
    box-shadow: none;
    color: var(--accentColor);
    background-color: var(--darkerColor);
    border: solid 1px var(--darkerColor);
}

/* ---- Interwiki ---- */
iframe.scpnet-interwiki-frame, div.scpnet-interwiki-wrapper {
    position: relative;
    width: auto;
}
div.scpnet-interwiki-wrapper {
    margin: 0 -5px 0.75em;
    background-color: var(--darkerColor);
}
.scpnet-interwiki-wrapper::before,
.scpnet-interwiki-wrapper::after {
    content: "";
    position: absolute;
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    pointer-events: none;
}
.scpnet-interwiki-wrapper::before {
    background: rgba(0,0,0, .4);
}
.scpnet-interwiki-wrapper::after {
    background-color: var(--accentColor);
    mix-blend-mode: overlay;
}

#interwiki {
    --accentColor: #888888;
    background: transparent;
}
#interwiki body {
    background: transparent;
}

#interwiki .side-block {
    margin-top: 0;
    padding: 0 15px;
}
#interwiki .side-block a,
#interwiki .side-block a:visited {
    color: var(--accentColor);
}
#interwiki .side-block a:hover,
#interwiki .side-block a:visited:hover {
    color: #ffffff;
}
#interwiki .heading {
    color: var(--accentColor);
    border-bottom-color: currentColor;
}
#interwiki .menu-item img {
    display: none;
}
/* ------------------- */

@media (max-width: 767px) {
    #main-content {
        padding: 0;
        margin: 0 5%;
        border-left: none;
    }
    #page-title {
        margin-top: 0.7em;
    }
    #side-bar {
        background-color: var(--darkerColor);
        left: -19em;
    }
    #side-bar:target {
        width: 19em;
        border: none;
        box-shadow: none;
    }
    #side-bar .close-menu {
        transition: width 0.5s ease-in-out 0.1s,
                    opacity 1s ease-in-out 0s;
        display: block;
        position: fixed;
        width: 100%;
        height: 100%;
        top: 0;
        right: 0; left: auto;
        background: rgba(0,0,0,0.3);
        background-position: 19em 50%;
        z-index: -1;
        opacity: 0;
        pointer-events: none;
    }
    #side-bar:target .close-menu {
        width: calc(100% - 19em);
        right: 0;
        left: auto;
        opacity: 1;
        pointer-events: auto;
    }
    #side-bar {
        top: 0;
    }
    #search-top-box {
        top: 107px;
    }
}

/* ---- TABS ---- */

/* ---- YUI TAB BASE ---- */
.yui-navset .yui-nav a,.yui-navset .yui-navset-top .yui-nav a{background-color:inherit;background-image:inherit}.yui-navset .yui-nav a:hover,.yui-navset .yui-nav a:focus{background:inherit;text-decoration:inherit}.yui-navset .yui-nav .selected a,.yui-navset .yui-nav .selected a:focus,.yui-navset .yui-nav .selected a:hover{color:inherit;background:inherit}.yui-navset .yui-nav,.yui-navset .yui-navset-top .yui-nav{border-color:inherit}.yui-navset li{line-height:inherit}

/* ---- YUI TAB CUSTOMIZATION ----*/

 .yui-navset .yui-nav,
 .yui-navset .yui-navset-top .yui-nav{
     display: flex;
     flex-wrap: wrap;
     width: calc(100% - .125rem);
     margin: 0 auto;
     border-color: var(--accentColor);
     box-shadow: none;
}
 .yui-navset .yui-nav a, /* ---- Link Modifier ---- */
 .yui-navset .yui-navset-top .yui-nav a{
     color: #ededed;
     font-weight: bold;
    /* ---- Tab Background Colour | [UNSELECTED] ---- */
     background-color: var(--darkerColor);
     border: unset;
     box-shadow: none;
     box-shadow: none;
     transition: background-color 0.15s linear;
}
 .yui-navset .yui-nav a:hover,
 .yui-navset .yui-nav a:focus{
     color: #ededed;
    /* ---- Tab Background Colour | [HOVER] ---- */
     background-color: var(--accentColor);
}
 .yui-navset .yui-nav li, /* ---- Listitem Modifier ---- */
 .yui-navset .yui-navset-top .yui-nav li{
     position: relative;
     display: flex;
     flex-grow: 2;
     max-width: 100%;
     margin: 0;
     padding: 0;
     color: #ededed;
     background-color: var(--darkerColor);
     border-color: transparent;
     box-shadow: none;
}
 .yui-navset .yui-nav li a,
 .yui-navset-top .yui-nav li a,
 .yui-navset-bottom .yui-nav li a{
     display: flex;
     align-items: center;
     justify-content: center;
     width: 100%;
}
 .yui-navset .yui-nav li em{
     border: unset;
}
 .yui-navset .yui-nav a em,
 .yui-navset .yui-navset-top .yui-nav a em{
     padding: .35em .75em;

     text-overflow: ellipsis;
     overflow: hidden;
     white-space: nowrap;
}
 .yui-navset .yui-nav .selected, /* ---- Selection Modifier ---- */
 .yui-navset .yui-navset-top .yui-nav .selected{
     flex-grow: 2;
     margin: 0;
     padding: 0;
    /* ---- Tab Background Colour | [SELECTED] ---- */
     background-color: var(--accentColor);
}
 .yui-navset .yui-nav .selected a,
 .yui-navset .yui-nav .selected a em{
     border: none;
}
 .yui-navset .yui-nav .selected a{
     width: 100%;
     color: var(--darkerColor);
     font-weight: bold;
}
 .yui-navset .yui-nav .selected a:focus,
 .yui-navset .yui-nav .selected a:active{
     color: var(--darkerColor);
     background-color: var(--accentColor);
}
 .yui-navset .yui-content {
    background-color: var(--darkerColor);
    box-shadow: none;
}
 .yui-navset .yui-content,
 .yui-navset .yui-navset-top .yui-content{
     padding: .5em;
     border: none;
}

/*---- TAB ANIMATION by Croquembouche ---- */

.yui-navset .yui-content > div {
    display: block;
    top: 0;
    overflow: hidden;
    transform-origin: 0 0;
}

#page-content .yui-navset .yui-content > div[style*="none"] {
    display: block !important;
    flex: 0;
    max-height: 0;
    padding: 0 0.5em;
    border-width: 0;
    /* The following transition affects the one that DISAPPEARS */
    transition: padding 0s linear 0.5s,
          border-width 0s linear 0.5s,
          flex 0.5s cubic-bezier(.18,.51,.54,.9) 0s;
    animation: tab-disappear 0.5s ease-in-out 0s 1 both;
}
#page-content .yui-navset .yui-content > div[style*="block"] {
    display: block !important;
    flex: 1;
    max-height: 9999rem;
    /* The following transition affects the one that APPEARS */
    transition: padding 0s linear 0.5s,
          border-width 0s linear 0.5s,
          flex 0.5s cubic-bezier(.18,.51,.54,.9) 0.5s;
    animation: tab-appear 0.5s ease-in-out 0.5s 1 both;
}

@keyframes tab-disappear {
    0% { max-height: 9999rem; }
    1% { max-height: 100vh; }
    100% { max-height: 0; }
}
@keyframes tab-appear {
    0% { max-height: 0; }
    99% { max-height: 100vh; }
    100% { max-height: 9999rem; }
}

/* ---- INFO BAR ---- */
 body{
     --barColour: var(--darkerColor);
     --linkColour: #ededed;
}

 .info-container .collapsible-block-content {
    padding: 0 .5em 30px;
}
#page-content .info-container > .collapsible-block .collapsible-block-link {
