[[module CSS]]
@import url(https://scpko.wdfiles.com/local--code/theme%3Apet/1);
[[/module]]
이 변종 테마는 2014년 만우절 이벤트인 "매우 귀여운 애완동물!"에 사용되었습니다.
----
[[code type="css"]]
@import url('https://scpko.wdfiles.com/local--files/unfont/UnPilgiBold.css');

/*
    Super Cute Pets!
    [2014 Wikidot Theme]
    Created for the SCP Foundation by Aelanna
    Edited for SCP財団 by Devanos
*/

/* COMMON */
#content-wrap {
	position: relative;
	margin: 2em auto 0;
	max-width: 1040px;
	min-height:1700px;
	height: auto !important;
	height: 2000px;
}

h1, #page-title {
	font-family: 'Comic Sans MS', 'UnPilgiBold', cursive !important;
	color: #909;
	padding: 0 0 0.25em;
	margin: 0 0 0.6em;
	font-weight: normal;
}
h1 {
	margin-top: 0.7em;
	padding: 0;
	font-weight: bold;
}
h2, h3, h4, h5, h6, h7 {
	margin: 0.5em 0 0.4em;
	padding: 0;
	letter-spacing: 1px;
}
#page-title {
	border-color: #bbb;
}
ul {
	/*list-style-image: url(../images/bullet.gif);*/
	list-style: square;
}
#top-bar ul {
/*	list-style-image: none; /* IE ONLY! IF list-style-image IS SET */
}

li, p {
	line-height: 141%;
}
a {
	color: #b0b;
	text-decoration: none;
	background: transparent;
}
a.newpage {
	color: #d6d;
	text-decoration: none;
	background: transparent;
}
a:hover {
	text-decoration: underline;
	background: transparent;
}

/* GLOBAL WIDTH */
#header, #top-bar {
	width: 920px;
	margin: 0 auto;
}

body {
	background-color: #fff;
	font-size: 0.80em;
	color: #333;
}
div#container-wrap {
	background: url(https://scp-jp.wdfiles.com/local--files/component:theme/body_bg_af.png) top left repeat-x;
}

/* HEADER */
#header {
	height: 140px;
	position: relative;
	z-index: 10;
	padding-bottom: 22px; /* FOR MENU */
	background: url(https://scp-jp.wdfiles.com/local--files/component:theme/logo-af.png) 10px 40px no-repeat;
}
#search-top-box {
	position: absolute;
	top: 79px;
	right: 9px;
	width: 250px;
	text-align: right;
}
#search-top-box-input {
	border: solid 1px #699;
	border-radius: 5px;
	color: #ccc;
	background-color: #303;
	box-shadow: inset 1px 1px 3px rgba(0,0,0,.5);
}
#search-top-box-input:hover,
#search-top-box-input:focus {
	border: solid 1px #fff;
	color: #fff;
	background-color: #606;
	box-shadow: inset 1px 1px 3px rgba(0,0,0,.8);
}
#search-top-box-form input[type=submit] {
	border: solid 1px #699;
	border-radius: 5px;
	padding: 2px 5px;
	font-size: 90%;
	font-weight: bold;
	color: #ccc;
	background-color: #636;
	background: linear-gradient(to bottom, #969,#606,#303);
	box-shadow: 0 1px 3px rgba(0,0,0,.5);
	cursor: pointer;
}
#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
	border: solid 1px #fff;
	color: #fff;
	text-shadow: 0 0 1px rgba(255,255,255,.25)
	background-color: #969;
	background: linear-gradient(to bottom, #c9c,#969,#636);
	box-shadow: 0 1px 3px rgba(0,0,0,.8);
}

#login-status {
	color: #aaa;
	font-size: 90%;
	z-index: 30;
}
#login-status a {
	background: transparent;
	color: #ddd;
}
#login-status ul a {
	color: #700;
	background: transparent;
}

#account-topbutton {
	background: #ccc;
	color: #700;
}

.printuser img.small {
	margin-right: 1px;
}

#header h1 {
	margin-left: 120px;
	padding: 0;
	float: left;
	max-height: 95px;
}
#header h2 {
	margin-left: 120px;
	padding: 0;
	clear: left;
	float: left;
	font-size: 105%;
	max-height: 38px;
}

#header h1 a {
	display: block;
	margin: 0;
	padding: 80px 0 25px;
	line-height: 0px;
	max-height: 0px;
	color: #eee;
	background: transparent;
	font-family: 'Comic Sans MS', 'UnPilgiBold', cursive !important;
	font-size: 180%;
	text-decoration: none;
	text-shadow: 3px 3px 5px #000;
	letter-spacing: 0.9px;
}

#header h2 span {
	display: block;
	margin: 0;
	padding: 19px 0;
	line-height: 0px;
	max-height: 0px;
	font-family: 'Comic Sans MS', 'UnPilgiBold', cursive !important;
	font-weight: bold;
	color: #f0f0c0;
	text-shadow: 1px 1px 1px #000;
	text-shadow: 1px 1px 1px rgba(0,0,0,.8);
}

/* TOP MENU */
#top-bar {
	position: absolute;
	top: 140px;
	left: 0;
	height: 21px;
	line-height: 18px;
	padding: 0;
	z-index: 20;
	font-size: 90%;
   max-width: 100%;
}
#top-bar ul {
	float: right;
}
#top-bar a {
	color: #fff;
	background: transparent;
}
#top-bar ul li {
	border: 0;
}
#top-bar ul li ul {
	border: solid 1px #666;
	box-shadow: 0 2px 6px rgba(0,0,0,.5);
	border-top: 0;
}
#top-bar ul li a {
	border-left: solid 1px rgba(64,64,64,.01);
	border-right: solid 1px rgba(64,64,64,.01);
	text-decoration: none;
	padding-top: 10px;
	padding-bottom: 10px;
	line-height: 1px;
	max-height: 1px;
	overflow: hidden;
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
	background: #eee;
	color: #a01;
	border-left: solid 1px rgba(64,64,64,1);
	border-right: solid 1px rgba(64,64,64,1);
}

#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
	border-width: 0;
	width: 150px;
	border-top: 1px solid #ddd;
	line-height: 160%;
	height: auto;
	max-height: none;
	padding-top: 0;
	padding-bottom: 0;
}
#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
	background: #fff;
	text-decoration: none;
}
#top-bar ul li ul {
	border-width: 0 1px 1px 1px;
	width: auto;
}
#top-bar ul li ul li, #top-bar ul li ul li.sfhover,
#top-bar ul li ul li, #top-bar ul li ul li:hover {
	border-width: 0;
}
#top-bar ul li ul li a {
	border-width: 0;
}
#top-bar ul li ul a, #top-bar a:hover {
	color: #a01;
}

/* IE7 HACK */
#top-bar ul > li > ul {  
	*margin-top: -4px;
}

/* SIDE MENU */
#side-bar {
	clear: none;
	float: none;
	position: absolute;
	top: 0.5em;
	left: 2em;
	width: 17em;
	padding: 0;
}
#side-bar .side-block {
	padding: 10px;
	border: 1px solid #660066;
	border-radius: 10px;
	box-shadow: 0 2px 6px rgba(102,0,102,.5);
	background: #fff;
	margin-bottom: 15px;
}
#side-bar .side-area {
	padding: 10px;
}
#side-bar .heading {
	color: #606;
	border-bottom: solid 1px #606;
	padding-left: 15px;
	margin-top: 10px;
	margin-bottom: 5px;
	font-size: 8pt;
	font-weight: bold;
}
#side-bar p {
	margin: 0;
}
#side-bar div.menu-item {
	margin: 2px 0;
}
#side-bar div.menu-item img {
	width: 13px;
	height: 13px;
	border: 0;
	margin-right: 2px;
	position: relative;
	bottom: -2px;
}
#side-bar div.menu-item a {
	font-weight: bold;
}
#side-bar div.menu-item.inactive img {
	opacity: 0.25;
}
#side-bar div.menu-item.inactive a {
	color: #999;
}
#side-bar div.menu-item .sub-text {
	font-size: 80%;
	color: #666;
}
#side-bar div.menu-item.sub-item {
}

#side-bar .collapsible-block-folded {
	background: url(https://scp-jp.wdfiles.com/local--files/nav:side/expand.png) 0 2px no-repeat;
}
#side-bar .collapsible-block-link {
	margin-left: 15px;
	font-weight: bold;
}
#side-bar .collapsible-block-unfolded-link {
	border-bottom: solid 1px #606;
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
	margin-top: 10px;
	margin-bottom: 5px;
	font-size: 8pt;
	color: #606;
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
	color: #b0b;
	text-decoration: none;
}
/* CONTENT */
#main-content {
	margin: 0 4em 0 23em;
	padding: 1em;
	position: relative;
}

#breadcrumbs {
	margin: -1em 0 1em;
	font-weight: 85%;
}

/* YUI-TABS */
.yui-navset .yui-content{
	background-color: #f5f5f5;
}
.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
	background-color:#d8d8d8;
	background-image: url(../images/yuitabs.png);
}
.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus, /* no focus effect for selected */
.yui-navset .yui-nav .selected a:hover { /* no hover effect for selected */
	background:#700 url(../images/yuitabs.png) repeat-x left -1400px; /* selected tab background */
	color:#fff;
}
.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus {
	background:#d88 url(../images/yuitabs.png) repeat-x left -1300px;
	text-decoration: none;
}
.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
	border-color: #444;
}
.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
	border-color: #444;
}
.yui-navset li {
	line-height: normal;
}

/* FOOTER */
#footer {
	clear: both;
	font-size: 77%;
	background: #424;
	color: #bbb;
	margin-top: 15px;
	padding: 3px 10px;
}
#footer .options {
	visibility: visible;
	display: block;
	float: right;
	width: 50%;
	font-size: 100%;
	text-align: right;
}
#footer a {
	color: #fff;
	background: transparent;
}

/* SOME NICE BOXES */
div.sexy-box {
	background: #eee;
	border: 1px solid #ccc;
	padding: 0 10px 12px;
	margin: 7px 4px 12px;
	overflow: hidden;
}
div.sexy-box div.image-container img {
	margin: 5px;
	padding: 2px;
	border: 1px solid #999;
}

/* Custom page content classes */
#page-content {
	min-height: 720px;
}

.unmargined > p {
	margin: 0;
	line-height: 100%;
}
.content-panel {
	border: solid 1px #308088;
	border-radius: 10px;
	background-color: #339099;
	margin: 10px 0 15px;
	box-shadow: 3px 3px 6px #bbb;
	box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.1);
}
.content-panel.standalone {
	background: #fdfbfd;
}
.content-panel.series {
	padding: 0 20px;
	margin-bottom: 20px;
}
.content-panel.centered {
	text-align: center;
}
.content-panel.left-column {
	float: left;
	width: 48%;
}
.content-panel.right-column {
	float: right;
	width: 48%;
}

.content-panel .panel-heading {
	padding: 2px 10px;
	color: #ffffff;
	font-size: 90%;
	font-weight: bold;
	text-shadow: 1px 1px 2px rgba(0,0,0,.35);
}
.content-panel .panel-heading > p,
.content-panel .panel-footer > p {
	margin: 0;
}
.content-panel .panel-body {
	padding: 5px 10px;
	background: #f0fff0;
}
.content-panel .panel-footer {
	padding: 1px 10px;
	color: #fffff0;
	font-size: 80%;
	font-weight: bold;
	text-align: right;
	text-shadow: 1px 1px 2px rgba(0,0,0,.5);
}
.content-panel .panel-footer a {
	color: #ffffff;
}
.content-panel .content-toc {
	float: right;
	padding: 0 20px;
	background-color: #fff;
	border: solid 1px #ccc;
	border-radius: 10px;
	margin: 20px 0 5px 5px;
	white-space: nowrap;
	box-shadow: inset 1px 2px 6px rgba(0,0,0,.15)
}
.alternate:nth-child(even) {
	background-color: rgba(255,255,255,.9);
}

/* Page Rating Module Customizations */
.page-rate-widget-box {
  display: inline-block;
  border-radius: 5px;
  box-shadow: 1px 1px 3px rgba(0,0,0,.5);
  margin-bottom: 10px;
  margin-right: 2em;
}
.page-rate-widget-box .rate-points {
  background-color: #636 !important;
  border: solid 1px #636;
  border-right: 0;
  border-radius: 5px 0 0 5px;
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
  background-color: #fff6f0;
  border-top: solid 1px #636;
  border-bottom: solid 1px #636;
  font-weight: bold;
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
  background: transparent;
  color: #636;
  padding: 0 4px;
  margin: 0 1px;
}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
  background: #636;
  color: #fffff0;
  text-decoration: none;
}
.page-rate-widget-box .cancel {
  background-color: #636;
  border: solid 1px #636;
  border-left: 0;
  border-radius: 0 5px 5px 0;
}
.page-rate-widget-box .cancel a {
  background: transparent;
  text-transform: uppercase;
  color: #969;
}
.page-rate-widget-box .cancel a:hover {
  border-radius: 0 3px 3px 0;
  background: #636;
  color: #fffff0;
  text-decoration: none;
}

/* Heritage Collection Rating Module */
.heritage-rating-module {
  float: right;
  background-color: #636;
  padding: 2px 8px 2px 5px;
  margin-bottom: 10px;
  margin-right: 2em;
  border: solid 1px #ccc066;
  border-radius: 8px;
  box-shadow: 0 1px 3px rgba(0,0,0,.25);
}
.heritage-rating-module .page-rate-widget-box {
  float: right;
  box-shadow: none;
  margin-bottom: 0;
  margin-right: 0;
}
.heritage-rating-module .heritage-emblem {
  float: right;
  position: relative;
  top: -2px;
  left: 2px;
  height: 16px;
  width: 16px;
  overflow: visible;
  margin-right: 2px;
}
.heritage-rating-module .heritage-emblem img {
  width: 20px;
  height: 20px;
  border: 0;
}

/* Fixes for Multi-Line Page Tags */
#main-content .page-tags {
	margin: 1em 0 0;
	padding: 0;
}
#main-content .page-tags span {
	display: inline-block;
	padding: 0;
	max-width: 60%;
}
#main-content .page-tags a {
	display: inline-block;
	white-space: nowrap;
}

/* Standard Image Block */
.scp-image-block {
  float: right;
  clear: right;
  margin: 0 2em 1em 2em;
  border: solid 1px #666;
  box-shadow: 0 1px 6px rgba(0,0,0,.25);
  width: 300px;
}
.scp-image-block.block-left {
  float: left;
  clear: left;
  margin: 0 2em 1em 0;
}
.scp-image-block img {
  border: 0;
  width: 300px;
}
.scp-image-block .scp-image-caption {
  background-color: #eee;
  border-top: solid 1px #666;
  padding: 2px 0;
  font-size: 80%;
  font-weight: bold;
  text-align: center;
  width: 300px;
}
.scp-image-block > p {
  margin: 0;
}
.scp-image-block .scp-image-caption > p {
  margin: 0;
  padding: 0 10px;
}

/* Forum Customizations */
.forum-thread-box .description-block {
  padding: .5em 1em;
  border-radius: 10px;
  box-shadow: 0 1px 5px rgba(0,0,0,.15),
	inset 0 1px 0 rgba(255,255,255,.8),
	inset 0 10px 5px rgba(255,255,255,.25),
	inset 0 -15px 30px rgba(0,0,0,.1)
}
.thread-container .post .head {
	padding: 0.5em 1em;
	background-color: #eee;
	background: linear-gradient(to right, #eee, #eeecec);
	box-shadow: inset 2px 3px 6px rgba(0,0,0,.15);
	border-radius: 5px 5px 0 0;
}

/* Hide Forum Signatures */
.signature {
  display:none !important;
}

/* Responsive Web Design */
img, embed, video, object, iframe, table {
	max-width: 100%;
}

#page-content div, #page-content div table {
	max-width: 100%;
}

.owindow {
	max-width: 99%;
}

/* Mobile Media Query */
@media (max-width: 479px) { 
#search-top-box-input {
	width: 5em;
}

#page-content {
	font-size: 0.9em;
}

#main-content {
	margin: 0;
}

#recent-posts-category {
	width: 100%;
}

#header, .mobile-top-bar {
	max-width: 90%;
}

#side-bar {
	width: 80%;
	position: relative;
}

.top-bar {
	display:none;
}

.mobile-top-bar {
	display: block;
	padding: 0;
}

.mobile-top-bar ul {
	display: block;
}

.mobile-top-bar li a {
	display: block;
}

.mobile-top-bar li ul li {
	display: block;
}

.page-options-bottom a {
	padding: 0 4px;
}

#header h1 a {
	font-size: 100%;
}

blockquote {
	margin: 1em 0;
}

.license-area {
	font-size: 0.8em;
}

#header {
	background-position: 0 5.5em;
	background-size: 55px 55px;
}

#header h1, #header h2 {
	margin-left: 66px;
}

table.form td, table.form th {
	float: left;
}
}

/* Note Media Query */
@media (min-width: 480px) and (max-width: 580px) { 
#search-top-box-input {
	width: 7em;
}

#main-content {
	margin: 0 2em 0 2em;
}

#header, .mobile-top-bar {
	max-width: 90%;
}

#side-bar {
	width: 80%;
	position: relative;
}

.top-bar {
	display:none;
}

.mobile-top-bar {
	display: block;
}

.mobile-top-bar ul {
	display: block;
}

.mobile-top-bar li a {
	display: block;
}

.mobile-top-bar li ul li {
	display: block;
}

.page-options-bottom a {
	padding: 0 5px;
}

#header h1 a {
	font-size: 120%;
}

blockquote {
	margin: 0.5em;
}

.license-area {
	font-size: 0.85em;
}

#header {
	background-position: 0.5em 4.5em;
	background-size: 66px 66px;
}

#header h1, #header h2 {
	margin-left: 80px;
}
}

/* Mini Tablet Media Query */
@media (min-width: 581px) and (max-width: 767px) { 
#search-top-box-input {
	width: 8em;
}

#side-bar {
	width: 80%;
	position: relative;
}

#main-content {
	margin: 0 3em 0 2em;
}

#header, .mobile-top-bar {
	max-width: 90%;
}

.top-bar {
	display: none;
}

.mobile-top-bar {
	display: block;
}

.mobile-top-bar ul {
	display: block;
}

.mobile-top-bar li a {
	display: block;
}

.mobile-top-bar li ul li {
	display: block;
}

.page-options-bottom a {
	padding: 0 6px;
}

#header h1 a {
	font-size: 140%;
}

.license-area {
	font-size: 0.9em;
}

#header {
	background-position: 1em 4em;
	background-size: 77px 77px;
}

#header h1, #header h2 {
	margin-left: 93px;
}
}

/* Tablet Media Query */
@media (min-width: 768px) and (max-width: 979px) {
#main-content {
	margin: 0 4em 0 23em;
}

#top-bar {
	right: 10%
}

#header, #top-bar #side-bar {
	max-width: 100%;
}

.top-bar {
	display:block;
}

.mobile-top-bar {
	display: none;
}

.top-bar li {
	margin: 0;
}

.top-bar ul li.sfhover ul li a,
.top-bar ul li:hover ul li a {
	width: 100px;
}

.page-options-bottom a {
	padding: 0 7px;
}

#header h1 a {
	font-size: 160%;
}

.license-area {
	font-size: 0.95em;
}

#header {
	background-position: 1em 4em;
	background-size: 88px 88px;
}

#header h1, #header h2 {
	margin-left: 106px;
}
}

/* Desktop Media Query */
@media (min-width: 980px) and (max-width: 1399px) {
.top-bar {
	display:block;
}

.mobile-top-bar {
	display: none;
}
}

/* Wide Monitor Media Query */
@media (min-width: 1400px) {
.top-bar {
	display:block;
}

.mobile-top-bar {
	display: none;
}
}

/* off-canvas */

.close-menu {
	display: none;
}

@media (max-width: 767px) {

.owindow {
	min-width: 80%;
	max-width: 99%;
}

.modal-body .table, .modal-body .table ~ div {
	float: left;
}

.owindow .button-bar {
	float: right;
}

.owindow div .btn-primary {
width: 100%;
float: left;
}

.owindow div .btn-primary ~ div {
width: 100%;
}

.yui-navset {
z-index: 1;
}

#navi-bar, #navi-bar-shadow {
	display: none;
}

.open-menu a {
	position: fixed;
	bottom: 0.5em;
	left: 0.5em;
	z-index: 15;
	font-family: 'Nanum Gothic', san-serif;
	font-size: 30px;
	font-weight: 700;
	width: 30px;
	height: 30px;
	line-height: 0.9em;
	text-align: center;
	border: 0.2em solid rgb(107, 40, 113) !important;
	background-color: #fff !important;
	border-radius: 3em;
	color: rgb(107, 40, 113) !important;
}

.open-menu a:hover {
	text-decoration: none !important;
	-webkit-box-shadow: 0px 0px 20px 3px rgba(107, 40, 113,1);
	-moz-box-shadow: 0px 0px 20px 3px rgba(107, 40, 113,1);
	-ms-box-shadow: 0px 0px 20px 3px rgba(107, 40, 113,1);
	-o-box-shadow: 0px 0px 20px 3px rgba(107, 40, 113,1);
	box-shadow: 0px 0px 20px 3px rgba(107, 40, 113,1);
}

#main-content {
	max-width: 90%;
	margin: 0 5%;
	padding: 0;
	-webkit-transition: 0.5s ease-in-out 0.1s;
	-moz-transition: 0.5s ease-in-out 0.1s;
	-ms-transition: 0.5s ease-in-out 0.1s;
	-o-transition: 0.5s ease-in-out 0.1s;
	transition: 0.5s ease-in-out 0.1s;
}

#side-bar {
	display: block;
	position: fixed;
	top: 0;
	left: -25em;
	width: 17em;
	height: 100%;
	background-color: rgb(153, 217, 234);
	overflow-y: auto;
	z-index: 10;
	padding: 1em 1em 0 1em;
	-webkit-transition: left 0.5s ease-in-out 0.1s;
	-moz-transition: left 0.5s ease-in-out 0.1s;
	-ms-transition: left 0.5s ease-in-out 0.1s;
	-o-transition: left 0.5s ease-in-out 0.1s;
	transition: left 0.5s ease-in-out 0.1s;
}

#side-bar:after {
	content: "";
	position: absolute;
	top: 0;
	width: 0;
	height: 100%;
	background-color: rgba(0, 0, 0, 0.2);

}

.close-menu a {
	display: none;
}

#side-bar:target {
	display: block;
	left: 0;
	width: 17em;
	margin: 0;
	border: 1px solid #dedede;
	z-index: 10;
}

#side-bar:target + #main-content {
	left: 0;
}

#side-bar:target a .close-menu {
	display: block;
	position: fixed;
	width: 100%;
	height: 100%;
	top: 0;
	left: 0;
}
}
