[[module CSS show="true"]]

@charset "utf-8";
@import url('https://scpko.wdfiles.com/local--files/korean-font/GmarketSans.css');

/*
    í”ŒëŸ¬ê·¸ì†Œí”„íŠ¸ CSS í…Œë§ˆ
    [2020 Wikidot Theme]
    Edited by thd-glasses.
    Based on SCP Sigma 9 Theme created by Aelanna and Dr Devan.
    Code adopted from Wilsons Theme created by stormbreath.
    Code adopted from Minimal Theme created by stormbreath.
    The Plug Soft Logo was created by lanlanmag & thd-glasses and is licensed under CC-BY-SA 3.0.
*/

 :root {
     --header-logo: url(https://scpko.wdfiles.com/local--files/theme%3Aplugsoft/ps_logo_90px_{$theme}.png) 0px 35px no-repeat;

     --header-sub: var(--subtitle-{$alt}, 'ì œğŸì¹™ : ê·œì¹™ì€ ì—†ë‹¤');
     --subtitle-2: "{$subtitle}";

     --header-bg: var(--{$theme});
     --grey: #EEE;
     --green: #C7DED1;
     --blue: #BDF;
     --red: #C55;
     --black: #000;
     --space: #002 url(https://scpko.wdfiles.com/local--files/theme%3Aplugsoft/body_bg_space.jpg) center top no-repeat;
     --blank: {$custom};

     --font: var(--{$theme}-font, #444);
     --black-font: #FFF;
     --space-font: #EEF;
}

/* COMMON */
 #content-wrap {
     min-height:1300px;
     height: 1500px;
}

 h1, #page-title {
     font-family: 'GmarketSansLight', Arial, sans-serif;
     color: #444;
}

 #page-title {
     border-color: #444;
}

/* links */

 a {
     color: #777;
}

 #side-bar a:visited {
     color: #555;
}

 a:visited {
     color: #555;
}

 a.newpage {
     color: #999;
}

 body {
     background-color: #FFF;
     color: #000;
}

/* HEADER */
div#container-wrap {
    background: none;
}

div#extra-div-1 {
    height: 140px;
    width: 100%;
    top: 0;
    position: absolute;
    background: var(--header-bg);
    background-size: 100%;
} /* Light grey */

div#extra-div-2 {
    height: 22px;
    width: 100%;
    top: 140px;
    position: absolute;
    background: #444;
} /* Dark grey for top-bar */

 #header {
     background: var(--header-logo);
}

 #search-top-box-input {
     border: transparent;
     color: #444;
     background-color: #FFF;
}

 #search-top-box-input:hover, #search-top-box-input:focus {
     border: solid 0.5px #888;
     color: #444;
     background-color: #FFF;
}

 #search-top-box-form input[type=submit] {
     border: transparent;
     color: #888;
     background-color: #EEE;
     background: linear-gradient(to bottom, #DDD,#FFF);
}

#search-top-box-form input[type=submit]:hover, #search-top-box-form input[type=submit]:focus {
     border: solid 0.5px #FFF;
     color: #FFF;
     background-color: #FFF;
     background: linear-gradient(to bottom, #BBB,#EEE);
}

 #login-status {
     color: var(--font);
}

 #login-status a {
     color: var(--font);
}

 #login-status ul a {
     color: #444;
}

 #header h1 a {
     color: transparent;
     font-family: 'GmarketSansMedium', Arial, sans-serif;
     font-size: 150%;
     text-shadow: none;
     letter-spacing: 0.5px;
}

 #header h1 a::before {
     content: "í”ŒëŸ¬ê·¸ì†Œí”„íŠ¸â„¢";
     color: var(--font);
     text-shadow: none;
}

 #header h2 span {
     color: transparent;
     font-family: 'GmarketSansLight', Arial, sans-serif;
     text-shadow: none;
     margin-left: 5px;
}

 #header h2 span::before {
     content: var(--header-sub);
     color: var(--font);
     text-shadow: none;
}

 #top-bar a {
     color: #FFF;
}

 #top-bar ul li ul {
     border: solid 1px #EEE;
     box-shadow: none;
}

 #top-bar ul li.sfhover a, #top-bar ul li:hover a {
     background: #EEE;
     color: #444;
}

 #top-bar ul li.sfhover ul li a, #top-bar ul li:hover ul li a {
     border-top: 1px solid #EEE;
}

 #top-bar ul li.sfhover a:hover, #top-bar ul li:hover a:hover {
     background: #DDD;
}

 #top-bar ul li ul a, #top-bar a:hover {
     color: #444;
}

/* SIDE MENU */
 #side-bar {
     background: transparent;
}

 #side-bar .side-block {
     border: 1px solid #444;
     background: #FFF !important;
     box-shadow: none !important;
}

 #side-bar .heading {
     color: #444;
     border-bottom: solid 1px #444;
}

 #side-bar div.menu-item.inactive a {
     color: #444;
}

 #side-bar div.menu-item .sub-text {
     color: #444;
}

 #side-bar .collapsible-block-folded {
     background: none;
}

 #side-bar .collapsible-block-unfolded-link {
     border-bottom: solid 1px #444;
}

 #side-bar .collapsible-block-unfolded-link .collapsible-block-link {
     color: #444;
}

 #side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
     color: #444;
}

/* YUI-TABS */
 .yui-navset .yui-content{
     background-color: #FFF;
}

 .yui-navset .yui-nav a, .yui-navset .yui-navset-top .yui-nav a {
    background: #EEE url(none) repeat-x;
    border-top: solid #333 1px;
    border-left: solid #444 1px;
    border-right: solid #444 1px;
    color:#000;
}

 .yui-navset .yui-nav .selected a,
 .yui-navset .yui-nav .selected a:focus, /* no focus effect for selected */
 .yui-navset .yui-nav .selected a:hover { /* no hover effect for selected */
     background: #444; /* selected tab background */
     color: #EEE;
}

 .yui-navset .yui-nav a:hover,
 .yui-navset .yui-nav a:focus {
     background: #DDD;
     text-decoration: none;
}
 .yui-navset .yui-nav,
 .yui-navset .yui-navset-top .yui-nav {
     border-color: #444;
}

/* Rating Module */
 .page-rate-widget-box{
     box-shadow: none;
}

 .page-rate-widget-box .rate-points {
     background-color: #EEE !important;
     border: solid 1px #DDD;
     color: #888;
}

 .page-rate-widget-box .rateup, .page-rate-widget-box .ratedown {
     background-color: #FFF;
     border-top: solid 1px #DDD;
     border-bottom: solid 1px #DDD;
}

 .page-rate-widget-box .rateup a, .page-rate-widget-box .ratedown a {
     color: #888;
}

 .page-rate-widget-box .rateup a:hover, .page-rate-widget-box .ratedown a:hover {
     background: #EEE;
     color: #666;
}

 .page-rate-widget-box .cancel {
     background-color: #EEE;
     border: solid 1px #DDD;
}

 .page-rate-widget-box .cancel a {
     color: #888;
}

 .page-rate-widget-box .cancel a:hover {
     background-color: #EEE;
     color: #AAA;
}

/* Info Rating Module */

 .rate-box-with-credit-button {
     background-color: #EEE !important;
     border: 1px solid #DDD !important;
     border-radius: 5px !important;
     box-shadow: none !important;
}

 .rate-box-with-credit-button .creditButton p a {
     border-left-color: #DDD !important;
     color: #888;
}

 .rate-box-with-credit-button .creditButton p a:hover {
     color: #AAA;
}

 .rate-box-with-credit-button .page-rate-widget-box .cancel {
     border-radius: 0;
}

 .rate-box-with-credit-button .page-rate-widget-box .rate-points {
     border-left: 0;
}

/* Heritage Collection Rating Module */

 .heritage-rating-module {
     background-color: #EEE;
     box-shadow: none;
}

/* Mobile Media Query */
 @media (max-width: 479px) {
     #header {
         background-position: 0 4.5em;
         background-size: 60px 60px;
    }
     #header h1 a {
         font-size: 120%;
    }
     #header h1, #header h2 {
         margin-left: 66px;
    }
}
/* Small Mobile Media Query */
 @media (max-width: 385px) {
     #header {
         background-position: 5% 5.5em;
    }
     #header h1, #header h2 {
         margin-left: -webkit-calc(66px + 5%);
         margin-left: -moz-calc(66px + 5%);
         margin-left: calc(66px + 5%);
    }
}
/* Note Media Query */
 @media (min-width: 480px) and (max-width: 580px) {
     #header {
         background-position: 0.5em 4.5em;
         background-size: 66px 66px;
    }
     #header h1 a {
         font-size: 120%;
    }
     #header h1, #header h2 {
         margin-left: 80px;
    }
}
/* Mini Tablet Media Query */
 @media (min-width: 581px) and (max-width: 767px) {
     #header {
         background-position: 0.5em 3.5em;
         background-size: 77px 77px;
    }
     #header h1 a {
         font-size: 140%;
    }
     #header h1, #header h2 {
         margin-left: 93px;
    }
}
/* Tablet Media Query */
 @media (min-width: 768px) and (max-width: 979px) {
     #header {
         background-size: 88px 88px;
    }
     #header h1, #header h2 {
         margin-left: 106px;
    }
}
