[[collapsible show="+  展开版式代码" hide="- 最小化"]]
[[code type="CSS"]]
/*
    Poor Foundation Theme
    [2023 Wikidot Theme]
    Created for the Poor Foundation (poor-foundation-hab) by yzkrt, kskhorn
    Based on 活人入棺 中心页 by Sekai_s
    Edited by MikuKaneko
    Licensed under the CC-BY SA 3.0
*/

#header h2::after,
#search-top-box,
#login-status,
#account-topbutton,
.top-bar,
.mobile-top-bar > *:not(.open-menu),
#side-bar .side-block,
#side-bar .scpnet-interwiki-wrapper,
#main-content > div:not(#page-content),
#page-content a,
div.page-rate-widget-box,
.creditButton,
.yui-nav,
.code,
#footer,
#license-area,
.monochrome {
    -webkit-filter: grayscale(1);
    filter: grayscale(1);
}

::selection {
    background-color: #cccccccc;
}

h1,
h2,
h3,
h4,
h5,
h6 {
    color: #000;
}

#header {
    background: none;
}
#header::before {
    background: url("https://scp-jp.github.io/files/util/common/media/component/theme//logo.png") 10px 40px no-repeat;
    background-size: 100px 100px;
    content: "";
    display: block;
    height: 140px;
    left: 0;
    position: absolute;
    top: 0;
    width: 90%;
    z-index: -1;
}
@media (max-width: 479px) {
    #header::before {
     background-position: 0 5.5em;
     background-size: 55px 55px;
    }
}
@media (min-width: 480px) and (max-width: 580px) {
    #header::before {
     background-position: .5em 4.5em;
     background-size: 66px 66px;
    }
}
@media (min-width: 581px) and (max-width: 767px) {
    #header::before {
     background-position: 1em 4em;
     background-size: 77px 77px;
    }
}
@media (min-width: 768px) and (max-width: 979px) {
    #header::before {
     background-position: 1em 4em;
     background-size: 88px 88px;
    }
}
#header h1 a::before {
    content: "贫困基金会";
    color: #eee;
}
#header h1 a span {
    font-size: 0;
}
div#header h2::after {
    content: "素，惨，贫";
    color: #f0f0c0;
    font-weight: 700;
    padding: 19px 0;
    text-shadow: 1px 1px 1px rgba(0, 0, 0, .8);
    white-space: pre;
}
div#header h2 span {
    font-size: 0;
    padding: 4px;
}

#header h1,
#header::before,
#header h2 {
    -webkit-animation: flicker 10s infinite;
    -o-animation: flicker 10s infinite;
    animation: flicker 10s infinite;
}
#header::before {
    -webkit-animation-delay: .1s;
    -o-animation-delay: .1s;
    animation-delay: .1s;
}
#header h2 {
    -webkit-animation-delay: .2s;
    -o-animation-delay: .2s;
    animation-delay: .2s;
}
@keyframes flicker {
    53% {
     webkit-filter: brightness(100%);
     filter: brightness(100%);
    }
    53.5% {
     -webkit-filter: brightness(60%);
     filter: brightness(60%);
    }
    54% {
     -webkit-filter: brightness(80%);
     filter: brightness(80%);
    }
    54.5% {
     -webkit-filter: brightness(50%);
     filter: brightness(50%);
    }
    55% {
     -webkit-filter: brightness(100%);
     filter: brightness(100%);
    }
    64% {
     -webkit-filter: brightness(100%);
     filter: brightness(100%);
    }
    64.5% {
     -webkit-filter: brightness(40%);
     filter: brightness(40%);
    }
    65% {
     -webkit-filter: brightness(40%);
     filter: brightness(40%);
    }
    65.5% {
     -webkit-filter: brightness(80%);
     filter: brightness(80%);
    }
    66% {
     -webkit-filter: brightness(40%);
     filter: brightness(40%);
    }
    96.5% {
     -webkit-filter: brightness(40%);
     filter: brightness(40%);
    }
    97% {
     -webkit-filter: brightness(70%);
     filter: brightness(70%);
    }
    97.5% {
     -webkit-filter: brightness(40%);
     filter: brightness(40%);
    }
    98% {
     -webkit-filter: brightness(100%);
     filter: brightness(100%);
    }
}

div#container-wrap {
    background-image: url("https://scp-jp-storage.wdfiles.com/local--files/file%3A7657035-73-0m0k/body_bg_grey.png");
    background-repeat: repeat-x;
}

@media (max-width: 767px) {
    #side-bar {
     background: #9e9e9e;
    }
}

.modalbox {
    box-shadow: 0 2px 6px #33333380;
}
[[/code]]
[[/collapsible]]
[[/iftags]]

[[module CSS]]
@import url("https://scp-wiki-cn.wdfiles.com/local--code/theme%3Apoor-foundation/1");
[[/module]]
