[[module CSS show="true"]]
/*
    Thème de Quand le Soleil se Couche (Sigma-9)
    Thème Wikidot 2024
    Créé par Cauchynambour
    Image logo créée par Cauchynambour
    Traduit en branche coréenne par XCninety
    Image header tirée du thème http://scp-int.wikidot.com/poor-foundation-hub
*/

@import url('https://fonts.googleapis.com/css2?family=Space+Mono&display=swap');


:root {
--ct_cnt_alt-header-txt: " 재단";
--ct_fns_alt-header-txt : var(--step-2, clamp(2.72rem, calc(1.77rem + 2.47vw), 3.53rem));
--ct_fns_header-txt: 0;
--ct_fns_slogan-txt: 0;
--ct_fns_alt-slogan-txt: var(--step--1, clamp(0.88rem, calc(1.02rem + -0.20vw), 0.98rem));
--ct_cnt_alt-slogan-txt: "{$sous-titre}";
--ct_bg_header-logo: url(http://fondationscp.wdfiles.com/local--files/theme:quand-le-soleil-se-couche/header-logo.png);
--ct_col_link: black;
--ct_col_link-visited: black;

--step-1: clamp(1.75rem, calc(1.74rem + 0.04vw), 1.77rem);
--step-2: clamp(2.33rem, calc(2.27rem + 0.33vw), 2.50rem);
}

/* Compatibility */

#header {
    background-image: var(--ct_bg_header-logo);
    background-size: 100px 100px;
}

#header h1 a {
    font-size: var(--ct_fns_header-txt);
}

#header h1 a::after {
    content: var(--ct_cnt_alt-header-txt);
    font-size: var(--ct_fns_alt-header-txt);
}

#header h1 a::before, .flickering {
content : 'SCP';
animation : flicker 3s linear infinite;
font-size : var(--ct_fns_alt-header-txt);
}

#header h2 span {
    font-size: var(--ct_fns_slogan-txt);
}

#header h2 span::before {
    content: var(--ct_cnt_alt-slogan-txt);
    font-size: var(--ct_fns_alt-slogan-txt);
}

a {
    color: var(--ct_col_link, #b01);
}

a:visited {
    color: var(--ct_col_link-visited, #824);
}

@keyframes flicker {
    0%, 19.999%, 22%, 62.999%, 64%, 64.999%, 70%, 100% {
     //text-shadow: none;
    opacity: .99;

    }
    20%, 21.999%, 63%, 63.999%, 65%, 69.999% {
     opacity: 0.4;
    }
}

/* from : http://scp-int.wikidot.com/poor-foundation-hub/ */
div#container-wrap {
  background-image: url(http://fondationscp.wdfiles.com/local--files/theme:quand-le-soleil-se-couche/body_bg_grey.png);
  background-repeat: repeat-x;
}

#header, #side-bar {
  -webkit-filter: grayscale(100%);
  filter: grayscale(100%);
}

/* / from : http://scp-int.wikidot.com/poor-foundation-hub/ */
.page-rate-widget-box, .creditButton, .yui-navset {
  -webkit-filter: grayscale(100%);
  filter: grayscale(100%);
}

#page-title {
   color : black;
}

#header h2 {
   transform : rotate(2deg);
}

div.img {
   filter : grayscale(0);
}

[[/module]]
[[/div]]
