/*
    RAISA Sigma-9 Theme
    [2024 Wikidot Theme]
    By macro_au_micro
    Adapted from RAISA BHL Theme by Rounderhouse
    Based on SCP Sigma 9 Theme created by Aelanna and Dr Devan
*/

@import url('https://fonts.googleapis.com/css?family=PT+Mono|Share+Tech+Mono');
@import url('https://fonts.googleapis.com/css2?family=Oxanium:wght@400;600&display=swap');

:root {
  --gradient-header: linear-gradient(to bottom, rgba(200, 178, 90,0.7) 0%, transparent 100%);

    scrollbar-color: rgb(170, 170, 85) rgb(34, 34, 0);
}

::selection {
    background: rgba(221, 221, 119,1);
    color: rgb(34, 34, 0);
}
::-webkit-scrollbar {
    width: 1rem;
}

#side-bar::-webkit-scrollbar {
    width: .4rem;
}

::-webkit-scrollbar-thumb {
    background: rgb(170, 170, 85);
}

::-webkit-scrollbar-track {
    background: rgb(34, 34, 0);
}

/* HEADER */
