    background-size: 100px;
}

#header h1 a {
    color: transparent;
    text-shadow: none;
    margin-top: -2px;
}
#header h1 a::before {
    content: "RAISA数据库";
    color: rgb(34, 34, 0);
    font-size: 100%;
    font-family: 'Share Tech Mono', monospace;
    font-weight: 700;
    letter-spacing: 0;
}

#header h2 span {
    color: transparent;
    text-shadow: none;
    font-family: 'Share Tech Mono', monospace;
    font-weight: 500;
    letter-spacing: 0;
    margin-top: -2px;
}
#header h2 span::before {
    content: "记录与信息安全管理部";
    color: rgb(34, 34, 0);
}

div#extra-div-1 {
    position: absolute;
    display: block;
    width: 100%;
    height: 25rem;
    top: 0;
    left: 0;
    background: var(--gradient-header);
    background-size: 31.250rem;
    background-repeat: repeat;
    z-index: -2;
}

@media (max-width: 767px) {
    #header h1 a {
        font-size: 85%;
        margin-top: 8px;
    }
    #header h2 span {
        font-size: .55rem;
    }
    #header h2 span::before {
        margin-top: -12px;
    }
    #search-top-box{
        top: 40px;
        right: 0;
        font-size: 80%;
    }
    #login-status {
        font-size: 80%;
    }
}

/* LOG-IN INFO */
#login-status {
    color: rgb(34, 34, 0);
    font-family: 'PT Mono', monospace;
}
#login-status a {
    color: rgb(34, 34, 0);
}
#account-options {
    border: 2px solid rgb(170, 170, 85) !important;
}
#login-status ul a {
    background-color: rgb(34, 34, 0);
    color: rgb(221, 221, 119);
}

#login-status ul a:hover {
    color: rgb(221, 221, 119);
    font-weight: bold;
}
#account-topbutton {
    border: none;
    background-color: rgb(34, 34, 0) !important;
    color: rgb(221, 221, 119) !important;
}

/* SEARCH BAR */
#search-top-box-form input[type=submit] {
    background: rgb(34, 34, 0);
    color: rgb(170, 170, 85);
    border: 2px solid rgb(170, 170, 85);
    font-family: 'PT Mono', monospace;
    transition: background 0.25s;
    box-shadow: none;
    border-radius: 0;

}
#search-top-box-form input[type=submit]:hover, #search-top-box-form input[type=submit]:focus {
    background:rgb(170, 170, 85);
    border: 2px solid rgb(34, 34, 0);
    color: rgb(34, 34, 0);
    box-shadow: none;
    border-radius: 0;
}

/* TITLE */
#page-title, .meta-title {
    border-color: rgb(34, 34, 0);
}
h1, h2, h3, h4, h5, h6, #page-title, .meta-title {
    font-weight: 800;
    color: rgb(34, 34, 0);
    font-family: 'Share Tech Mono', monospace;
}
#breadcrumbs {
    font-family: 'PT Mono', monospace;
}

@media (max-width: 767px) {
    #page-title, .meta-title {
        font-size: 180%
    }
}

/* TOP-BAR SETTINGS */
#top-bar ul li ul {
    border-color: rgb(34, 34, 0);
}
#top-bar ul li a {
    color: rgb(170, 170, 85);
    font-family: 'PT Mono', monospace;
}
#top-bar ul li ul li a {
    font-family: 'PT Mono', monospace;
}
#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
    border-color: rgb(203, 203, 107);
    color: rgb(34, 34, 0);
}
#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: rgb(221, 221, 119);
    color: rgb(34, 34, 0);
}
#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background: rgb(236, 234, 181);
    color: rgb(34, 34, 0);
}
#top-bar ul li ul.sfhover a:hover,
#top-bar ul li ul:hover a:hover {
    background: rgb(236, 234, 181);
    color: rgb(34, 34, 0);
}

/* SIDE-BAR SETTINGS */
#side-bar .side-block,#interwiki .side-block {
    border: 2px solid rgb(170, 170, 85);
    border-radius: 0;
    background-color: rgb(34, 34, 0) !important;
    box-shadow: none;
    font-family: 'PT Mono', monospace;
    font-style: normal;
    color: rgb(221, 221, 119);
}

#side-bar .heading,#interwiki .heading {
    font-family: 'Oxanium', cursive;
    font-size: 0.9em;
    color: rgb(224, 196, 71);
    border-bottom-color: rgb(221, 221, 119);
    text-transform: uppercase;
}
#interwiki .menu-item a, #side-bar .menu-item a {
    font-weight: 500;
}
#interwiki .menu-item img {
    display: none;
}


/* MOBILE SIDE-BAR */
@media (max-width: 767px) {
#side-bar {
    background-color: rgb(242, 242, 194);
}
#top-bar .open-menu a {
    border-radius: 0;
    border: 2px solid rgb(170, 170, 85);
    background-color: rgb(34, 34, 0);
    color: rgb(221, 221, 119);
    }
}

/* BODY */
body {
    font-family: 'Oxanium', cursive;
    color: rgb(12, 12, 12);
}
body tt {
    font-family: 'PT Mono', monospace;
}

/* FOOTER */
#footer {
    background-color: rgb(170, 170, 85);
    color: rgb(34, 34, 0);
    font-family: 'PT Mono', monospace;
}

#license-area {
    background-color: rgb(34, 34, 0);
    color: rgb(221, 221, 119);
    font-family: 'PT Mono', monospace;
}

/* OTHER STRUCTURAL ELEMENTS */

/* BARS */
#main-content hr {
    border-top: .0625rem solid rgb(34, 34, 0);
}

hr {
background: rgb(221, 221, 119);
}

/* LINKS */
a {
    color: rgb(188, 145, 37);
}
a:visited {
    color: rgb(165, 128, 67);
}
a.newpage {
    color: rgb(224, 182, 31);
}
#side-bar a,
#side-bar a:visited,
#side-bar a.newpage,
#interwiki a {
    color: rgb(221, 221, 119);
    font-size: 0.85em;
}

/* FOOTNOTES */
.footnotes-footer {
    background-color: rgb(34, 34, 0);
    color: rgb(221, 221, 119);
    border: 2px solid rgb(170, 170, 85);
    font-family: 'Share Tech Mono', monospace;
    margin-top: 3rem;
    margin-bottom: 1.5rem;
    padding: 1em;
    padding-top: 0;
}
.footnotes-footer a[href*=javascript] {
    background-color: rgb(221, 221, 119);
    color: rgb(34, 34, 0);
}

/* POP-UP WINDOWS */
.owindow {
    background-color: rgb(34, 34, 0);
    color: rgb(221, 221, 119);
    border-color: rgb(170, 170, 85);
}

.owindow .modal-header {
    font-family: 'Share Tech Mono', monospace;
}
.owindow .modal-body img {
    background-color: transparent !important;
}
.owindow .title {
    background-color: rgb(221, 221, 119);
    color: rgb(34, 34, 0);
}
.owindow .button-bar a {
    background-color: rgb(34, 34, 0);
    border-color: rgb(170, 170, 85);
    color: rgb(221, 221, 119);
    font-family: 'PT Mono', monospace;
}
.owindow .button-bar a:hover {
    background-color: rgb(170, 170, 85);
    color: rgb(34, 34, 0);
}
.owindow a.btn.btn-primary,.owindow a.btn.btn-danger.btn-small.btn-sm {
    font-family: 'PT Mono', monospace;
}

/* CODE */
.code,div.code {
    background-color: rgb(34, 34, 0);
    border: dashed 2px rgb(170, 170, 85);
    font-family: 'Share Tech Mono', monospace;
    color: rgb(221, 221, 119);
}
.hl-default, .hl-code, .hl-brackets {
    color: rgb(243, 243, 243);
}
.hl-identifier, .hl-special, .hl-string {
    color: rgb(114, 197, 255);
}
.hl-var {
    color: rgb(128, 164, 55);
}
.hl-number {
    color: rgb(241, 141, 59);
}
.hl-reserved {
    color: rgb(238, 161, 230);
}

/* RATING MODULE */
div.page-rate-widget-box {
    font-family: 'PT Mono', monospace;
    border-radius: 0;
}
div.page-rate-widget-box .rate-points {
    background-color: rgb(34, 34, 0);
    border: 2px solid rgb(170, 170, 85);
    border-right: none;
    border-radius: 0;
    color: rgb(221, 221, 119);
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background-color: rgb(34, 34, 0);
    border-top: 2px solid rgb(170, 170, 85);
    border-bottom: 2px solid rgb(170, 170, 85);
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    color: rgb(221, 221, 119);
    transition: background 0.25s;
}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background: rgb(221, 221, 119);
    text-decoration: none;
    color: rgb(34, 34, 0);
}
.page-rate-widget-box .cancel {
    background-color: rgb(34, 34, 0);
    border: 2px solid rgb(170, 170, 85);
    border-left: none;
    border-radius: 0;
    transition: background 0.25s;
}
.page-rate-widget-box .cancel a {
    color: rgb(221, 221, 119);
}
.page-rate-widget-box .cancel a:hover {
    background: rgb(221, 221, 119);
    text-decoration: none;
    color: rgb(34, 34, 0);
    border-radius: 0;
}

/* PAGE ELEMENTS */

/* BLOCKQUOTES */
blockquote,
div.blockquote {
    background-color: rgb(34, 34, 0);
    border: 3px solid rgb(170, 170, 85);
    color: rgb(221, 221, 119);
}
blockquote blockquote,
div.blockquote div.blockquote {
    background-color: rgb(34, 34, 0);
    border: 3px solid rgb(170, 170, 85);
    color: rgb(221, 221, 119);
}

blockquote :is(h1, h2, h3, h4, h5, h6, div),
div.blockquote :is(h1, h2, h3, h4, h5, h6, div) {
    color: rgb(221, 221, 119);
}

/* TABLES */
table.wiki-content-table {
    border: 2px solid rgb(170, 170, 85);
}

table.wiki-content-table td {
    color: rgb(221, 221, 119);
    background-color: rgb(34, 34, 0);
    border-color: rgb(170, 170, 85);
}

table.wiki-content-table th {
    color: rgb(224, 196, 71);
    background-color: rgb(34, 34, 0);
    border-color: rgb(170, 170, 85);
   text-transform: uppercase;
}

/* TABVIEW */
/* Adapted from BHL */
.yui-navset .yui-nav a,.yui-navset .yui-navset-top .yui-nav a{background-color:inherit;background-image:inherit}.yui-navset .yui-nav a:hover,.yui-navset .yui-nav a:focus{background:inherit;text-decoration:inherit}.yui-navset .yui-nav .selected a,.yui-navset .yui-nav .selected a:focus,.yui-navset .yui-nav .selected a:hover{color:inherit;background:inherit}.yui-navset .yui-nav,.yui-navset .yui-navset-top .yui-nav{border-color:inherit}.yui-navset li{line-height:inherit}

 .yui-navset * {
    transition:
       color 80ms cubic-bezier(.4, 0, .2, 1),
       background-color 80ms cubic-bezier(.4, 0, .2, 1);
}

 .yui-navset .yui-nav,
 .yui-navset .yui-navset-top .yui-nav {
    display: flex;
    flex-wrap: wrap;
    width: 100%;
    margin: 0 auto;

    border-color: rgb(224, 196, 71);
    box-shadow: 0 calc(.0625rem * 5) 0 0 rgb(224, 196, 71);
}

 .yui-navset .yui-nav a, /* == Link Modifier == */
 .yui-navset .yui-navset-top .yui-nav a {
    color: rgb(252, 252, 252);
    /* ==== Tab Background Colour | [UNSELECTED] ==== */
    background-color: rgb(34, 34, 0);
    /* ==== */
    background-image: none;
    border: unset;
}

 .yui-navset .yui-nav a:hover,
 .yui-navset .yui-nav a:focus {
    color: rgb(34, 34, 0);
    /* ==== Tab Background Colour | [HOVER] ==== */
    background-color: rgb(221, 221, 119);
    /* ==== */
}

 .yui-navset .yui-nav li { /* == Listitem Modifier == */
    position: relative;
    display: flex;
    flex-grow: 2;
    max-width: 100%;
    margin: 0;
    padding: 0;

    background-color: #f5f5f5;
    border-color: transparent;
}

 .yui-navset .yui-nav li a,
 .yui-navset-top .yui-nav li a,
 .yui-navset-bottom .yui-nav li a {
    display: flex;
    align-items: center;
    justify-content: center;
    width: 100%;
}

 .yui-navset .yui-nav li em {
    border: unset;
}

 .yui-navset .yui-nav a em {
    padding: .35em .75em;

    text-overflow: ellipsis;
    overflow: hidden;
    white-space: nowrap;
}

 .yui-navset .yui-nav .selected { /* == Selection Modifier == */
    flex-grow: 2;
    margin: 0;
    padding: 0;

    /* ==== Tab Background Colour | [SELECTED] ==== */
    background-color: rgb(224, 196, 71);
    /* ==== */
}

 .yui-navset .yui-nav .selected a,
 .yui-navset .yui-nav .selected a em {
    border: rgb(224, 196, 71);
}

 .yui-navset .yui-nav .selected a {
    width: 100%;

    color: rgb(34, 34, 0) !important;
    background-image: none;
}

 .yui-navset .yui-nav .selected a:focus,
 .yui-navset .yui-nav .selected a:active {
    color: rgb(34, 34, 0);
    background-color: rgb(224, 196, 71);
}

 .yui-navset .yui-nav .selected a:hover {
    cursor: default;
}

 .yui-navset .yui-content,
 .yui-navset .yui-navset-top .yui-content {
    padding: .5em;
    border: 1px solid #999999;
    background-color: rgb(198, 198, 189);
}

/* IMAGE BLOCK */
.scp-image-block .scp-image-caption {
    color: rgb(221, 221, 119);
    background-color: rgb(34, 34, 0);
    border-top: 2px solid rgb(170, 170, 85);
}
.scp-image-block {
    border: 2px solid rgb(170, 170, 85);
}

/* HOVERTIP */
.hovertip {
    background-color: rgb(34, 34, 0) !important;
    border: 2px solid rgb(170, 170, 85) !important;
    color: rgb(221, 221, 119);
    font-family: 'PT Mono', monospace;
}

/* TAGS & OPTIONS */
#page-info {
    font-family: 'PT Mono', monospace;
}
#main-content .page-tags span {
    max-width: 100%;
    width: 100%;
    padding-top: 3px;
}
.page-tags span {
    border-color: rgb(34, 34, 0);
}
.page-tags a, .page-options-bottom a, .page-watch-options a, a.action-area-close.btn.btn-danger {
    font-family: 'PT Mono', monospace;
}

/* 移动端适配 */
@media (min-width: 768px) and (max-width: 979px) {
    #header {
        background-size: 88px 88px;
    }
}
@media (min-width: 581px) and (max-width: 767px) {
    #header {
        background-size: 77px 77px;
    }
}
@media (min-width: 480px) and (max-width: 580px) {
    #header {
        background-size: 66px 66px;
    }
}
@media (max-width: 479px) {
    #header {
        background-size: 55px 55px;
    }
}

