@import url("https://scp-wiki-cn.wikidot.com/component:croqstyle/code/1");
@import url('https://fonts.googleapis.com/css2?family=Righteous&display=swap');

/* 主体 */

body {
    background: #1D1D1C;
    color: #B0AFAA;
}

.scp-image-block .scp-image-caption {
    background-color: #151515;
}

#page-title, .meta-title  {
    border-color: #B0AFAA;
    color: #B0AFAA;
}

div#container-wrap {
    background-image: url('https://scp-wiki.wdfiles.com/local--files/theme%3Ared-vizor/body_bg.png');
}

.close-credits,
.credit-back {
    filter: grayscale(100%);
}

/* 某些页面上的信息模块。 */
.modalbox {
    text-shadow: none;
    color: #000000;
}

/* 媒体与版头 */

div.blockquote,
blockquote, div#toc  {
    background-color: #232321;
    border: dashed 1px #6c6c6a;
}

h1 {
    color: #B0AFAA;
}

hr {
    background-color: #9e9e9e;
}

/* 链接 */

a {
    color: #FB4141;
}

a:visited {
    color: #ff8585;
}

/* 分页 */

.yui-navset .yui-content {
    background-color: #232321;
}

table.wiki-content-table th {
    background-color: #232321;
}

/* 侧栏 */

#side-bar .heading {
     color: #F4F4E1;
     border-color: #6c6c6a;
}

/* ------ 搬运注：此处有修改 ----- */
#side-bar div.side-block {
    background-color: #232321 !important;
    border: solid 1px #6c6c6a;
    box-shadow: none;
}

#side-bar a,
#side-bar a:visited {
    color: #FB4141;
}

/* 弹窗 */

.hovertip {
  background: #232321 !important;
  border: solid 1px #B0AFAA;
}

/* 沙盒弹窗 */

.preview-message {
    color: #232321;
}

.owindow,
.owindow h2,
.owindow h3,
.owindow h4,
.owindow h5,
.owindow h6 {
  color: #333;
}
.owindow h1,
.owindow a {
  color: #333;
}

.owait div.modal-body {
    color: #232321 !important;
}

form div.sub {
    color: #232321;
}

#lock-info {
    color: #232321;
}

table.page-files .highlight {
    background-color: transparent;
}

/* 编辑 */

/*  编辑  */

#edit-page-title {
  background-color: #1D1D1C;
  border: 1px solid #ccc;
  color: #B0AFAA;
}
#edit-page-textarea {
  background-color: #1D1D1C;
  border-color: #ccc;
  color: #B0AFAA;
}
#edit-page-comments {
  background-color: #1D1D1C;
  border-color: #ccc;
  color: #B0AFAA;
}
#lock-info {
  background-color: #1D1D1C;
}
[[/code]]

[[/iftags]]
