[[code type="CSS"]]
/* --------------------------
  Redsquare
  [2025 Wikidot Theme]
   - Theme by lestday233
   - Logo by SeymorWu
   - Body_bg by Juliya8 via Pixabay, use Pixabay License (pixabay.com/zh/service/license-summary)

  Based on Zattou
  [2021 Wikidot Theme]
   - Theme by sendoh-oroka

  Unless otherwise stated, All licensed under CC BY-SA 3.0
 -------------------------- */

@import url(https://fonts.googleapis.com/css2?family=Noto+Serif+SC:wght@200;300;400;500;600;700;900&display=swap);

/* BASIC */
#side-bar a:visited,
a {
  color: #e90000;
}
a:visited {
  color: #b90f06;
}
a.newpage {
  color: #ba5104;
}
h1 {
  color: #e60000;
  padding: 0;
  font-weight: bold;
}
h2, h3, h4, h5, h6 {
  margin: 0.5em 0 0.4em;
  padding: 0;
  letter-spacing: 1px;
}
hr {
  height: 1px;
  margin: 15px auto;
  background-color: #e60000;
}

/* HEADER */
  /* Body_bg */
div#container-wrap {
  height: 140px;
  width: 100%;
  background: url("https://lestday-lab.wdfiles.com/local--files/theme%3Aredsquare/body_bg.jpg");
  background-size: cover;
  background-position: 50% 50%;
}
div#extra-div-2 {
  height: 21px;
  width: 100%;
  background-color: #861111;
  border-top: solid 1px #b21b1b;
}
  /* Logo */
div#header {
  background: url(https://lestday-lab.wdfiles.com/local--files/theme%3Aredsquare/logo.png) 18px 45px no-repeat;
  background-size: 82px 82px;
}
@media (max-width: 479px) {
  div#header {
    background-position: 0 5em;
    background-size: 55px 55px;
  }
}
@media (min-width: 480px) and (max-width: 580px) {
  div#header {
    background-position: 0.5em 4.2em;
    background-size: 68px 68px;
  }
}
@media (min-width: 581px) and (max-width: 767px) {
  div#header {
    background-position: 0.2em 3.8em;
    background-size: 75px 75px;
  }
}
  /* Title */
#header h1 a span,
#header h2 span {
  visibility: hidden;
}
#header h1 a,
#header h2 {
  font-family: 'Noto Serif SC', serif;
  font-weight: bolder;
}
#header h1 a {
  font-size: 138%;
}
#header h1 a::before {
  content: var(--header-title, "军事情报总局");
}
#header h2 {
  margin-top: .25em;
}
#header h2::before {
  content: var(--header-subtitle, "\"超心理学\" 部门");
  color: #ffef00;
  display: block;
  text-align: center;
}
  /* Other */
#search-top-box-input {
  display: none;
}
#login-status {
  color: #000;
  padding: .5em 1em;
  border: 2px solid #ea1919;
  background: #fff;
}
#login-status a {
  color: #000;
}
#account-options {
  border-color: #ea1919;
}
#search-top-box-form > input[type=submit] {
  color: #fff;
  background: #e60000;
}
#search-top-box-form > input[type=submit]:hover {
  color: #fff;
  background: #ca3131;
}

/* NAVBAR */
  /* Topbar */
#top-bar ul li:hover a,
#top-bar ul li:hover a:hover {
  color: #fff;
  background: #931313;
}
#top-bar ul li:hover ul li a {
  border-top: 1px solid #b64141;
}
#top-bar .open-menu a {
  border: 2px solid #ea1919;
  border-radius: 0;
  color: #ea1919 !important;
}
  /* Sidebar */
#side-bar .side-block {
  background: #ffecec;
  border: none;
  border-radius: unset;
  box-shadow: 0px 0px 4px #741c1c;
}
  /* Content */
#page-title,
.meta-title {
  color: #e60000;
  font-family: 'Noto Serif SC', serif;
  font-size: 200%;
  transition: 0.5s;
  border-bottom: 1px solid #e60000;
}
#main-content .footnotes-footer .title {
  color: #e60000;
}
.owindow .title {
  color: #fff;
  background-color: #b53838;
}
#main-content .page-tags span {
  padding-top: .1em;
  border-top: 1px solid #e60000;
}
#action-area div.page-source:not(.inline-diff) {
  height: 432px;
  overflow: auto;
  border-color: #e60000;
  background-color: #f4f4f4;
}
#footer {
  background: #861111;
}

/* BLOCK */
  /* Rate */
div.page-rate-widget-box,
div.page-rate-widget-box .rate-points,
div.page-rate-widget-box .cancel,
.creditRate .rateBox div.rate-box-with-credit-button {
  border-radius: unset;
  text-decoration: none;
}
.creditRate .rateBox div.rate-box-with-credit-button {
  background-color: #ae1d1d;
  border-color: #ae1d1d;
}
.page-rate-widget-box .rate-points,
.page-rate-widget-box .cancel {
  background-color: #ae1d1d !important;
  border-color: #ae1d1d;
}
.page-rate-widget-box .cancel a {
  color: #fff;
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
  border-top-color: #ae1d1d;
  border-bottom-color: #ae1d1d;
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
  color: #9f4949;
}
.page-rate-widget-box .ratedown a:hover,
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .cancel a:hover {
  background: #ae1d1d;
}
.rate-box-with-credit-button .creditButton p a.fa.fa-info {
  border-left-color: #891b1b;
}
  /* Toc */
#toc {
  display: block;
  min-width: 10rem;
  border: 1px solid #e60000;
  padding: unset;
}
#toc #toc-action-bar {
  background: #ea3e3e;
}
#toc #toc-action-bar a {
  color: #fff;
  text-decoration: none;
}
#toc #toc-action-bar a:first-child::before {
  content: "+";
  padding: 0 2px;
}
#toc #toc-action-bar a:last-child::before {
  content: "-";
  padding: 0 4px;
}
f#toc .title {
  color: #e60000;
  display: flex;
  padding: .2rem;
}
#toc .title::after,
#toc .title::before {
  content: "";
  height: 0;
  margin: auto 6px;
  flex-grow: 1;
  border-top: 1px solid #e60000;
}
#toc #toc-list {
  margin: .5em;
}
  /* Imageblock */
.scp-image-block {
  border-color: #e60000;
}
.scp-image-block .scp-image-caption {
  border-top-color: #e60000;
  background-color: #e8a5a5;
}
  /* Tabview */
.yui-navset .yui-nav {
  border: none;
  background: #ea3e3e;
}
.yui-navset .yui-nav li,
.yui-navset .yui-nav li.selected {
  margin: 0;
  padding: 0;
}
.yui-navset .yui-nav li a {
  color: #fff;
  border: unset;
  background: #ea3e3e;
}
.yui-navset .yui-nav li.selected a {
  background: #fff;
  color: #000;
}
.yui-navset .yui-nav li a,
.yui-navset .yui-nav li a:focus,
.yui-navset .yui-nav li a:hover {
  background: unset;
}
.yui-navset .yui-nav li.selected a,
.yui-navset .yui-nav li.selected a:focus,
.yui-navset .yui-nav li.selected a:hover {
  background: #fff;
  color: #000;
}
.yui-navset .yui-nav li a em {
  border: 1px solid #ec3f3f;
  position: unset;
}
.yui-navset .yui-nav li.selected a em {
  border-color: #ea3e3e;
}
.yui-navset .yui-content {
  border: 1px solid #ec3f3f;
  border-top: none;
  background-color: #fff;
}
  /* Blockquote */
blockquote,
div.blockquote {
  margin: 15px auto;
  border: 1px solid #e60000;
  background-color: #fff;
}
  /* Code */
div.code {
  border: 1.5px dashed #e60000;
  background-color: #fff;
}
  /* Table */
table.wiki-content-table th {
  background-color: #e8a5a5;
  color: #351313;
}
table.wiki-content-table td {
  background-color: #fffbfc;
}
  /* Title */
.linetitle h1 span {
  color: #e60000;
  display: flex;
  padding: .15rem;
}
.linetitle h1 span::after,
.linetitle h1 span::before {
  content: "";
  height: 0;
  margin: auto 10px;
  flex-grow: 1;
  border-top: 1px solid #e60000;
}
[[/code]]
[[/collapsible]]
[[/iftags]]
