content:
[[module CSS]]
@import url(https://scp-wiki-cn.wikidot.com/theme:sai-napoleon-i/code/1);
[[/module]]

[[iftags +版式]]
[[code type="css"]]
/*
      Allègrement repompé sur : SCP Sigma 9
    [2014 Wikidot Theme]
    Created for the SCP Foundation by Aelanna (bidouillé par DrGemini)
*/

/* RECOLOR THE TITLE */
h1, #page-title {
    color: #fff; /* COULEUR GROS TEXTE BLANC */
    padding: 0 0 0.25em;
    margin: 0 0 0.6em;
    font-weight: normal;
z-index: -10;
}

#page-title {
    border-color: #f6d058;
}
ul {
    list-style: square;
}

/*RECOLOR THE LINKS */
a {
    color: #960000;
    text-decoration: none;
    background: transparent;
}
a.newpage {
    color: #f6d058;
    text-decoration: none;
    background: transparent;
}
a:hover {
    color: #e7d30e
    text-decoration: underline;
    background-color: transparent;
}

#side-bar a:visited{ color: #EDB44F; }

 a:visited { color: #EDB44F; }

/*HEADER IMAGES*/
div#container-wrap {
    background: url(http://fondationscp.wdfiles.com/local--files/theme%3Asai-napoleon-i/Th%C3%A8meSAINapoI.jpg) top left repeat-x;
}

#header {
    height: 200px;
    position: relative;
    z-index: 10;
    padding-bottom: 0px; /* FOR MENU */
    background: url(http://fondationscp.wdfiles.com/local--files/theme%3Asai-napoleon-i/1506450654-saii.png) 20px 0px no-repeat;
}

/* POUR AVOIR UN FOND NOIR (DUH) */
body {
    background-color: #000000;
}

/* Adjust Mobile Image Size */
@media (max-width: 479px) {
#header {
    background-size: 415px 145px;
    background-position: -5.1em 1.3em;
}
}
@media (min-width: 480px) and (max-width: 580px) {
#header {
    background-size: 415px 145px;
    background-position: 0em -1em;
}
}
@media (min-width: 581px) and (max-width: 767px) {
#header {
background-size: 500px 135px;
    background-position: -1em -1em;
}
}
@media (min-width: 768px) and (max-width: 979px) {
#header {
    background-size: 500px 135px;
    background-position: 0em -1em;
}
}


/* HIDE THE HEADER TEXT */
#header h1 a {
    display: block;
    margin: 0;
    padding: 80px 0 25px;
    line-height: 0px;
    max-height: 0px;
    color: #00003D;
    background: transparent;
    font-family: Akashi, Arial, sans-serif;
    font-size:0%;
    text-decoration: none;
    text-shadow: 3px 3px 5px #000;
    letter-spacing: 0.9px;
}

#header h2 span {
    display: block;
    margin: 0;
    padding: 19px 0;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: #f0f0c0;
    font-family: Helvetica, sans-serif;
    font-size: 0%;
    text-shadow: 1px 1px 1px #000;
    text-shadow: 1px 1px 1px rgba(0,0,0,.8);
}

/* TOP BARRE */
#top-bar {
    position: absolute;
    z-index: 50;
    top: 140px;
    height: 21px;
    line-height: 18px;
    padding: 0;
    z-index: 20;
    font-size: 90%;
}
#top-bar ul {
    float: right;
}
#top-bar a {
    color: #fff;
    background: transparent;
}
#top-bar ul li {
    border: 0;
}
#top-bar ul li ul {
    border: solid 1px #666;
    box-shadow: 0 2px 6px rgba(0,0,0,.5);
    border-top: 0;
}
#top-bar ul li a {
    border-left: solid 1px rgba(64,64,64,.1);
    border-right: solid 1px rgba(64,64,64,.1);
    text-decoration: none;
    padding-top: 10px;
    padding-bottom: 10px;
    line-height: 1px;
    max-height: 1px;
    overflow: hidden;
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: #00003D;
    color: #e7d30e;
    border-left: solid 1px rgba(64,64,64,1);
    border-right: solid 1px rgba(64,64,64,1);
}

#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
    border-width: 0;
    width: 150px;
    border-top: 1px solid #ddd;
    line-height: 160%;
    height: auto;
    max-height: none;
    padding-top: 0;
    padding-bottom: 0;
}
#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background: #fff;
    text-decoration: none;
}
#top-bar ul li ul {
    border-width: 0 1px 1px 1px;
    width: auto;
}
#top-bar ul li ul li, #top-bar ul li ul li.sfhover,
#top-bar ul li ul li, #top-bar ul li ul li:hover {
    border-width: 0;
}
#top-bar ul li ul li a {
    border-width: 0;
}
#top-bar ul li ul a, #top-bar a:hover {
    color: #a01;
}

/* IE7 HACK */
#top-bar ul > li > ul {
    *margin-top: -4px;
}


/* RECOLOR THE SEARCH BAR */
#search-top-box-input, #search-top-box-input:hover, #search-top-box-input:focus {
    background-color: #11131f;
    border-radius: 0 0px 0px 0;
}
#search-top-box-form input[type=submit] {
    background-image: linear-gradient(#b57416, #edb15a);
    color: #800909;
    border-radius: 0 0px 0px 0;
}

#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    background-image: linear-gradient(#b57416, #edb15a);
    color: #800909;
    border-radius: 0 0px 0px 0;
}

/* LOGIN STUFF */
#login-status {
    color: #1f1f1f;
    font-size: 90%;
    z-index: 30;
}
#login-status a {
    background: transparent;
    color: #1f1f1f;
}
#login-status ul a {
    color: #1f1f1f;
    background: transparent;
}

#account-topbutton {
    background: #ddd;
    color: #1f1f1f;
    border: none;
}


/* COULEUR BLOC D’IMAGE (PEUT TRIPATOUILLER)*/
.scp-image-block {
  border: inset 3px #9E6E54;
}
.scp-image-block .scp-image-caption {
  background-color: #F5F1CE;
  border-top: inset 3px #9E6E54;
}

/*-------------------*\
 *  LES ENCADRES  *
\*-------------------*/

blockquote {
    border: 1px solid rgba(0,0,0,0.12);
    background-color: #F5EDCE;
}

/* MODULE RATE RECOLOR */
.page-rate-widget-box {
    display: inline-block;
    border-radius: 5px;
    box-shadow: 1px 1px 3px rgba(0,0,0,.5);
    margin-bottom: 10px;
    margin-right: 2em;
}
.page-rate-widget-box .rate-points {
  background-color: #380d0d !important;
  border: solid 1px #f6d058;
  border-right: 0;
  border-radius: 5px 0 0 5px;
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
  background-color: #380d0d;
  border-top: solid 1px #f6d058;
  border-bottom: solid 1px #f6d058;
  font-weight: bold;
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
  background: transparent;
  color: #d28f11 ;
  padding: 0 4px;
  margin: 0 1px;
}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
  background: #c70000;
  color: #d28f11;
  text-decoration: none;
}
.page-rate-widget-box .cancel {
  background-color: #380d0d;
  border: solid 1px #f6d058;
  border-left: 0;
  border-radius: 0 5px 5px 0;
}
.page-rate-widget-box .cancel a {
  background: transparent;
  text-transform: uppercase;
  color: #db411a;
}
.page-rate-widget-box .cancel a:hover {
  border-radius: 0 3px 3px 0;
  background: #d28f11;
  color: #d28f11;
  text-decoration: none;
}

/* INFO MODULE */
.rate-box-with-credit-button .creditButton {

    background-color: #380d0d !important;
    border-color: #f6d058 !important;
}

.creditButton a{
    color: #d28f11;
}

.creditButton a:hover {
    color: #d28f11;
}

.creditButton {
    margin-bottom: 10px;
    margin-right: -2em;
}



/* TAB RECOLORING (A TRIPATOUILLER) */
/* TABVIEW LOOKS
===========================*/

.yui-navset .yui-content {
    background-color: #F8F4E7;
    padding: 5px 20px 5px 20px;
}

.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
    background-color:#fbc951;
    background-image: url(http://fondationscpsandbox.wdfiles.com/local--files/scp-2212-valide-par-nerem/ARCA.png);
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:hover { /* no hover effect for selected */
    background: #784c34; /* selected tab background */
    color:white;
}

.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus {
    background: #d8cac1;
    text-decoration: none;
}
.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    border-color: #6b513b;
}
.yui-navset li {
    line-height: normal;
}

/* BAS DE PAGE */
#footer {
    clear: both;
    font-size: 77%;
    background: #540400;
    color: #d89900;
    margin-top: 15px;
    padding: 3px 10px;
}
#footer .options {
    visibility: visible;
    display: block;
    float: right;
    width: 50%;
    font-size: 100%;
    text-align: right;
}
#footer a {
    color: #fff;
    background: transparent;
}

/*SIDE BAR RECOLOR AND STUFF */
#side-bar .side-block {
    padding: 10px;
    border: 1px solid #f2e281;
    border-radius: 10px;
    box-shadow: 0 2px 6px rgba(0,0,0,.5);
    background: #dbdaad;
    text-shadow: 1px 1px 1px solid #B9B9B9;
    background: linear-gradient(#380d0d, #380d0d);
    margin-bottom: 15px;
    color: #ddac13;
    text-shadow: 1px 1px 1px #B9B9B9;
    text-shadow: 1px 1px 1px rgba(0,0,0,.8);
}
.side-block a {
color: #f6d058;
text-decoration:none;
}
.side-block a:hover {
color: #e7d30e;
text-decoration:none;
}
.side-block a.newpage {
color: #00003D ! important;
text-decoration:none;
}
.side-block a:focus {
color: #e7d30e;
text-decoration:none;
}
#side-bar .heading1 {
    color: #e7d30e;
    border-bottom: solid 1px #080808;
    padding-left: 24px;
    margin-top: 10px;
    margin-bottom: 0px;
    font-size: 15pt;
    font-weight: bold;
}
#side-bar .heading {
    color: #e7d30e;
    border-bottom: solid 1px #fafafa;
    padding-left: 15px;
    margin-top: 10px;
    margin-bottom: 5px;
    font-size: 8pt;
    font-weight: bold;
}
 #side-bar div.menu-item {
    margin: 2px 0;
}
#side-bar div.menu-item img {
    width: 13px;
    height: 13px;
    border: 0;
    margin-right: 2px;
    position: relative;
    bottom: -2px;
}
#side-bar div.menu-item a {
    font-weight: bold;
}
#side-bar div.menu-item.inactive img {
    opacity: 0.25;
}
#side-bar div.menu-item.inactive a {
    color: #fddd21;
}
#side-bar div.menu-item .sub-text {
    font-size: 80%;
    color: #fddd21;
}
#side-bar div.menu-item.sub-item {
}

#side-bar .collapsible-block-folded {
    background: url(http://www.scp-wiki.net/local--files/nav:side/expand.png) 0 2px no-repeat;
}
#side-bar .collapsible-block-link {
    margin-left: 15px;
    font-weight: bold;
}
#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px #fafafa;
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    margin-top: 10px;
    margin-bottom: 5px;
    font-size: 8pt;
    color: #fafafa;
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    color: #a8a8a8;
    text-decoration: none;
}

#side-bar ul{
    list-style-type: none;
    padding: 0 5px 0;
/* COULEUR DES LIENS UTILISES */
#side-bar a:visited{ color: #EDB44F; }

 a:visited { color: #EDB44F; }

/* MOBILE SIDEBAR */
@media (max-width: 767px) {
#side-bar{background-color: transparent}

.open-menu a {
    border: 0.2em solid transparent !important;
    background-color: transparent !important;
    color: transparent !important;
}
}

/* Custom page content classes */
#page-content {
    min-height: 720px;
}

.unmargined > p {
    margin: 0;
    line-height: 100%;
}
.content-panel {
    border: solid 1px #e7d30e;
    border-radius: 10px;
    background-color: #e7d30e;
    margin: 10px 0 15px;
    box-shadow: 3px 3px 6px #bbb;
    box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.1);
}
.content-panel.standalone {
    background: #fcfdfb;
}
.content-panel.series {
    padding: 0 20px;
    margin-bottom: 20px;
}
.content-panel.centered {
    text-align: center;
}
.content-panel.left-column {
    float: left;
    width: 48%;
}
.content-panel.right-column {
    float: right;
    width: 48%;
}

.content-panel .panel-heading {
    padding: 2px 10px;
    color: #00003D;
    font-size: 90%;
    font-weight: bold;
    text-shadow: 1px 1px 2px rgba(0,0,0,.35);
}
.content-panel .panel-heading > p,
.content-panel .panel-footer > p {
    margin: 0;
}
.content-panel .panel-body {
    padding: 5px 10px;
    background: #fff9f0 url(/local--files/component:theme/panel-bg-gradient-reverse.png) bottom repeat-x;
    /* box-shadow: 1px 2px 3px rgba(0,0,0,.2) inset */
}
.content-panel .panel-footer {
    padding: 1px 10px;
    color: #e7d30e;
    font-size: 80%;
    font-weight: bold;
    text-align: right;
    text-shadow: 1px 1px 2px rgba(0,0,0,.5);
}
.content-panel .panel-footer a {
    color: #e7d30e;
}
.content-panel .content-toc {
    float: right;
    padding: 0 20px;
    background-color: #fff;
    border: solid 1px #fff;
    border-radius: 10px;
    margin: 20px 0 5px 5px;
    white-space: nowrap;
    box-shadow: inset 1px 2px 6px rgba(0,0,0,.15)
}
.alternate:nth-child(even) {
    background-color: rgba(255,255,255,.9);
}

div.prompt {
border-radius: 5px;
border: 1px solid #F22424;
background: linear-gradient(#171717, #242424);
color:transparent;
padding:10px;
margin:5px;
}

div.prompt pre, div.prompt blockquote {
color:e7d30e;
}

div.side-block pre, div.side-block blockquote {
color:e7d30e;
text-shadow: none;
}

div.box-brown {
border-radius: 5px;
border: 1px solid #5c1515;
background: linear-gradient(#5c1515,#F0F0F0);
color:e7d30e;
padding:10px;
margin:5px;
}

/* SEXY BOX */
div.sexy-box {
    background: #f0f8ff;
    border: 1px solid #903C67;
}
div.sexy-box div.image-container img {
    border: 1px solid #9f8170;
}

/* TRANSLATION BLOCK */
iframe.scpnet-interwiki-frame {
    filter: hue-rotate(275deg);
    -webkit-filter: hue-rotate(275deg);
}

[[/code]]
[[/iftags]]
