content:
[[module CSS]]
@import url(https://scp-wiki-cn.wikidot.com/theme:sai-napoleon-iii/code/1);
[[/module]]

[[iftags +版式]]
[[code type="css"]]
@charset "utf-8";

/*
    Allègrement repompé sur : SCP Sigma 9
    [2014 Wikidot Theme]
    Created for the SCP Foundation by Aelanna (bidouillé par DrGemini)
*/

/* COMMON */
#content-wrap {
    position: relative;
    margin: 2em auto 0;
    width: 920px;
}

h1, #page-title {
    color: #fff; /* COULEUR GROS TEXTE BLANC */
    padding: 0 0 0.25em;
    margin: 0 0 0.6em;
    font-weight: normal;
z-index: -10;
}
h1 {
    margin-top: 0.7em;
    padding: 0;
    font-weight: bold;
}
h2, h3, h4, h5, h6, h7 {
    margin: 0.5em 0 0.4em;
    padding: 0;
    letter-spacing: 1px;
}
#page-title {
    border-color: #f6d058;
}
ul {
    list-style: square;
}
#top-bar ul {
    /* list-style-image: none; /* IE ONLY! IF list-style-image IS SET */
}

li, p {
    line-height: 141%;
}
a {
    color: #960000;
    text-decoration: none;
    background: transparent;
}
a.newpage {
    color: #f6d058;
    text-decoration: none;
    background: transparent;
}
a:hover {
    color: #e7d30e
    text-decoration: underline;
    background-color: transparent;
}

.form-control {
width: 95%;
}


/* GLOBAL WIDTH */
#header, #top-bar {
    width: 90%;
    max-width: 980px;
    margin: 0 auto;
}

#top-bar {
    width: 100%;
    margin: 0 auto;
}

.mobile-top-bar {
    display: none;
    position: absolute;
    left: 1em;
    bottom: 0px;
    z-index: 0;
}

body {
    background-color: #000000;
}

div#container-wrap {
    background: url(http://fondationscp.wdfiles.com/local--files/theme%3Asai-napoleon-iii/Th%C3%A8meSAINapoIII.jpg) top left repeat-x;
}

/* HEADER */
#header {
    height: 200px;
    position: relative;
    z-index: 10;
    padding-bottom: 0px; /* FOR MENU */
    background: url(http://fondationscp.wdfiles.com/local--files/theme%3Asai-napoleon-i/1506450654-saii.png) 20px 0px no-repeat;
}

#search-top-box {
    position: absolute;
    top: 79px;
    right: 9px;
    width: 250px;
    text-align: right;
}
#search-top-box-input {
    border: solid 1px #999;
    border-radius: 5px;
    color: #d28f11;
background-color: #fff;
    box-shadow: inset 1px 1px 3px rgba(0,0,0,.5);
}
#search-top-box-input:hover,
#search-top-box-input:focus {
    border: solid 1px #fff;
    color: #fff;
    background-color: #380d0d;
    box-shadow: inset 1px 1px 3px rgba(0,0,0,.8);
}
#search-top-box-form input[type=submit] {
    border: solid 1px #999;
    border-radius: 5px;
    padding: 2px 5px;
    font-size: 90%;
    font-weight: bold;
    color: #fff;
    background-color: #380d0d;
    box-shadow: 0 1px 3px rgba(0,0,0,.5);
    cursor: pointer;
}
#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    border: solid 1px #fff;
    color: #fff;
    text-shadow: 0 0 1px rgba(255,255,255,.25)
    background-color: #999;
    background: linear-gradient(to bottom, #fff,#999,#666);
    box-shadow: 0 1px 3px rgba(0,0,0,.8);
}

#login-status {
    color: #1f1f1f;
    font-size: 90%;
    z-index: 30;
}
#login-status a {
    background: transparent;
    color: #1f1f1f;
}
#login-status ul a {
    color: #1f1f1f;
    background: transparent;
}

#account-topbutton {
    background: #ddd;
    color: #1f1f1f;
    border: none;
}

.printuser img.small {
    margin-right: 1px;
}

#header h1 {
    margin-left: 120px;
    padding: 0;
    float: left;
    max-height: 95px;
}
#header h2 {
    margin-left: 120px;
    padding: 0;
    clear: left;
    float: left;
    font-size: 105%;
    max-height: 38px;
}

#header h1 a {
    display: block;
    margin: 0;
    padding: 80px 0 25px;
    line-height: 0px;
    max-height: 0px;
    color: #00003D;
    background: transparent;
    font-family: Akashi, Arial, sans-serif;
    font-size:0%;
    text-decoration: none;
    text-shadow: 3px 3px 5px #000;
    letter-spacing: 0.9px;
}

#header h2 span {
    display: block;
    margin: 0;
    padding: 19px 0;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: #f0f0c0;
    font-family: Helvetica, sans-serif;
    font-size: 0%;
    text-shadow: 1px 1px 1px #000;
    text-shadow: 1px 1px 1px rgba(0,0,0,.8);
}

/* TOP MENU */
#top-bar {
    position: absolute;
    z-index: 50;
    top: 140px;
    height: 21px;
    line-height: 18px;
    padding: 0;
    z-index: 20;
    font-size: 90%;
}
#top-bar ul {
    float: right;
}
#top-bar a {
    color: #fff;
    background: transparent;
}
#top-bar ul li {
    border: 0;
}
#top-bar ul li ul {
    border: solid 1px #666;
    box-shadow: 0 2px 6px rgba(0,0,0,.5);
    border-top: 0;
}
#top-bar ul li a {
    border-left: solid 1px rgba(64,64,64,.1);
    border-right: solid 1px rgba(64,64,64,.1);
    text-decoration: none;
    padding-top: 10px;
    padding-bottom: 10px;
    line-height: 1px;
    max-height: 1px;
    overflow: hidden;
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: #00003D;
    color: #e7d30e;
    border-left: solid 1px rgba(64,64,64,1);
    border-right: solid 1px rgba(64,64,64,1);
}

#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
    border-width: 0;
    width: 150px;
    border-top: 1px solid #ddd;
    line-height: 160%;
    height: auto;
    max-height: none;
    padding-top: 0;
    padding-bottom: 0;
}
#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background: #fff;
    text-decoration: none;
}
#top-bar ul li ul {
    border-width: 0 1px 1px 1px;
    width: auto;
}
#top-bar ul li ul li, #top-bar ul li ul li.sfhover,
#top-bar ul li ul li, #top-bar ul li ul li:hover {
    border-width: 0;
}
#top-bar ul li ul li a {
    border-width: 0;
}
#top-bar ul li ul a, #top-bar a:hover {
    color: #a01;
}

/* IE7 HACK */
#top-bar ul > li > ul {
    *margin-top: -4px;
}

/* SIDE MENU */
#side-bar {
    clear: none;
    float: none;
    position: absolute;
    top: 0.5em;
    left: 0em;
    width: 17em;
    padding: 0;
    border: none;
    display: block;
}
#side-bar .side-block {
    padding: 10px;
    border: 1px solid #f2e281;
    border-radius: 10px;
    box-shadow: 0 2px 6px rgba(0,0,0,.5);
    background: #dbdaad;
    text-shadow: 1px 1px 1px solid #B9B9B9;
    background: linear-gradient(#380d0d, #380d0d);
    margin-bottom: 15px;
    color: #ddac13;
    text-shadow: 1px 1px 1px #B9B9B9;
    text-shadow: 1px 1px 1px rgba(0,0,0,.8);
}
.side-block a {
color: #f6d058;
text-decoration:none;
}
.side-block a:hover {
color: #e7d30e;
text-decoration:none;
}
.side-block a.newpage {
color: #00003D ! important;
text-decoration:none;
}
.side-block a:focus {
color: #e7d30e;
text-decoration:none;
}

#side-bar .heading1 {
    color: #e7d30e;
    border-bottom: solid 1px #080808;
    padding-left: 24px;
    margin-top: 10px;
    margin-bottom: 0px;
    font-size: 15pt;
    font-weight: bold;
}
#side-bar .heading {
    color: #e7d30e;
    border-bottom: solid 1px #fafafa;
    padding-left: 15px;
    margin-top: 10px;
    margin-bottom: 5px;
    font-size: 8pt;
    font-weight: bold;
}
 #side-bar div.menu-item {
    margin: 2px 0;
}
#side-bar div.menu-item img {
    width: 13px;
    height: 13px;
    border: 0;
    margin-right: 2px;
    position: relative;
    bottom: -2px;
}
#side-bar div.menu-item a {
    font-weight: bold;
}
#side-bar div.menu-item.inactive img {
    opacity: 0.25;
}
#side-bar div.menu-item.inactive a {
    color: #fddd21;
}
#side-bar div.menu-item .sub-text {
    font-size: 80%;
    color: #fddd21;
}
#side-bar div.menu-item.sub-item {
}

#side-bar .collapsible-block-folded {
    background: url(http://www.scp-wiki.net/local--files/nav:side/expand.png) 0 2px no-repeat;
}
#side-bar .collapsible-block-link {
    margin-left: 15px;
    font-weight: bold;
}
#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px #fafafa;
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    margin-top: 10px;
    margin-bottom: 5px;
    font-size: 8pt;
    color: #fafafa;
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    color: #a8a8a8;
    text-decoration: none;
}

#side-bar ul{
    list-style-type: none;
    padding: 0 5px 0;
}

/* CONTENT */
#main-content {
    margin: 0 0 0 19em;
    padding: 0em;
    position: relative;
}

#breadcrumbs {
    margin: -1em 0 1em;
    font-weight: 85%;
}

/* TABVIEW LOOKS
===========================*/

.yui-navset .yui-content {
    background-color: #f6f2ef;
}

.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
    background-color:#fbc951;
    background-image: url(http://fondationscpsandbox.wdfiles.com/local--files/scp-2212-valide-par-nerem/ARCA.png);
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:hover { /* no hover effect for selected */
    background: #784c34; /* selected tab background */
    color:white;
}

.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus {
    background: #d8cac1;
    text-decoration: none;
}
.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    border-color: #6b513b;
}
.yui-navset li {
    line-height: normal;
}


/* FOOTER */
#footer {
    clear: both;
    font-size: 77%;
    background: #540400;
    color: #d89900;
    margin-top: 15px;
    padding: 3px 10px;
}
#footer .options {
    visibility: visible;
    display: block;
    float: right;
    width: 50%;
    font-size: 100%;
    text-align: right;
}
#footer a {
    color: #fff;
    background: transparent;
}

/* SOME NICE BOXES */
div.sexy-box {
    background: #e7d30e;
    border: 1px solid #e7d30e;
    padding: 0 10px 12px;
    margin: 7px 4px 12px;
    overflow: hidden;
}
div.sexy-box div.image-container img {
    margin: 5px;
    padding: 2px;
    border: 1px solid #999;
}

/* Custom page content classes */
#page-content {
    min-height: 720px;
}

.unmargined > p {
    margin: 0;
    line-height: 100%;
}
.content-panel {
    border: solid 1px #e7d30e;
    border-radius: 10px;
    background-color: #e7d30e;
    margin: 10px 0 15px;
    box-shadow: 3px 3px 6px #bbb;
    box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.1);
}
.content-panel.standalone {
    background: #fcfdfb;
}
.content-panel.series {
    padding: 0 20px;
    margin-bottom: 20px;
}
.content-panel.centered {
    text-align: center;
}
.content-panel.left-column {
    float: left;
    width: 48%;
}
.content-panel.right-column {
    float: right;
    width: 48%;
}

.content-panel .panel-heading {
    padding: 2px 10px;
    color: #00003D;
    font-size: 90%;
    font-weight: bold;
    text-shadow: 1px 1px 2px rgba(0,0,0,.35);
}
.content-panel .panel-heading > p,
.content-panel .panel-footer > p {
    margin: 0;
}
.content-panel .panel-body {
    padding: 5px 10px;
    background: #fff9f0 url(/local--files/component:theme/panel-bg-gradient-reverse.png) bottom repeat-x;
    /* box-shadow: 1px 2px 3px rgba(0,0,0,.2) inset */
}
.content-panel .panel-footer {
    padding: 1px 10px;
    color: #e7d30e;
    font-size: 80%;
    font-weight: bold;
    text-align: right;
    text-shadow: 1px 1px 2px rgba(0,0,0,.5);
}
.content-panel .panel-footer a {
    color: #e7d30e;
}
.content-panel .content-toc {
    float: right;
    padding: 0 20px;
    background-color: #fff;
    border: solid 1px #fff;
    border-radius: 10px;
    margin: 20px 0 5px 5px;
    white-space: nowrap;
    box-shadow: inset 1px 2px 6px rgba(0,0,0,.15)
}
.alternate:nth-child(even) {
    background-color: rgba(255,255,255,.9);
}

div.prompt {
border-radius: 5px;
border: 1px solid #F22424;
background: linear-gradient(#171717, #242424);
color:white;
padding:10px;
margin:5px;
}

div.prompt pre, div.prompt blockquote {
color:e7d30e;
}

div.side-block pre, div.side-block blockquote {
color:e7d30e;
text-shadow: none;
}

div.box-brown {
border-radius: 5px;
border: 1px solid #5c1515;
background: linear-gradient(#5c1515,#F0F0F0);
color:e7d30e;
padding:10px;
margin:5px;
}
/* Page Rating Module Customizations */
.page-rate-widget-box {
    display: inline-block;
    border-radius: 5px;
    box-shadow: 1px 1px 3px rgba(0,0,0,.5);
    margin-bottom: 10px;
    margin-right: 2em;
}
.page-rate-widget-box .rate-points {
  background-color: #380d0d !important;
  border: solid 1px #f6d058;
  border-right: 0;
  border-radius: 5px 0 0 5px;
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
  background-color: #380d0d;
  border-top: solid 1px #f6d058;
  border-bottom: solid 1px #f6d058;
  font-weight: bold;
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
  background: transparent;
  color: #d28f11 ;
  padding: 0 4px;
  margin: 0 1px;
}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
  background: #c70000;
  color: #d28f11;
  text-decoration: none;
}
.page-rate-widget-box .cancel {
  background-color: #380d0d;
  border: solid 1px #f6d058;
  border-left: 0;
  border-radius: 0 5px 5px 0;
}
.page-rate-widget-box .cancel a {
  background: transparent;
  text-transform: uppercase;
  color: #db411a;
}
.page-rate-widget-box .cancel a:hover {
  border-radius: 0 3px 3px 0;
  background: #d28f11;
  color: #d28f11;
  text-decoration: none;
}

/* Info Module */

.rate-box-with-credit-button .creditButton {

    background-color: #380d0d !important;

    border-color: #f6d058 !important;


}

.creditButton a{

    color: #d28f11;

}

.creditButton a:hover {

    color: #d28f11;

}

.creditButton {

    margin-bottom: 10px;
    margin-right: 2em;
}
[[/code]]
[[/iftags]]
