#header #header-extra-div-2::before {
    z-index: -1;
    background-image: url('https://scp-wiki.wdfiles.com/local--files/theme%3Ascheme/stick.png');
    -webkit-animation-name: dir;
    animation-name: dir;
    animation-direction: reverse
