/* STICK-2 */
#header #header-extra-div-3 {
    z-index: -2;
    width: 13px;
    height: 13px;
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    position: absolute;
    left: 105px;
    top: 105px;
    -webkit-transform: translateX(-50%);
    -ms-transform: translateX(-50%);
    transform: translateX(-50%);
    margin-top: 0
}

#header #header-extra-div-3::before {
    z-index: -1;
    content: "";
