@import url('https://fonts.googleapis.com/css2?family=Outfit:wght@500&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+JP:wght@500&display=swap');
/*
  [EN]
  SCP-JP 10th ANNIVERSARY EVENTS Theme
  Creative Commons 3.0 Attribution Succession
  Managed by SCP-JP Event Committee
  Created at 2023 by Mishary

  [JA]
  SCP-JP10周年イベントテーマ
  クリエイティブコモンズ3.0 表示 継承
  テーマ管理者: SCP-JPイベント委員会
  作成年: 2023
  テーマ作成者: Mishary
*/

/* COMMON */

:root {
    --black: 0, 0, 0;
    --gray: 128, 128, 128;
    --white: 255, 255, 255;
    --bright-jp-red: 239, 51, 64;
    --dark-jp-red: 210, 22, 63;
}

body {
    font-family: 'Outfit', 'Noto Sans JP', sans-serif;
    background-color: rgb(var(--white));
}

#content-wrap {
    max-width: 1200px;
}

#page-title, h1, h2, h3, h4, h5, h6 {
    color: rgb(var(--black));
    font-family: 'Outfit', 'Noto Sans JP', sans-serif;
    font-weight: bold;
}

#page-title {
    font-weight: bold;
}

.footnote-footer {
     overflow: auto;
}

a, a:visited {
    color: rgb(var(--bright-jp-red));
    color: rgb(var(--bright-jp-red));
}

#toc, blockquote, div.blockquote{
    border-color: #9fa0a0;
    border-style: solid;
    border-width: 1px;
    margin: 1em 0;
    background-color: rgba(223, 224, 211,.5);
}

.scp-image-block .scp-image-caption{
    background-color: rgba(223, 224, 211,.5);
}

#page-content hr {
    background: rgb(var(--black));
    margin: 1em 0;
    width: 100%;
}

.contest{
    color: #d11140;
    font-weight: bold;
    font-size: 180%;
    padding-top: 40px;
    padding-bottom: 40px;
}

.presenter{
    font-size: 120%;
}

#page-content .blockdivide hr {
    border: 0;
    height: 3px;
    background-image: linear-gradient(to right, #000 90%, #d11140 90%, #d11140 96.7%,  #ef3340 96.7%,  #ef3340 100%);
}

.hide_col .collapsible-block-folded a{
    color: #eee;
}

/* GLOBAL WIDTH */
div#container-wrap {
    background: none;
}

/* HEADER */
#header {
    background: url('http://scp-jp-storage.wdfiles.com/local--files/file:3438721-249-6152/10th_logotype.svg') 0px 20px no-repeat;
    background-size: 400px;
    height: 120px;
    max-width: 1140px;
}
#header h1 {
    margin-left: unset;
    margin-top: 15px;
}
#header h1 a {
    padding: 80px 200px 25px;
}
#header h1 a span {
    display: none;
}
#header h2 span {
    font-size: 0px;
}

#search-top-box {
    top: 60px;
    width: unset;
}
#search-top-box-input {
    background-color: transparent;
    color: rgb(var(--black));
}
#search-top-box input.empty{
    color: rgb(var(--black));
    border-radius: unset;
}
#search-top-box-input:hover, #search-top-box-input:focus {
    background-color: rgba(102,51,51,.2);
    color: rgb(var(--black));
}
#search-top-box-form input[type=submit] {
    border: none;
    color: rgb(var(--white));
    background: rgb(var(--black));
    border-radius: unset;
}
#search-top-box-form input[type=submit]:hover, #search-top-box-form input[type=submit]:focus {
    border: none;
    color: rgb(var(--black));
    text-shadow: 0 0 1px rgba(var(--white),.25);
    background: rgba(223, 224, 211,.5);
    box-shadow: 0 1px 3px rgba(var(--black),.8);
}

.printuser{
    color: rgb(var(--black));
}

#login-status{
    color: rgb(var(--black));
}

#login-status a{
    color: rgb(var(--black));
}

/* TOP MENU */
#top-bar {
    top: 120px;
    max-width: 1140px;
}

#top-bar a {
    color: rgb(var(--black));
}

#top-bar li:hover ul, #top-bar li.sfhover ul {
    box-shadow: unset;
}

#top-bar ul li.sfhover a, #top-bar ul li:hover a {
    color: rgb(var(--black));
    background: rgb(var(--white));
}

#top-bar ul li.sfhover a:hover, #top-bar ul li:hover a:hover{
    color: rgb(var(--white));
    background: rgb(var(--bright-jp-red));
}
/* SIDE MENU */
#side-bar {
    background-color: transparent;
}
#side-bar .side-block {
    border-bottom-color: rgb(var(--bright-jp-red));
    border: none;
    margin-bottom: 0;
    padding: 12px 0px;
    background: none;
    background-color: transparent !important;
    box-shadow: none;
}
#side-bar .side-block a, #side-bar .collapsible-block-unfolded-link .collapsible-block-link{
    color: rgb(var(--black));
    font-size: 9pt;
}

#side-bar .collapsible-block-link {
    margin-left: 17px;
}
#side-bar .side-block a:hover, #side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover{
    color: rgb(var(--bright-jp-red));
    text-decoration: none;
}
#side-bar .side-block+ .side-block {
    border-top: 1px solid #9fa0a0;
    border-radius: 0;
}
#side-bar .side-block hr {
    display: none;
}
#side-bar hr {
    margin: 0;
    background-color: rgb(var(--bright-jp-red));
}
#side-bar .heading {
    border-top: solid 1px rgb(var(--bright-jp-red));
    border-bottom: none;
    margin-top: 12px;
    padding-top: 12px;
    padding-left: 16px;
    color: rgb(var(--black));
    font-size: 9pt;
    -webkit-text-stroke-width: 0.8px;
}
#side-bar div.menu-item {
    margin: 2px 0 2px 12px;
    padding-left: 4px;
}
#side-bar div.menu-item a {
    font-size: 9pt;
    font-weight: bold;
    line-height: 23px;
}
#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px rgb(var(--bright-jp-red));
    padding-bottom: 12px;
}
.side-block .menu-item > .image {
    filter: hue-rotate(12deg) saturate(21%) brightness(96%);
}

#interwiki body {
    background: unset;
}

#side-bar .side-block {
    background: transparent;
    border: unset;
    box-shadow: unset;
}

/* Rate Module */
div.page-rate-widget-box .rate-points, .page-rate-widget-box .cancel, div.rate-box-with-credit-button .creditButton {
    background: rgb(var(--black))!important;
    border: none;
    border-radius: unset;
    color: rgb(var(--white));
}
.page-rate-widget-box .cancel a{
    color: rgb(var(--white));
}
.rate-box-with-credit-button .creditButton {
    padding: 1px 0;
}
.creditButton a, .creditButton a:visited{
    color: rgb(var(--white))
}
div.page-rate-widget-box .cancel a:hover{
    background: rgb(var(--white));
    color: rgb(var(--bright-jp-red));
    border-radius: unset;
}
.page-rate-widget-box .rateup, .page-rate-widget-box .ratedown{
    background: rgb(var(--white));
    border: none;
    color: rgb(var(--black));
}
.page-rate-widget-box .rateup a, .page-rate-widget-box .ratedown a{
    color: rgb(var(--black));
}
.page-rate-widget-box .rateup a:hover, .page-rate-widget-box .ratedown a:hover{
    background: rgb(var(--bright-jp-red));
    color: rgb(var(--white));
}
div.creditButton a:hover {
    color: rgb(var(--gray));
}

.modalbox {
    border-radius: unset;
}
.modalbox .modalbox-title {
    border-radius: unset;
    background: rgb(var(--black));
}
.modalbox .modalbox-title h2{
    color: rgb(var(--white));
}

/* Media */
@media (max-width: 767px) {
  #header {
    background: url(http://scp-jp-storage.wikidot.com/local--files/file:3438721-223-plep/10th_logo.svg) 0px 15px no-repeat;
    background-size: 150px;
  }
  #header h1 {
    margin-top: unset;
  }
  #header h1 a {
    padding: 65px 80px;
  }
  #top-bar {
    top: 90px;
  }
  div.scpnet-interwiki-wrapper {
    width: auto;
  }
  #side-bar {
    background-color: rgb(var(--white));
  }
  #side-bar:target {
    border: 1px solid #9fa0a0;
  }
  #side-bar:target .close-menu {
    background: rgba(var(--black), 0.1) 1px 1px repeat;
  }
  .open-menu a {
    color: transparent !important;
    border: 0.1em solid black !important;
  }
  .open-menu a::before {
    content: "";
    background: url(http://scp-jp-storage.wikidot.com/local--files/file:3438721-266-50z4/scp-jp-sun.svg) 3px no-repeat;
    background-size: 24px;
    width: 30px;
    display: block;
    height: 30px;
    border-radius: 3em;
  }
}

@media (min-width: 451px) and (max-width: 767px) {
  .mobile-top-bar {
    left: unset;
    right: 0;
  }
}

@media (max-width: 450px) {
  #header {
    background-size: 80px;
    height: 80px;
  }
  #header h1 a {
    padding: 45px 40px;
  }
  #search-top-box {
    top: 40px;
  }
  #top-bar {
    top: 80px;
  }
}
[[/code]]
[[code type="CSS"]]
#interwiki .side-block {
    background: transparent !important;
    border: unset;
    border-radius: unset;
    box-shadow: unset;
}

#interwiki .heading {
    border-top: solid 1px #ed3442;
    border-bottom: none;
    margin-top: 12px;
    padding-top: 12px;
    padding-left: 16px;
    color: #000;
    font-size: 9pt;
    -webkit-text-stroke-width: 0.5px;
}

#interwiki .menu-item a {
    color: #000;
    font-size: 9pt;
}

#interwiki .menu-item {
    margin: 2px 0 2px;
    padding: 0 2px 4px;
}

#interwiki .menu-item img {
    filter: hue-rotate(12deg) saturate(21%) brightness(96%);
}

#interwiki .menu-item a:hover {
    color: #b01;
}
[[/code]]
