[[module css]]
@import url("https://nu-scptheme.github.io/Black-Highlighter/css/min/normalize.min.css");
@import url("https://nu-scptheme.github.io/Black-Highlighter/css/min/black-highlighter.min.css");

:is(div.rate-box-with-credit-button div.page-rate-widget-box,div.page-rate-widget-box) .rate-points {
    color: rgb(255 255 255);
    font-family: var(--UI-font);
    font-size: .85em;
    line-height: calc(var(--rating-module-height)* 1.25);
    text-transform: capitalize;
    background-color: #4D4637 !important;
    border: none;
    padding: 0 .5em;
    display: -webkit-box;
    display: -webkit-flex;
    display: flex
;
}

:root:lang(cn) {

  --header-font: 'Courier New';
}


:root {


    --body-font: Courier New, bold;
    --header-font: Courier New, bold;
    --title-font: Courier New, bold;
    --UI-font: Courier New, bold;
    --mono-font: Courier New, bold;

    --gradient-header: url("http://scpsandbox-ua.wikidot.com/local--files/theme:scp-military/%D1%88%D0%B0%D0%BF%D0%BA%D0%B0_%D0%BF%D1%80%D0%BE%D0%B12.png");


    --white-monochrome: 252, 252, 252;
    --pale-gray-monochrome: 184, 167, 124;
    --light-pale-gray-monochrome: 77, 70, 52;

    --light-gray-monochrome: 77, 70, 52;
    --gray-monochrome: 184, 167, 124;
    --dark-gray-monochrome: 77, 70, 52;
    --black-monochrome: 41, 37, 28;
    --bright-accent: 77, 70, 52;
   /* Заголовки сайдбару і ко */
    --medium-accent: 77, 70, 52;
    --alt-accent: 77, 70, 52;
    --dark-accent:  77, 70, 52;

   --swatch-primary: var(--bright-accent);
   --swatch-primary-darker: var(--medium-accent);
   --swatch-primary-darkest: var(--dark-accent);
   --swatch-border-color: var(--medium-accent);

   --swatch-text-dark: var(--black-monochrome);
   --swatch-text-light: var(--white-monochrome);
   --swatch-important-text: var(--bright-accent);

   --link-color: var(--dark-gray-monochrome);
   --visited-link-color: var(--gray-monochrome);
   --hover-link-color: var(--dark-gray-monochrome);


}



[[/module]]
