[[collapsible show="＋테마 코드 열기" hide="－테마 코드 닫기"]]
[[module CSS show="true"]]
@import url('https://fonts.googleapis.com/css?family=Basic|IBM+Plex+Mono:400,700');
@font-face {
    font-family: 'MonoplexKR_Medium';
    src: url('https://fastly.jsdelivr.net/gh/projectnoonnu/noonfonts_Monoplex-kr@1.0/MonoplexKR-Medium.woff2') format('woff2');
    font-weight: 500;
    font-style: normal;
}
@font-face {
    font-family: 'MonoplexKR_Regular';
    src: url('https://fastly.jsdelivr.net/gh/projectnoonnu/noonfonts_Monoplex-kr@1.0/MonoplexKR-Regular.woff2') format('woff2');
    font-weight: 400;
    font-style: normal;
}

@supports(--css: variables) {
:root:lang(ko) {
    /* Header */
    --header-title: "SCP 재단";
    --header-subtitle: "확보, 격리, 보호";

    /* Typefaces */
    --body-font: 'IBM Plex Mono', 'MonoplexKR_Regular', monospace;
    --header-font: 'IBM Plex Mono', 'MonoplexKR_Medium', monospace;
    --title-font: 'IBM Plex Mono', 'MonoplexKR_Medium', monospace;
    --mono-font: 'IBM Plex Mono', 'MonoplexKR_Regular', monospace;
    }

}

/* Info module fix */
#page-content .rate-box-with-credit-button {
    background: linear-gradient(to bottom, rgb(var(--bright-accent)) 0%, rgb(var(--dark-accent)) 30%, rgb(var(--dark-accent)) 100%) !important;
}

#page-content .rate-box-with-credit-button>.creditButton:hover {
    background: none;
}

#page-content .rate-box-with-credit-button>.creditButton>p>a:before {
    -webkit-transition: color 0.2s ease-in-out, opacity 0.2s ease-in-out, background-color 0.2s ease-in-out;
    -o-transition: color 0.2s ease-in-out, opacity 0.2s ease-in-out, background-color 0.2s ease-in-out;
    -moz-transition: color 0.2s ease-in-out, opacity 0.2s ease-in-out, background-color 0.2s ease-in-out;
    transition: color 0.2s ease-in-out, opacity 0.2s ease-in-out, background-color 0.2s ease-in-out;
}

#page-content .rate-box-with-credit-button>.creditButton>p>a:hover:before {
    background: rgb(var(--white-monochrome)) !important;
}

textarea {
   font-family: var(--mono-font);
}
input#edit-page-title.text {
   font-family: var(--title-font);
}
[[/module]]
