[[code type="css"]]

/* Modified from Just Girly Things Theme
(https://scp-wiki.wikidot.com/theme:just-girly-things)
by stormbreath
and Letters Theme
(http://scp-wiki-cn.wikidot.com/component:letters-theme)
by breaddd */

@charset "utf-8";

#content-wrap {
    min-height: 800px;
}

body {
    background-attachment: fixed;
    color: #000000;
    font-family: "等线", nonserif;
    --muse-color: #E40080;
    --aqours-color: #19b1f6;
    --nijigasaki-color: #f8b656;
    --liella-color: #da57d8;
    --hasunosora-color: #FB8A9B;
    width: 100%;
    background-color: rgba(228,0,128,.05);
}


h1,
#page-title {
    color: var(--muse-color);
}

#page-title {
    border-color: var(--muse-color);
}

.enlarge {
    transition: .25s;
}

.enlarge:hover {
    transform: scale(1.3);
    transition: .25s;
    transition-delay: .4s;
    z-index: 1000;
}

.scplive {
    font-style:italic;
    font-family: "Times New Roman", serif;
    color: var(--muse-color);
}

.scplive::before {
    content: "SCPLive! "
}

/* links */

a {
    color: var(--muse-color);
}

a:visited {
    color: var(--aqours-color);
}

a.newpage {
    color: var(--muse-color);
}

/* SIDE BAR */
#side-bar .side-block {
    background: var(--muse-color);
    color: #FFF !important;
    border: none;
    box-shadow: none;
}

#side-bar .heading,
#side-bar a,
#side-bar a:visited {
    color: #FFF;
}

.side-block .menu-item > .image, 
#interwiki .menu-item img {
    filter: grayscale(100%) brightness(50)
}

#side-bar .side-block.newcomer,
#side-bar .side-block.resources {
    background-color: var(--aqours-color) !important;
}
#side-bar .side-block.admin {
    background-color: var(--nijigasaki-color) !important;
}
#side-bar .side-block.media {
    background-color: var(--liella-color) !important;
}
#side-bar .side-block.license {
    background-color: var(--hasunosora-color) !important;
}

div#container-wrap {
    background: none;
}

/* HEADER */

#header {
    background-color: var(--muse-color);
    background-image: url(http://1145141919810.wdfiles.com/local--files/blog%3A_start/%E7%94%BB%E6%9D%BF%201.png) !important;
    border-radius: 0px 0px 10px 10px;
    background-size: 75px;
    background-position: 1.5em 4.5em;
}


#header h1 a {
    display: block;
    margin: 0;
    padding: 80px 0 25px;
    font-size: 0px;
    line-height: 0px;
    max-height: 0px;
    color: transparent;
    background: transparent;
    font-family: "Times New Roman", serif;
    text-shadow: none;
    text-decoration: none;
    letter-spacing: 0.9px;
}

#header h1 a::before {
    content: "SCPLive!";
    font-style: italic;
    font-size: 40px !important;
    color: #FFF;
}

h1,
#page-title {
    color: var(--muse-color);
}

#header h2 span {
    display: block;
    margin: 0;
    padding: 19px 0;
    font-size: 0px;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    font-family: "Consolas", monospace;
    color: transparent;
    text-shadow: none;
}

#header h2 span::before {
    content: 'Foundation Idol Project';
    font-size: 10px;
    color: #FFF;
}

/* SEARCH BAR */
#search-top-box-input {
    border: solid 1px #AAA;
    color: #000;
    background-color: #FFF;
    box-shadow: none;
}

#search-top-box-input:hover,
#search-top-box-input:focus {
    border: solid 1px #AAA;
    color: #000;
    background-color: #EEE;
    box-shadow: none;
}

#search-top-box-form input[type=submit] {
    border: transparent;
    color: var(--muse-color);
    background: #FFF;
    box-shadow: none;
}

#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    border: transparent;
    color: var(--muse-color);
    background: #FFF;
    box-shadow: none;
}

/* LOG-IN INFO */

#login-status {
    color: #000;
}

#login-status a {
    color: #000;
}

#login-status ul a {
    color: #000;
}

#account-topbutton {
    background: #CCCCCC;
    color: #000;
}

/* -------------- FOOTER -------------- */
#footer {
    background: var(--muse-color);
    color: white
}

/* -------------- RATING MODULE -------------- */
.page-rate-widget-box .rate-points {
    background-color: var(--muse-color) !important;
    color: hsl(335, 90%, 95%) !important;
    border: solid 1px var(--muse-color);
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background-color: hsl(335, 90%, 95%);
    border-top: solid 1px var(--muse-color);
    border-bottom: solid 1px var(--muse-color);
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    background: transparent;
    color: var(--muse-color);
}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background: var(--muse-color);
    color: hsl(335, 90%, 95%);
}
.page-rate-widget-box .cancel {
    background: transparent;
    background-color: var(--muse-color);
    border: solid 1px var(--muse-color);
}
.page-rate-widget-box .cancel a {
    color: hsl(335, 90%, 95%);
}
.page-rate-widget-box .cancel a:hover {
    background: hsl(335, 90%, 95%);
    color: var(--muse-color);
}

/* Info Module */
#page-content .rate-box-with-credit-button {
    background-color: var(--muse-color);
    border-color: var(--muse-color);
    border-radius: 5px;
    box-shadow: 1px 1px 3px rgba(0,0,0,.5);
}
#page-content .creditButton p a {
    border-left-color: hsl(335, 90%, 95%);
}

#page-content .creditButton p a:hover {
    color: hsl(305, 100%, 15%);
}

/* -------------- OTHER ELEMENTS -------------- */
blockquote,
div.blockquote,
.code {
    border-color: hsl(335, 90%, 85%);
    background-color: hsl(335, 90%, 95%);
}

#page-content .wiki-content-table tr th {
     background-color: var(--muse-color);
     color: hsl(335, 100%, 98%);
     border: 1px solid hsl(335, 100%, 14%);
}

.scp-image-block .scp-image-caption {
    background-color: hsl(335, 90%, 95%);
}

.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    border-color: hsl(335, 90%, 75%); /* color between tab list and content */
}

.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
    background-color: transparent !important; /* tab background */
    border-color: transparent;
    color: hsl(335, 100%, 14%);
}

.yui-navset .yui-nav a em {
     background-color: hsl(335, 90%, 95%);
     box-shadow: 0px 1px 2px rgba(0,0,0,0.7);
     border-color: hsl(335, 90%, 85%);
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus,
.yui-navset .yui-nav .selected a:hover {
    background-image: none;
    background-color: var(--muse-color); /* selected tab background */
    color: hsl(335, 100%, 98%);
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a em {
    border-color: var(--muse-color);
    box-shadow: 0px 1px 2px rgba(0,0,0,0.7);
    background-color: var(--muse-color);
}

.yui-navset .yui-content {
    background-color: hsl(335, 90%, 95%); /* content background color */
}

/* -------------- MOBILE VIEW -------------- */
@media (max-width: 767px) {
    #side-bar {
          background-color: hsl(335, 65%, 65%);
     }

    .open-menu a {
         border: 0.2em solid hsl(335, 100%, 85%) !important;
         background-color: hsl(335, 100%, 98%) !important;
         color: hsl(335, 100%, 85%) !important;
    }
}
[[/code]]
[[/iftags]]
