  grid-template-areas:'open-menu top-bar search user';

  justify-items: center;
  align-items: center;
  position: fixed;
  z-index: 10;
  max-width: 100%;
  width: 100%;
  margin: 0;
  padding: 0;
  background: transparent;
  backdrop-filter: blur(2px);
